import React, { useState } from 'react';
import { Box, Container, Paper, Typography, ThemeProvider, createTheme } from '@mui/material';
import FileUpload from './components/FileUpload';
import Reader from './components/Reader';
import './styles/Reader.css';
import ReadingControls from './components/ReadingControls';

const theme = createTheme({
  palette: {
    mode: 'light',
    primary: {
      main: '#1976d2',
    },
    secondary: {
      main: '#dc004e',
    },
  },
});

function App() {
  const [text, setText] = useState('');
  const [highlightColor, setHighlightColor] = useState('#ffeb3b');
  const [readingMode, setReadingMode] = useState('static'); // 'static' or 'dynamic'
  const [highlightedWords, setHighlightedWords] = useState([]);
  const [selectedPos, setSelectedPos] = useState(['noun']); // 默认高亮名词
  const [isDarkMode, setIsDarkMode] = useState(false);
  const [fileContent, setFileContent] = useState('');
  const [fileName, setFileName] = useState('');

  const handleFileUpload = (event) => {
    const file = event.target.files[0];
    if (!file) return;

    setFileName(file.name);
    
    const reader = new FileReader();
    
    reader.onload = function(event) {
      try {
        const text = event.target.result;
        setFileContent(text);
        console.log('文件内容已加载:', text.substring(0, 100) + '...'); // 调试用
      } catch (error) {
        console.error('读取文件出错:', error);
        alert('读取文件时出错，请重试');
      }
    };

    reader.onerror = function(error) {
      console.error('文件读取错误:', error);
      alert('读取文件时出错，请重试');
    };

    reader.readAsText(file);
  };

  return (
    <ThemeProvider theme={theme}>
      <Container maxWidth="lg">
        <Box sx={{ my: 4 }}>
          <Typography variant="h3" component="h1" gutterBottom align="center">
            ADHD阅读助手
          </Typography>
          
          <Paper elevation={3} sx={{ p: 3, mb: 3 }}>
            <FileUpload setText={setText} />
          </Paper>

          <Paper elevation={3} sx={{ p: 3, mb: 3 }}>
            <ReadingControls
              highlightColor={highlightColor}
              setHighlightColor={setHighlightColor}
              readingMode={readingMode}
              setReadingMode={setReadingMode}
              selectedPos={selectedPos}
              setSelectedPos={setSelectedPos}
            />
          </Paper>

          <Paper elevation={3} sx={{ p: 3 }}>
            {fileContent ? (
              <Reader content={fileContent} isDarkMode={isDarkMode} />
            ) : (
              <div className={`upload-prompt ${isDarkMode ? 'dark' : 'light'}`}>
                <p>请上传文件开始阅读</p>
                <p className="supported-formats">支持的格式: TXT, MD</p>
              </div>
            )}
          </Paper>
        </Box>
      </Container>
      <button 
        className={`mode-toggle ${isDarkMode ? 'dark' : 'light'}`}
        onClick={() => setIsDarkMode(!isDarkMode)}
      >
        切换{isDarkMode ? '明亮' : '暗黑'}模式
      </button>
    </ThemeProvider>
  );
}

export default App;
