import React, { useState, useRef, useEffect } from 'react';
import * as pdfjsLib from 'pdfjs-dist';
import * as pdfjsViewer from 'pdfjs-dist/web/pdf_viewer';
import { marked } from 'marked'; // 需要安装: npm install marked
import './styles/Reader.css';
import { pdfjs } from 'pdfjs-dist';

// 设置 PDF.js worker
pdfjsLib.GlobalWorkerOptions.workerSrc = `//cdnjs.cloudflare.com/ajax/libs/pdf.js/${pdfjsLib.version}/pdf.worker.min.js`;

// 全局变量定义
window.currentPage = 1;
window.totalPages = 0;
window.changePage = null;

// 添加全局事件跟踪对象
window.eventTracker = window.eventTracker || {
  mouseFollowHandlers: []
};

// 自动阅读器全局对象
window.autoReader = {
  charPositions: [],
  intervalId: null,
  currentIndex: 0,
  speed: 300,
  pageContainer: null,
  isScrolling: false,
  currentPage: 1,
  isPageChanging: false,
  cleanup: null
};

// 添加新的节奏阅读模式代码
// 全局变量，确保在页面切换后仍然保持状态
window.rhythmReaderGlobal = window.rhythmReaderGlobal || {
  active: false,
  speed: 300
};

// 节奏阅读模式 - 确保手动翻页后也能自动恢复
(function() {
  console.log("正在初始化节奏阅读模式...");

  // 确保只初始化一次
  if (window.rhythmReadingInitialized) {
    console.log("节奏阅读模式已经初始化过，跳过");
    return;
  }
  window.rhythmReadingInitialized = true;

  // 改进的存储函数，同时使用session和local storage提高可靠性
  function setStorage(key, value) {
    try {
      sessionStorage.setItem(key, value);
      localStorage.setItem(key, value);
    } catch (e) {
      console.error("无法写入存储", e);
    }
  }

  function getStorage(key) {
    try {
      // 优先使用sessionStorage，这样更适合单次会话
      return sessionStorage.getItem(key) || localStorage.getItem(key);
    } catch (e) {
      console.error("无法读取存储", e);
      return null;
    }
  }

  // 状态变量和功能对象
  const rhythmReader = {
    active: false,
    intervalId: null,
    speed: 300,
    currentIndex: 0,
    readIndices: new Set(),
    charElements: [],
    pageTransitionInProgress: false,

    // 初始化文本节点
    initializeTextNodes: function() {
      console.log("【节奏阅读】初始化文本节点...");

    // 重置字符元素数组
      this.charElements = [];

    // 获取内容容器
      const contentContainer = document.querySelector('.page-content');
      if (!contentContainer) {
        console.error("【节奏阅读】找不到内容容器");
        return false;
      }

    // 查找所有文本节点并处理
    const textNodes = [];
      this.findTextNodes(contentContainer, textNodes);

    if (textNodes.length === 0) {
        console.error("【节奏阅读】未找到文本节点");
        this.showNotification("无法启动节奏阅读：未找到文本内容");
      return false;
    }

      console.log(`【节奏阅读】找到 ${textNodes.length} 个文本节点`);

    // 为每个文本节点创建字符span
    textNodes.forEach(node => {
      const text = node.nodeValue;
      if (!text || text.trim() === '') return;

      // 创建文档片段来存放字符span
      const fragment = document.createDocumentFragment();

      // 为每个字符创建span
      for (let i = 0; i < text.length; i++) {
        const char = text[i];
        const span = document.createElement('span');
        span.textContent = char;
        span.className = 'rhythm-char';
          span.dataset.rhythmIndex = String(this.charElements.length);

          // 如果是空白字符，添加一个标志
          if (char.trim() === '') {
          span.dataset.whitespace = 'true';
        }

          // 保持原始样式，不设置背景
        span.style.display = 'inline';
        span.style.margin = '0';
        span.style.padding = '0';
        span.style.border = 'none';
        span.style.backgroundColor = 'transparent';

        fragment.appendChild(span);
          this.charElements.push(span);
      }

      // 替换原始节点
      if (node.parentNode) {
        node.parentNode.replaceChild(fragment, node);
      }
    });

      console.log(`【节奏阅读】总共创建了 ${this.charElements.length} 个字符span`);
      return this.charElements.length > 0;
    },

    // 递归查找所有文本节点
    findTextNodes: function(element, textNodes) {
      // 处理子节点
      for (let i = 0; i < element.childNodes.length; i++) {
      const node = element.childNodes[i];

        // 如果是文本节点且有内容
        if (node.nodeType === Node.TEXT_NODE && node.nodeValue.trim() !== '') {
        textNodes.push(node);
      } 
      // 如果是元素节点，递归处理
      else if (node.nodeType === Node.ELEMENT_NODE) {
        // 跳过读者UI元素
        if (node.id === 'rhythm-speed-slider' || 
              node.id === 'rhythm-notification' ||
            node.className === 'rhythm-char') {
          continue;
        }
          this.findTextNodes(node, textNodes);
      }
    }
    },

  // 更新文本颜色
    updateTextColors: function() {
    // 更新所有字符的颜色
      this.charElements.forEach((span, index) => {
      // 确定颜色
      let color;
      let fontWeight = 'inherit';

        if (index === this.currentIndex) {
        color = '#00aa00'; // 当前阅读的字 - 绿色
        fontWeight = 'bold';
        } else if (index === this.currentIndex + 1 || index === this.currentIndex + 2) {
          color = '#aaaaaa'; // 接下来两个字 - 淡灰色
        } else if (this.readIndices.has(index)) {
        color = '#ffffff'; // 已读字符 - 白色（表示已读）
      } else {
        color = 'rgba(160, 160, 160, 0.25)'; // 未读字符 - 非常淡的灰色（可见但不明显）
      }

      span.style.color = color;
      span.style.fontWeight = fontWeight;

      // 确保没有背景
      span.style.backgroundColor = 'transparent';
      span.style.background = 'none';
    });
    },

    // 移动到下一个字符
    moveToNextChar: function() {
    // 将当前字符标记为已读
      this.readIndices.add(this.currentIndex);

    // 移动到下一个非空白字符
    do {
        this.currentIndex++;

        // 检查是否到达末尾
        if (this.currentIndex >= this.charElements.length) {
          console.log("【节奏阅读】已到达页尾，尝试翻页");

          // 尝试点击下一页按钮
          if (!this.clickNextPageButton()) {
            // 如果没有找到下一页按钮，完全停止阅读
            this.stop();
          }
          return;
      }
    } while (
        this.currentIndex < this.charElements.length && 
        this.charElements[this.currentIndex] && 
        this.charElements[this.currentIndex].dataset.whitespace === 'true'
    );

    // 更新颜色
      this.updateTextColors();

      // 滚动到当前字符
      this.scrollToCurrentChar();
    },

    // 滚动到当前字符
    scrollToCurrentChar: function() {
      const span = this.charElements[this.currentIndex];
    if (!span) return;

    const rect = span.getBoundingClientRect();
    const contentContainer = document.querySelector('.page-content');

    // 估计行高
    const lineHeight = parseInt(window.getComputedStyle(span).lineHeight) || 
                       Math.max(20, rect.height * 1.5);

    // 确定滚动容器
    const isContainerScrollable = contentContainer && 
      contentContainer.scrollHeight > contentContainer.clientHeight &&
      ['auto', 'scroll', 'overlay'].includes(window.getComputedStyle(contentContainer).overflowY);

    if (isContainerScrollable) {
      // 容器滚动
      const containerRect = contentContainer.getBoundingClientRect();
      const relativeTop = rect.top - containerRect.top + contentContainer.scrollTop;

      const visibleTop = contentContainer.scrollTop;
      const visibleBottom = visibleTop + contentContainer.clientHeight;

      if (relativeTop < visibleTop + lineHeight || relativeTop > visibleBottom - lineHeight) {
        contentContainer.scrollBy({
          top: lineHeight * 2 * (relativeTop > visibleBottom - lineHeight ? 1 : -1),
          behavior: 'smooth'
        });
      }
    } else {
      // 窗口滚动
      const viewportTop = window.scrollY;
      const viewportBottom = viewportTop + window.innerHeight;
      const absoluteTop = rect.top + window.scrollY;

      if (absoluteTop < viewportTop + lineHeight || absoluteTop > viewportBottom - lineHeight) {
        window.scrollBy({
          top: lineHeight * 2 * (absoluteTop > viewportBottom - lineHeight ? 1 : -1),
          behavior: 'smooth'
        });
      }
    }
    },

    // 添加速度滑块
    addSpeedSlider: function() {
      let slider = document.getElementById('rhythm-speed-slider');
      if (slider) slider.remove();

      const container = document.createElement('div');
      container.id = 'rhythm-speed-slider';
      container.style.position = 'fixed';
      container.style.top = '70px';
      container.style.right = '20px';
      container.style.backgroundColor = 'white';
      container.style.padding = '10px';
      container.style.borderRadius = '5px';
      container.style.boxShadow = '0 2px 5px rgba(0,0,0,0.2)';
      container.style.zIndex = '10000';
      container.style.display = 'flex';
      container.style.alignItems = 'center';
      container.style.maxWidth = '300px';

      const label = document.createElement('label');
      label.textContent = '速度: ';
      label.style.marginRight = '8px';
      container.appendChild(label);

      const input = document.createElement('input');
      input.type = 'range';
      input.min = '50';
      input.max = '1000';
      input.step = '50';
      input.value = this.speed;
      input.style.width = '120px';

      const valueDisplay = document.createElement('span');
      valueDisplay.textContent = `${this.speed}ms`;
      valueDisplay.style.marginLeft = '8px';
      valueDisplay.style.minWidth = '60px';

      const self = this;
      input.addEventListener('input', function() {
        const newSpeed = parseInt(this.value);
        self.speed = newSpeed;
        valueDisplay.textContent = `${newSpeed}ms`;

        if (self.intervalId) {
          clearInterval(self.intervalId);
          self.intervalId = setInterval(() => self.moveToNextChar(), newSpeed);
        }
      });

      container.appendChild(input);
      container.appendChild(valueDisplay);
      document.body.appendChild(container);
    },

  // 恢复原始文本
    restoreOriginalText: function() {
    // 获取所有已创建的字符span
    const charSpans = document.querySelectorAll('.rhythm-char');

    // 按父节点分组
    const parentNodeMap = new Map();

    charSpans.forEach(span => {
      const parent = span.parentNode;
      if (!parent) return;

      if (!parentNodeMap.has(parent)) {
        parentNodeMap.set(parent, []);
      }
      parentNodeMap.get(parent).push(span);
    });

      // 对每个父节点，将连续的字符span合并回文本节点
      parentNodeMap.forEach((spans, parent) => {
      if (spans.length === 0) return;

      // 排序spans确保顺序正确
      spans.sort((a, b) => {
        const indexA = parseInt(a.dataset.rhythmIndex || '0');
        const indexB = parseInt(b.dataset.rhythmIndex || '0');
        return indexA - indexB;
      });

        // 根据连续性分组
        const groups = [];
        if (spans.length > 0) {
      let currentGroup = [spans[0]];

      for (let i = 1; i < spans.length; i++) {
        const prevIndex = parseInt(spans[i-1].dataset.rhythmIndex || '0');
        const currentIndex = parseInt(spans[i].dataset.rhythmIndex || '0');

        if (currentIndex === prevIndex + 1 && spans[i].previousSibling === spans[i-1]) {
          currentGroup.push(spans[i]);
        } else {
          groups.push(currentGroup);
          currentGroup = [spans[i]];
        }
      }

      groups.push(currentGroup);
        }

        // 对每个连续组替换为文本节点
        groups.forEach(group => {
        if (group.length === 0) return;

        try {
          const text = group.map(span => span.textContent).join('');
          const textNode = document.createTextNode(text);

          // 替换第一个span，然后移除其余的
          if (group[0].parentNode === parent) {
            parent.replaceChild(textNode, group[0]);
            for (let i = 1; i < group.length; i++) {
              if (group[i].parentNode === parent) {
                parent.removeChild(group[i]);
              }
            }
          }
        } catch (e) {
            console.error("【节奏阅读】恢复文本时出错:", e);
        }
      });
    });

    // 清除字符元素数组
      this.charElements = [];
    },

  // 启动节奏阅读
    start: function() {
      console.log("【节奏阅读】启动中...");

      // 检查是否正在进行页面转换
      if (this.pageTransitionInProgress) {
        console.log("【节奏阅读】页面转换正在进行中，延迟启动");
        setTimeout(() => this.start(), 500);
        return;
      }

    // 先停止已有的
      this.stop(false);

      // 设置为活动状态
      this.active = true;
      setStorage('rhythmReadingActive', 'true');

      // 初始化文本
      if (!this.initializeTextNodes()) {
        console.error("【节奏阅读】无法初始化文本节点");
        return;
      }

      // 重要：始终从头开始阅读
      this.currentIndex = 0;
      this.readIndices.clear();

      // 添加速度滑块
      this.addSpeedSlider();

    // 更新颜色
      this.updateTextColors();

      // 滚动到开始位置
      this.scrollToCurrentChar();

    // 设置阅读间隔
      this.intervalId = setInterval(() => this.moveToNextChar(), this.speed);

      console.log("【节奏阅读】成功启动");

      // 显示提示
      this.showNotification("节奏阅读模式已启动");

      // 劫持翻页函数 - 重要步骤
      this.hijackPageTurningFunctions();

      // 设置页面导航事件监听
      this.setupPageNavigationListeners();
    },

    // 停止节奏阅读
    stop: function(showNotification = true) {
      if (this.intervalId) {
        clearInterval(this.intervalId);
        this.intervalId = null;
      }

      if (this.active) {
        console.log("【节奏阅读】停止中...");
        this.active = false;

        // 仅当不是因翻页而停止时才清除活动状态
        if (!this.pageTransitionInProgress) {
          setStorage('rhythmReadingActive', 'false');
        }

        // 恢复原始文本
        this.restoreOriginalText();

        // 移除速度滑块
        const slider = document.getElementById('rhythm-speed-slider');
        if (slider) slider.remove();

        if (showNotification) {
          this.showNotification("节奏阅读模式已停止");
        }
      }
    },

    // 显示通知
    showNotification: function(message) {
      let notification = document.getElementById('rhythm-notification');
      if (notification) notification.remove();

      notification = document.createElement('div');
      notification.id = 'rhythm-notification';
      notification.textContent = message;
      notification.style.position = 'fixed';
      notification.style.top = '50%';
      notification.style.left = '50%';
      notification.style.transform = 'translate(-50%, -50%)';
      notification.style.backgroundColor = 'rgba(0, 0, 0, 0.8)';
      notification.style.color = 'white';
      notification.style.padding = '15px 25px';
      notification.style.borderRadius = '5px';
      notification.style.zIndex = '10001';
      notification.style.fontSize = '18px';

      document.body.appendChild(notification);

      // 2秒后自动消失
      setTimeout(() => {
        if (notification && notification.parentNode) {
          notification.parentNode.removeChild(notification);
        }
      }, 2000);
    },

    // 劫持页面翻转函数 - 改进版本
    hijackPageTurningFunctions: function() {
      if (typeof window.changePage === 'function' && !window._originalChangePage) {
        console.log("【节奏阅读】劫持changePage函数");
        window._originalChangePage = window.changePage;

        const self = this;
        window.changePage = function(page) {
          console.log("【节奏阅读】检测到changePage调用，参数:", page);

          const wasActive = self.active;
          self.pageTransitionInProgress = true;

          if (wasActive) {
            self.stop(false);
            setStorage('rhythmReadingActive', 'true'); 
            setStorage('rhythmReadingPending', 'true');
            document.body.dataset.rhythmReadingActive = 'true';
          }

          try {
            if (page === undefined || page === null) {
              console.warn('【节奏阅读】安全处理：page参数为空');
              page = {};
            }

            if (typeof page === 'object' && !page.something) {
              console.log('【节奏阅读】添加空的something对象防止错误');
              page.something = { active: false };
            }

            window._originalChangePage(page);
          } catch (err) {
            console.error("【节奏阅读】翻页函数执行出错:", err);
            self.clickNextPageButton();
          }

          setTimeout(() => {
            self.pageTransitionInProgress = false;

            if (wasActive && getStorage('rhythmReadingPending') === 'true') {
              setStorage('rhythmReadingPending', 'false');
              console.log("【节奏阅读】翻页后重新启动");
              setTimeout(() => self.start(), 500);
            }
          }, 1000);
        };
      }
    },

    // 设置页面导航事件监听 - 加强版
    setupPageNavigationListeners: function() {
      // 查找所有可能的翻页按钮
      console.log("【节奏阅读】正在设置页面导航监听");

      const self = this;

      // 使用事件委托来捕获所有可能的点击
      document.addEventListener('click', function(e) {
        // 检查点击的元素是否可能是翻页按钮
        let target = e.target;
        while (target && target !== document.body) {
          // 检查是否是翻页元素
          if (target.tagName === 'BUTTON' || target.tagName === 'A' || 
              target.getAttribute('role') === 'button') {

            const text = (target.textContent || '').trim().toLowerCase();
            const ariaLabel = (target.getAttribute('aria-label') || '').toLowerCase();

            // 检查是否是翻页元素
            if (text.includes('上一页') || text.includes('下一页') || 
                text.includes('prev') || text.includes('next') ||
                ariaLabel.includes('page') || ariaLabel.includes('页') ||
                text === '<' || text === '>' || text === '←' || text === '→') {

              if (self.active) {
                console.log(`【节奏阅读】检测到可能的翻页元素点击: ${text || ariaLabel}`);

                // 标记页面转换开始
                self.pageTransitionInProgress = true;

                // 保存状态到存储
                setStorage('rhythmReadingActive', 'true');
                setStorage('rhythmReadingPending', 'true');
                document.body.dataset.rhythmReadingActive = 'true';

                // 停止当前的阅读
                self.stop(false);

                // 添加延迟执行的监听器，确保数据在页面加载后恢复
                setTimeout(() => {
                  self.pageTransitionInProgress = false;

                  if (getStorage('rhythmReadingPending') === 'true') {
                    setStorage('rhythmReadingPending', 'false');
                    self.start();
                  }
                }, 1500);
              }

              break;
            }
          }

          target = target.parentNode;
        }
      });

      // 监听键盘导航事件
      document.addEventListener('keydown', function(e) {
        // 检查是否是翻页快捷键
        if (['ArrowLeft', 'ArrowRight', 'PageUp', 'PageDown'].includes(e.key)) {
          if (self.active) {
            console.log(`【节奏阅读】检测到键盘翻页: ${e.key}`);

            // 保存状态到存储
            setStorage('rhythmReadingActive', 'true');
            setStorage('rhythmReadingPending', 'true');

            // 标记页面转换开始
            self.pageTransitionInProgress = true;

            // 停止当前的阅读
            self.stop(false);

            // 添加延迟执行的监听器
            setTimeout(() => {
              self.pageTransitionInProgress = false;

              if (getStorage('rhythmReadingPending') === 'true') {
                setStorage('rhythmReadingPending', 'false');
                self.start();
              }
            }, 1500);
          }
        }
      });

      // 添加popstate监听器捕获浏览器前进/后退导航
      window.addEventListener('popstate', function() {
        if (self.active) {
          console.log("【节奏阅读】检测到历史导航事件");

          // 保存状态
          setStorage('rhythmReadingActive', 'true');
          setStorage('rhythmReadingPending', 'true');

          // 标记页面转换开始
          self.pageTransitionInProgress = true;

          // 停止当前阅读
          self.stop(false);

          // 延迟恢复
          setTimeout(() => {
            self.pageTransitionInProgress = false;

            if (getStorage('rhythmReadingPending') === 'true') {
              setStorage('rhythmReadingPending', 'false');
              self.start();
            }
          }, 1500);
        }
      });
    },

    // 改进的自动翻页函数，更精确地处理状态保存
    clickNextPageButton: function() {
      console.log("【节奏阅读】尝试查找并点击下一页按钮");

      // 查找下一页按钮
      const nextPageSelectors = [
        'button.next-page', 
        'button[aria-label="下一页"]',
        'a.next-page',
        'a[aria-label="下一页"]',
        '.next-page',
        '#next-page',
        'button.next',
        'a.next'
      ];

      let nextPageButton = null;

      // 尝试通过选择器找到按钮
      for (const selector of nextPageSelectors) {
        try {
          const elements = document.querySelectorAll(selector);
          for (const el of elements) {
            if (el.offsetParent !== null) { // 检查元素是否可见
              console.log(`【节奏阅读】找到下一页按钮: ${selector}`);
              nextPageButton = el;
              break;
            }
          }
          if (nextPageButton) break;
        } catch (e) {
          // 某些选择器可能不被支持，忽略错误
        }
      }

      // 如果没找到，尝试按文本内容查找
      if (!nextPageButton) {
        const buttons = document.querySelectorAll('button, a, div[role="button"]');
        for (const button of buttons) {
          const text = button.textContent.trim().toLowerCase();
          if ((text.includes('下一页') || text.includes('next') || text === '>' || text === '→') && 
              button.offsetParent !== null) {
            console.log(`【节奏阅读】通过文本内容找到下一页按钮: ${text}`);
            nextPageButton = button;
            break;
          }
        }
      }

      if (nextPageButton) {
        console.log("【节奏阅读】模拟点击下一页按钮");

        // 明确标记页面转换开始
        this.pageTransitionInProgress = true;

        // 保存状态
        setStorage('rhythmReadingActive', 'true');
        setStorage('rhythmReadingPending', 'true');
        document.body.dataset.rhythmReadingActive = 'true';

        // 恢复原始文本
        this.restoreOriginalText();

        // 停止计时器
        if (this.intervalId) {
          clearInterval(this.intervalId);
          this.intervalId = null;
        }

        // 模拟点击
        nextPageButton.click();

        // 延迟结束页面转换状态
        setTimeout(() => {
          this.pageTransitionInProgress = false;
        }, 1000);

        return true;
  } else {
        console.log("【节奏阅读】未找到下一页按钮");
        return false;
      }
    }
  };

  // 暴露到全局，以便调试和直接调用
  window.rhythmReader = rhythmReader;

  // 额外添加一个全局安全函数用于检查节奏阅读状态
  window.isRhythmActive = function() {
    return rhythmReader.active || getStorage('rhythmReadingActive') === 'true';
  };

  // 添加一个停止函数供外部安全调用
  window.stopRhythmSafely = function() {
    try {
      setStorage('rhythmReadingActive', 'false');
      setStorage('rhythmReadingPending', 'false');

      if (rhythmReader && typeof rhythmReader.stop === 'function') {
        rhythmReader.stop();
      }
    } catch (e) {
      console.error("停止节奏阅读出错", e);
    }
  };

  // 设置页面加载监听器，确保页面转换后能恢复
  function setupPageLoadListener() {
    // DOM内容加载和完全加载的事件监听...

    // 改进的页面变化监测
    const observer = new MutationObserver(function(mutations) {
      // 检测到内容变化且有待处理的节奏阅读时
      if (getStorage('rhythmReadingPending') === 'true') {
        const contentChanges = mutations.some(m => 
          m.type === 'childList' && m.addedNodes.length > 5); // 使用更高的阈值，确保是真正的内容变化

        if (contentChanges) {
          console.log("【节奏阅读】检测到大量DOM变化，可能是页面切换");

          // 给页面一点时间稳定下来
          setTimeout(() => {
            if (getStorage('rhythmReadingPending') === 'true') {
              console.log("【节奏阅读】页面内容变化后，尝试恢复阅读");
              setStorage('rhythmReadingPending', 'false');

              if (!rhythmReader.pageTransitionInProgress) {
                rhythmReader.start();
              }
            }
          }, 800);
        }
      }
    });

    // 观察body变化
    observer.observe(document.body, {
      childList: true,
      subtree: true
    });
  }

  // 设置页面加载监听器
  setupPageLoadListener();

  // 检查是否需要立即恢复阅读状态
  if (getStorage('rhythmReadingActive') === 'true') {
    console.log("【节奏阅读】检测到之前的阅读状态，准备恢复");
    setStorage('rhythmReadingPending', 'false');

    setTimeout(function() {
      rhythmReader.start();
    }, 800);
  }

  console.log("【节奏阅读】初始化完成");
})();

