  // 添加速度滑块UI
  const addSpeedSlider = function() {
    // 检查是否已存在
    const existingSlider = document.getElementById('rhythm-speed-container');
    if (existingSlider) existingSlider.remove();

    // 创建容器
    const container = document.createElement('div');
    container.id = 'rhythm-speed-container';
    container.style.cssText = `
      position: fixed;
      top: 50px;
      right: 10px;
      background: red;
      padding: 10px 15px;
      border-radius: 5px;
      z-index: 999999;
      display: flex;
      flex-direction: column;
      width: 200px;
      box-shadow: 0 2px 8px rgba(0,0,0,0.3);
      border: 3px solid black;
    `;

    // 添加标签
    const label = document.createElement('div');
    label.textContent = "阅读速度: " + currentSpeed + "ms";
    label.id = 'rhythm-speed-label';
    label.style.cssText = `
      color: black;
      font-size: 14px;
      font-weight: bold;
      margin-bottom: 8px;
    `;
    container.appendChild(label);

    // 添加滑块
    const slider = document.createElement('input');
    slider.type = 'range';
    slider.min = '50';
    slider.max = '800';
    slider.step = '50';
    slider.value = currentSpeed;
    slider.style.width = '100%';
    slider.style.height = '20px';

    // 滑块事件
    slider.oninput = function() {
      currentSpeed = parseInt(this.value);
      label.textContent = "阅读速度: " + currentSpeed + "ms";

      // 如果正在阅读，更新间隔
      if (intervalId) {
        clearInterval(intervalId);
        intervalId = setInterval(nextChar, currentSpeed);
      }
    };

    container.appendChild(slider);
    document.body.appendChild(container);
    console.log("速度滑块已添加！位置：测试按钮下方");
    return container;
  };

  // 启动阅读
  const startReading = function() {
    console.log("启动节奏阅读...");

    // 清理之前的实例
    cleanup();

    const container = document.querySelector('.pdf-content') || 
                      document.querySelector('[class*="pdf"]') || 
                      document.querySelector('article') || 
                      document.querySelector('main') || 
                      document.body;

    const textNodes = [];
    const findText = function(element) {
      for (let i = 0; i < element.childNodes.length; i++) {
        const node = element.childNodes[i];
        if (node.nodeType === 3 && node.nodeValue.trim() !== '') {
          textNodes.push(node);
        } else if (node.nodeType === 1 && 
                  !['SCRIPT', 'STYLE', 'BUTTON', 'INPUT', 'SELECT', 'NOSCRIPT'].includes(node.tagName)) {
          findText(node);
        }
      }
    };
    findText(container);

    if (textNodes.length === 0) {
      showMessage("错误: 未找到文本内容");
      return;
    }

    try {
      textNodes.forEach(node => {
        if (!node.nodeValue || !node.parentNode) return;

        const text = node.nodeValue;
        const fragment = document.createDocumentFragment();

        for (let i = 0; i < text.length; i++) {
          const span = document.createElement('span');
          span.textContent = text[i];
          span.className = 'rhythm-char';
          span.dataset.index = charElements.length;

          fragment.appendChild(span);
          charElements.push(span);
        }

        node.parentNode.replaceChild(fragment, node);
      });
    } catch(e) {
      console.error("处理文本失败:", e);
      showMessage("生成节奏阅读效果失败");
      return;
    }

    if (charElements.length === 0) {
      showMessage("未找到可读内容");
      return;
    }

    isActive = true;
    currentIndex = 0;

    updateDisplay();
    intervalId = setInterval(nextChar, currentSpeed);

    addControlPanel();
    addSpeedSlider(); // 确保滑块被添加
  };

  // 更新显示
  const updateDisplay = function() {
    charElements.forEach((span, index) => {
      if (index === currentIndex) {
        span.style.color = 'green';
        span.style.fontWeight = 'bold';
      } else if (readIndices.has(index)) {
        span.style.color = 'white';
      } else {
        span.style.color = 'rgba(200,200,200,0.3)';
      }
    });
  };

  // 检查是否在视口中
  const isInViewport = function(element) {
    const rect = element.getBoundingClientRect();
    return (
      rect.top >= 0 &&
      rect.left >= 0 &&
      rect.bottom <= (window.innerHeight || document.documentElement.clientHeight) &&
      rect.right <= (window.innerWidth || document.documentElement.clientWidth)
    );
  };

  // 下一个字符
  const nextChar = function() {
    if (!isActive || !charElements.length) return;

    readIndices.add(currentIndex);

    currentIndex++;
    if (currentIndex >= charElements.length) {
      if (intervalId) {
        clearInterval(intervalId);
        intervalId = null;
      }
      console.log("当前页已读完，尝试翻页");
      autoTurnPage();
      return;
    }

    updateDisplay();

    // 确保当前字符可见
    const currentChar = charElements[currentIndex];
    if (currentChar && !isInViewport(currentChar)) {
      currentChar.scrollIntoView({
        behavior: 'smooth',
        block: 'center'
      });
    }
  };

  // 处理模式切换
  const handleModeChange = function(mode) {
    console.log("模式更改为:", mode);

    if (mode !== 'rhythm' && mode !== '节奏阅读' && isActive) {
      cleanup();
    }
  };

  // 重写模式切换函数
  const originalFunction = window.readingModeChanged;
  window.readingModeChanged = function(mode) {
    if (typeof originalFunction === 'function') {
      try {
        originalFunction(mode);
      } catch(e) {}
    }
    handleModeChange(mode);
  };

  // 添加测试按钮
  const addTestButton = function() {
    const button = document.createElement('button');
    button.id = 'rhythm-test-button';
    button.textContent = '测试节奏阅读';
    button.style.cssText = `
      position: fixed;
      top: 10px;
      right: 10px;
      z-index: 999999;
      background: #ff9800;
      color: white;
      padding: 8px 12px;
      border: none;
      border-radius: 4px;
      font-size: 14px;
      cursor: pointer;
    `;

    button.onclick = startReading;  // 直接启动阅读
    document.body.appendChild(button);
  };

  // 初始化
  const initialize = function() {
    console.log("初始化节奏阅读组件...");

    addTestButton();
    addSpeedSlider(); // 在初始化时就添加速度滑块
  };

  // 等待DOM加载完成
  document.addEventListener('DOMContentLoaded', initialize);

  // 暴露全局API
  window.manualStartRhythmReading = startReading;

  console.log("节奏阅读模式初始化完成 - 增强控制版");
})();
