import React, { useState, useRef } from 'react';
import * as pdfjsLib from 'pdfjs-dist';

// 确保使用正确的 worker 版本
const workerSrc = require('pdfjs-dist/build/pdf.worker.entry');
pdfjsLib.GlobalWorkerOptions.workerSrc = workerSrc;

function App() {
  const [isDarkMode, setIsDarkMode] = useState(false);
  const [pages, setPages] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const [totalPages, setTotalPages] = useState(0);
  const [fileName, setFileName] = useState('');
  const [loading, setLoading] = useState(false);
  const fileRef = useRef(null);

  // 处理PDF文件
  const handlePdfFile = async (file) => {
    try {
      setLoading(true);
      console.log('开始处理PDF文件:', file.name);

      const fileArrayBuffer = await file.arrayBuffer();
      
      // 使用 getDocument 的新语法
      const loadingTask = pdfjsLib.getDocument({
        data: fileArrayBuffer,
        cMapUrl: 'https://cdn.jsdelivr.net/npm/pdfjs-dist@3.11.174/cmaps/',
        cMapPacked: true,
      });

      const pdf = await loadingTask.promise;
      console.log('PDF加载成功，总页数:', pdf.numPages);
      
      setTotalPages(pdf.numPages);
      fileRef.current = file;

      // 加载第一页
      const page = await pdf.getPage(1);
      const viewport = page.getViewport({ scale: 1.5 });
      
      const canvas = document.createElement('canvas');
      const context = canvas.getContext('2d');
      
      canvas.height = viewport.height;
      canvas.width = viewport.width;

      await page.render({
        canvasContext: context,
        viewport: viewport
      }).promise;

      const pageImage = canvas.toDataURL('image/jpeg', 0.8);
      setPages([pageImage]);
      
      setLoading(false);

    } catch (error) {
      console.error('PDF处理错误:', error);
      setLoading(false);
      alert(`PDF处理失败: ${error.message}`);
    }
  };

  // 加载指定页面
  const loadPage = async (pageNumber) => {
    if (!fileRef.current) {
      console.error('没有找到PDF文件引用');
      return;
    }

    try {
      setLoading(true);
      console.log(`开始加载第 ${pageNumber} 页`); // 调试日志

      const fileArrayBuffer = await new Promise((resolve, reject) => {
        const reader = new FileReader();
        reader.onload = () => resolve(reader.result);
        reader.onerror = reject;
        reader.readAsArrayBuffer(fileRef.current);
      });

      const pdf = await pdfjsLib.getDocument({ data: fileArrayBuffer }).promise;
      const page = await pdf.getPage(pageNumber);
      const viewport = page.getViewport({ scale: 1.5 });
      
      const canvas = document.createElement('canvas');
      const context = canvas.getContext('2d');
      canvas.height = viewport.height;
      canvas.width = viewport.width;

      await page.render({
        canvasContext: context,
        viewport: viewport
      }).promise;

      const pageImage = canvas.toDataURL('image/jpeg', 0.8);
      
      setPages(prevPages => {
        const newPages = [...prevPages];
        newPages[pageNumber - 1] = pageImage;
        return newPages;
      });

      console.log(`第 ${pageNumber} 页加载完成`); // 调试日志
      setLoading(false);

    } catch (error) {
      console.error(`加载第 ${pageNumber} 页时出错:`, error);
      setLoading(false);
      alert(`加载页面失败: ${error.message}`);
    }
  };

  // 文件上传处理
  const handleFileUpload = async (event) => {
    const file = event.target.files[0];
    if (!file) return;

    // 检查文件大小（100MB限制）
    if (file.size > 100 * 1024 * 1024) {
      alert('文件大小不能超过100MB');
      return;
    }

    setFileName(file.name);
    setPages([]);
    setCurrentPage(1);

    console.log('文件类型:', file.type); // 调试日志

    if (file.type === 'application/pdf') {
      await handlePdfFile(file);
    } else {
      // 处理文本文件
      try {
        const text = await file.text();
        const textPages = text.split('\n\n').filter(page => page.trim());
        setPages(textPages);
        setTotalPages(textPages.length);
      } catch (error) {
        console.error('文件读取失败:', error);
        alert('文件读取失败，请重试');
      }
    }
  };

  // 翻页处理
  const handlePageChange = (direction) => {
    const newPage = direction === 'next' 
      ? Math.min(currentPage + 1, totalPages)
      : Math.max(currentPage - 1, 1);
    
    setCurrentPage(newPage);
    if (pages[newPage - 1] === undefined) {
      loadPage(newPage);
    }
  };

  return (
    <div className={`App ${isDarkMode ? 'dark' : 'light'}`}>
      <div className="app-header">
        <h1>ADHD 阅读助手</h1>
        <div className="controls">
          <input
            type="file"
            accept=".txt,.md,.pdf"
            onChange={handleFileUpload}
            id="file-upload"
          />
          <label htmlFor="file-upload">选择文件</label>
          {fileName && <span className="file-name">当前文件: {fileName}</span>}
          <button onClick={() => setIsDarkMode(!isDarkMode)}>
            切换{isDarkMode ? '明亮' : '暗黑'}模式
          </button>
        </div>
      </div>

      <div className="reader-container">
        {loading ? (
          <div className="loading">
            <div className="loading-spinner"></div>
            <div>正在加载PDF，请稍候...</div>
          </div>
        ) : pages.length > 0 ? (
          <div className="book-view">
            <button 
              className="page-btn prev" 
              onClick={() => handlePageChange('prev')}
              disabled={currentPage === 1}
            >
              ‹
            </button>
            <div className="page-content">
              {pages[currentPage - 1]?.startsWith('data:image') ? (
                <img 
                  src={pages[currentPage - 1]} 
                  alt={`页面 ${currentPage}`}
                  style={{ maxWidth: '100%', height: 'auto' }}
                />
              ) : (
                <div className="text-content">
                  {pages[currentPage - 1]}
                </div>
              )}
            </div>
            <button 
              className="page-btn next" 
              onClick={() => handlePageChange('next')}
              disabled={currentPage === totalPages}
            >
              ›
            </button>
            <div className="page-number">
              {currentPage} / {totalPages}
            </div>
          </div>
        ) : (
          <div className="upload-prompt">
            <p>请上传文件开始阅读</p>
            <p>支持的格式: PDF, TXT, MD (最大100MB)</p>
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
