import React, { useState } from 'react';
import * as pdfjsLib from 'pdfjs-dist';
import './styles/Reader.css';

// 确保使用正确的 worker 版本
const workerSrc = require('pdfjs-dist/build/pdf.worker.entry');
pdfjsLib.GlobalWorkerOptions.workerSrc = workerSrc;

function App() {
  const [isDarkMode, setIsDarkMode] = useState(true);
  const [pages, setPages] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const [totalPages, setTotalPages] = useState(0);
  const [fileName, setFileName] = useState('');
  const [loading, setLoading] = useState(false);

  // 处理PDF文件
  const handlePdfFile = async (file) => {
    try {
      setLoading(true);
      const fileArrayBuffer = await file.arrayBuffer();
      const pdf = await pdfjsLib.getDocument({
        data: fileArrayBuffer,
      }).promise;

      setTotalPages(pdf.numPages);
      const pagesContent = [];

      // 获取第一页
      const page = await pdf.getPage(1);
      const viewport = page.getViewport({ scale: 1.0 });
      const textContent = await page.getTextContent();

      // 处理文本内容，保持原始位置信息
      const pageText = textContent.items.map(item => ({
        text: item.str,
        x: item.transform[4],
        y: viewport.height - item.transform[5], // 转换坐标系
        fontSize: Math.sqrt((item.transform[0] * item.transform[0]) + (item.transform[1] * item.transform[1])),
        fontFamily: item.fontName || 'serif'
      }));

      pagesContent.push(pageText);
      setPages(pagesContent);
      setCurrentPage(1);
      setLoading(false);

    } catch (error) {
      console.error('PDF处理错误:', error);
      setLoading(false);
      alert('PDF文件处理失败，请重试');
    }
  };

  // 处理TXT文件
  const handleTxtFile = async (file) => {
    try {
      setLoading(true);
      const text = await file.text();
      setContent(text);
      setLoading(false);
    } catch (error) {
      console.error('TXT处理错误:', error);
      setLoading(false);
      alert('TXT文件处理失败，请重试');
    }
  };

  // 文件上传处理
  const handleFileUpload = async (event) => {
    const file = event.target.files[0];
    if (!file) return;

    if (file.size > 100 * 1024 * 1024) {
      alert('文件大小不能超过100MB');
      return;
    }

    setFileName(file.name);
    setContent('');

    if (file.type === 'application/pdf') {
      await handlePdfFile(file);
    } else if (file.type === 'text/plain' || file.name.endsWith('.txt')) {
      await handleTxtFile(file);
    }
  };

  // 加载指定页面
  const loadPage = async (pageNum) => {
    try {
      setLoading(true);
      const fileArrayBuffer = await fileRef.current.arrayBuffer();
      const pdf = await pdfjsLib.getDocument({
        data: fileArrayBuffer,
      }).promise;

      const page = await pdf.getPage(pageNum);
      const viewport = page.getViewport({ scale: 1.0 });
      const textContent = await page.getTextContent();

      const pageText = textContent.items.map(item => ({
        text: item.str,
        x: item.transform[4],
        y: viewport.height - item.transform[5],
        fontSize: Math.sqrt((item.transform[0] * item.transform[0]) + (item.transform[1] * item.transform[1])),
        fontFamily: item.fontName || 'serif'
      }));

      setPages(prev => {
        const newPages = [...prev];
        newPages[pageNum - 1] = pageText;
        return newPages;
      });
      setLoading(false);
    } catch (error) {
      console.error('页面加载错误:', error);
      setLoading(false);
    }
  };

  // 处理翻页
  const handlePageChange = (direction) => {
    const newPage = direction === 'next' 
      ? Math.min(currentPage + 1, totalPages)
      : Math.max(currentPage - 1, 1);
    
    setCurrentPage(newPage);
    if (!pages[newPage - 1]) {
      loadPage(newPage);
    }
  };

  return (
    <div className="App dark">
      <div className="app-container">
        <h1 className="app-title">ADHD 阅读助手</h1>
        
        <div className="control-panel">
          <input
            type="file"
            accept=".txt,.pdf"
            onChange={handleFileUpload}
            id="file-upload"
            className="file-input"
          />
          <label htmlFor="file-upload" className="control-btn">
            选择文件
          </label>
          
          {fileName && (
            <span className="file-info">
              当前文件: {fileName}
            </span>
          )}
        </div>

        <div className="reader-container">
          {loading ? (
            <div className="loading">
              <div className="loading-spinner"></div>
              <div>正在加载内容，请稍候...</div>
            </div>
          ) : pages.length > 0 ? (
            <div className="book-view">
              <button 
                className="page-btn prev" 
                onClick={() => handlePageChange('prev')}
                disabled={currentPage === 1}
              >
                ‹
              </button>
              
              <div className="page-content">
                <div className="page-layout">
                  {pages[currentPage - 1]?.map((item, index) => (
                    <span
                      key={index}
                      style={{
                        position: 'absolute',
                        left: `${item.x}px`,
                        top: `${item.y}px`,
                        fontSize: `${item.fontSize}px`,
                        fontFamily: item.fontFamily,
                        letterSpacing: '-0.02em',
                        whiteSpace: 'pre'
                      }}
                    >
                      {item.text}
                    </span>
                  ))}
                </div>
              </div>

              <button 
                className="page-btn next" 
                onClick={() => handlePageChange('next')}
                disabled={currentPage === totalPages}
              >
                ›
              </button>

              <div className="page-number">
                {currentPage} / {totalPages}
              </div>
            </div>
          ) : (
            <div className="upload-prompt">
              <p>请上传文件开始阅读</p>
              <p className="supported-formats">支持的格式: PDF, TXT (最大100MB)</p>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

export default App;
