import React, { useState, useRef, useEffect } from 'react';
import * as pdfjsLib from 'pdfjs-dist';
import * as pdfjsViewer from 'pdfjs-dist/web/pdf_viewer';
import { marked } from 'marked'; // 需要安装: npm install marked
import './styles/Reader.css';
import { pdfjs } from 'pdfjs-dist';

// 设置 PDF.js worker
pdfjsLib.GlobalWorkerOptions.workerSrc = `//cdnjs.cloudflare.com/ajax/libs/pdf.js/${pdfjsLib.version}/pdf.worker.min.js`;

// 全局变量定义
window.currentPage = 1;
window.totalPages = 0;
window.changePage = null;

// 自动阅读器全局对象
window.autoReader = {
  charPositions: [],
  intervalId: null,
  currentIndex: 0,
  speed: 300,
  pageContainer: null,
  isScrolling: false,
  currentPage: 1,
  isPageChanging: false,
  cleanup: null
};

// 添加新的节奏阅读模式代码
// 超可靠的节奏阅读模式 - 重点解决手动翻页问题
(function() {
  console.log("初始化超可靠版节奏阅读模式...");

  // ===== 状态控制 - 使用三种方式保存状态以确保可靠性 =====
  function saveState(active) {
    try {
      // 使用三种不同的存储方式来确保状态不丢失
      sessionStorage.setItem('rhythmReading', active ? 'true' : 'false');
      localStorage.setItem('rhythmReading', active ? 'true' : 'false');
      document.cookie = `rhythmReading=${active ? 'true' : 'false'}; path=/; max-age=3600`;
      console.log(`节奏阅读模式状态已保存: ${active}`);
    } catch(e) {
      console.error("保存状态失败", e);
    }
  }

  function isActive() {
    try {
      // 按优先级检查三种存储
      if (sessionStorage.getItem('rhythmReading') === 'true') return true;
      if (localStorage.getItem('rhythmReading') === 'true') return true;
      if (document.cookie.includes('rhythmReading=true')) return true;
      return false;
    } catch(e) {
      console.error("读取状态失败", e);
      return false;
    }
  }

  // ===== 核心功能对象 =====
  const rhythmReader = {
    intervalId: null,
    speed: 300,
    currentIndex: 0,
    readIndices: new Set(),
    charElements: [],
    injectedCode: false,

    injectAutoStartScript() {
      if (this.injectedCode) return;

      try {
        const script = document.createElement('script');
        script.id = 'rhythm-autostart-script';
        script.textContent = `
          console.log("节奏阅读自动启动脚本已加载");
          (function() {
            function checkAndStart() {
              try {
                const isActive = (
                  sessionStorage.getItem('rhythmReading') === 'true' || 
                  localStorage.getItem('rhythmReading') === 'true' || 
                  document.cookie.includes('rhythmReading=true')
                );

                if (isActive && window.rhythmReader && !window.rhythmReader.intervalId) {
                  console.log("页面加载后自动恢复节奏阅读模式");
                  window.rhythmReader.start();
                }
              } catch(e) {
                console.error("自动启动检查失败", e);
              }
            }

            if (document.readyState === 'loading') {
              document.addEventListener('DOMContentLoaded', checkAndStart);
            } else {
              setTimeout(checkAndStart, 100);
            }

            window.addEventListener('load', checkAndStart);

            [500, 1000, 1500, 2000].forEach(delay => {
              setTimeout(checkAndStart, delay);
            });
          })();
        `;

        document.head.appendChild(script);
        this.injectedCode = true;
        console.log("注入了自动启动脚本");
      } catch(e) {
        console.error("注入自动启动脚本失败", e);
      }
    },

    start() {
      console.log("开始节奏阅读模式");

      this.stop(false);
      saveState(true);
      this.injectAutoStartScript();
      this.interceptPageTurning();

      if (!this.initializeTextNodes()) {
        console.error("无法初始化文本节点，没有找到可读内容");
        return;
      }

      this.currentIndex = 0;
      this.readIndices.clear();
      this.addSpeedSlider();
      this.updateTextColors();
      this.scrollToCurrentChar();

      this.intervalId = setInterval(() => this.moveToNextChar(), this.speed);
      this.showNotification("节奏阅读模式已启动");
    },

    stop(updateState = true) {
      if (this.intervalId) {
        clearInterval(this.intervalId);
        this.intervalId = null;
      }

      this.restoreOriginalText();

      const slider = document.getElementById('rhythm-speed-slider');
      if (slider) slider.remove();

      if (updateState) {
        saveState(false);
      }
    },

    showNotification(message) {
      let notification = document.getElementById('rhythm-notification');
      if (notification) notification.remove();

      notification = document.createElement('div');
      notification.id = 'rhythm-notification';
      notification.textContent = message;
      notification.style.position = 'fixed';
      notification.style.top = '50%';
      notification.style.left = '50%';
      notification.style.transform = 'translate(-50%, -50%)';
      notification.style.backgroundColor = 'rgba(0, 0, 0, 0.8)';
      notification.style.color = 'white';
      notification.style.padding = '15px 25px';
      notification.style.borderRadius = '5px';
      notification.style.zIndex = '10001';
      notification.style.fontSize = '18px';

      document.body.appendChild(notification);

      setTimeout(() => {
        if (notification && notification.parentNode) {
          notification.parentNode.removeChild(notification);
        }
      }, 2000);
    },

    interceptPageTurning() {
      if (window._pageIntercepted) return;
      window._pageIntercepted = true;

      if (typeof window.changePage === 'function' && !window._originalChangePage) {
        window._originalChangePage = window.changePage;

        window.changePage = (delta) => {
          console.log(`拦截到changePage调用(${delta})，当前状态: ${isActive()}`);

          if (isActive()) {
            saveState(true);
            this.stop(false);
            this.showNotification("正在翻页，将自动继续阅读...");
            document.body.dataset.autoResumeRhythm = "true";
          }

          window._originalChangePage(delta);
        };

        console.log("已拦截changePage函数");
      }

      const findAndInterceptButtons = () => {
        const buttons = document.querySelectorAll('button, a, [role="button"]');
        let count = 0;

        buttons.forEach(button => {
          if (button.dataset.rhythmIntercepted) return;

          const text = (button.textContent || '').trim().toLowerCase();
          if (text.includes('下一页') || text.includes('上一页') || 
              text.includes('next') || text.includes('prev') ||
              text === '>' || text === '<' || text === '→' || text === '←') {

            button.dataset.rhythmIntercepted = "true";

            const originalClick = button.onclick;

            button.onclick = (event) => {
              console.log(`拦截到翻页按钮点击: ${text}`);

              if (isActive()) {
                saveState(true);
                this.stop(false);
                this.showNotification("正在翻页，将自动继续阅读...");
                document.body.dataset.autoResumeRhythm = "true";
              }

              if (originalClick) {
                return originalClick.call(this, event);
              }
            };

            count++;
            console.log(`已拦截翻页按钮: ${text}`);
          }
        });

        if (count > 0) {
          console.log(`总共拦截了${count}个翻页按钮`);
        }
      };

      findAndInterceptButtons();

      const buttonObserver = new MutationObserver(mutations => {
        mutations.forEach(mutation => {
          if (mutation.type === 'childList' && mutation.addedNodes.length > 0) {
            findAndInterceptButtons();
          }
        });
      });

      buttonObserver.observe(document.body, {
        childList: true,
        subtree: true
      });

      document.addEventListener('keydown', event => {
        if (['ArrowLeft', 'ArrowRight', 'PageUp', 'PageDown'].includes(event.key)) {
          console.log(`拦截到键盘翻页: ${event.key}`);

          if (isActive()) {
            saveState(true);
            this.stop(false);
            document.body.dataset.autoResumeRhythm = "true";
          }
        }
      });

      console.log("已拦截键盘翻页事件");

      const contentObserver = new MutationObserver(mutations => {
        if (document.body.dataset.autoResumeRhythm === "true" && isActive()) {
          const contentChanges = mutations.filter(m => 
            m.type === 'childList' && m.addedNodes.length > 0);

          if (contentChanges.length > 0) {
            console.log("检测到内容变化，正在恢复节奏阅读...");

            document.body.dataset.autoResumeRhythm = "false";

            setTimeout(() => {
              if (isActive() && !this.intervalId) {
                console.log("在内容变化后自动恢复节奏阅读");
                this.start();
              }
            }, 300);
          }
        }
      });

      contentObserver.observe(document.body, {
        childList: true,
        subtree: true
      });

      console.log("已设置内容变化监测");
    },

    initializeTextNodes() {
      this.charElements = [];

      const contentContainer = document.querySelector('.page-content') || document.body;

      const textNodes = [];
      this.findTextNodes(contentContainer, textNodes);

      if (textNodes.length === 0) {
        console.error("未找到文本节点");
        return false;
      }

      textNodes.forEach(node => {
        const text = node.nodeValue;
        if (!text || text.trim() === '') return;

        const fragment = document.createDocumentFragment();

        for (let i = 0; i < text.length; i++) {
          const char = text[i];
          const span = document.createElement('span');
          span.textContent = char;
          span.className = 'rhythm-char';
          span.dataset.rhythmIndex = this.charElements.length;

          if (char.trim() === '') {
            span.dataset.whitespace = 'true';
          }

          span.style.display = 'inline';
          span.style.margin = '0';
          span.style.padding = '0';
          span.style.border = 'none';
          span.style.backgroundColor = 'transparent';

          fragment.appendChild(span);
          this.charElements.push(span);
        }

        if (node.parentNode) {
          node.parentNode.replaceChild(fragment, node);
        }
      });

      console.log(`初始化了 ${this.charElements.length} 个字符`);
      return this.charElements.length > 0;
    },

    findTextNodes(element, textNodes) {
      for (let i = 0; i < element.childNodes.length; i++) {
        const node = element.childNodes[i];

        if (node.nodeType === Node.TEXT_NODE && node.nodeValue.trim() !== '') {
          textNodes.push(node);
        } 
        else if (node.nodeType === Node.ELEMENT_NODE) {
          if (node.id === 'rhythm-speed-slider' || 
              node.id === 'rhythm-notification' ||
              node.className === 'rhythm-char') {
            continue;
          }
          this.findTextNodes(node, textNodes);
        }
      }
    },

    updateTextColors() {
      this.charElements.forEach((span, index) => {
        let color;
        let fontWeight = 'inherit';

        if (index === this.currentIndex) {
          color = '#00aa00';
          fontWeight = 'bold';
        } else if (index === this.currentIndex + 1 || index === this.currentIndex + 2) {
          color = '#aaaaaa';
        } else if (this.readIndices.has(index)) {
          color = '#ffffff';
        } else {
          color = 'rgba(160, 160, 160, 0.25)';
        }

        span.style.color = color;
        span.style.fontWeight = fontWeight;
        span.style.backgroundColor = 'transparent';
        span.style.background = 'none';
      });
    },

    moveToNextChar() {
      this.readIndices.add(this.currentIndex);

      do {
        this.currentIndex++;

        if (this.currentIndex >= this.charElements.length) {
          console.log("已到达页尾，尝试翻页");

          if (!this.clickNextPageButton()) {
            this.stop(true);
          }
          return;
        }
      } while (
        this.currentIndex < this.charElements.length && 
        this.charElements[this.currentIndex] && 
        this.charElements[this.currentIndex].dataset.whitespace === 'true'
      );

      this.updateTextColors();
      this.scrollToCurrentChar();
    },

    clickNextPageButton() {
      console.log("尝试查找并点击下一页按钮");

      const nextPageSelectors = [
        'button.next-page', 
        'button[aria-label="下一页"]',
        'a.next-page',
        'a[aria-label="下一页"]',
        '.next-page',
        '#next-page',
        'button.next',
        'a.next'
      ];

      let nextPageButton = null;

      for (const selector of nextPageSelectors) {
        try {
          const elements = document.querySelectorAll(selector);
          for (const el of elements) {
            if (el.offsetParent !== null) {
              console.log(`找到下一页按钮: ${selector}`);
              nextPageButton = el;
              break;
            }
          }
          if (nextPageButton) break;
        } catch (e) {
          // 某些选择器可能不被支持，忽略错误
        }
      }

      if (nextPageButton) {
        console.log("模拟点击下一页按钮");

        saveState(true);
        document.body.dataset.autoResumeRhythm = "true";
        this.restoreOriginalText();

        if (this.intervalId) {
          clearInterval(this.intervalId);
          this.intervalId = null;
        }

        this.showNotification("正在翻页，将自动继续阅读...");

        try {
          nextPageButton.click();
        } catch(e) {
          console.error("点击下一页按钮失败", e);
          return false;
        }

        return true;
      } else if (typeof window._originalChangePage === 'function') {
        console.log("使用changePage函数翻页");

        saveState(true);
        document.body.dataset.autoResumeRhythm = "true";
        this.restoreOriginalText();

        if (this.intervalId) {
          clearInterval(this.intervalId);
          this.intervalId = null;
        }

        this.showNotification("正在翻页，将自动继续阅读...");

        try {
          window._originalChangePage(1);
        } catch(e) {
          console.error("使用changePage函数翻页失败", e);
          return false;
        }

        return true;
      } else {
        console.log("未找到任何可用的翻页方式");
        return false;
      }
    },

    scrollToCurrentChar() {
      const span = this.charElements[this.currentIndex];
      if (!span) return;

      const rect = span.getBoundingClientRect();
      const contentContainer = document.querySelector('.page-content');

      const lineHeight = parseInt(window.getComputedStyle(span).lineHeight) || 
                       Math.max(20, rect.height * 1.5);

      const isContainerScrollable = contentContainer && 
        contentContainer.scrollHeight > contentContainer.clientHeight &&
        ['auto', 'scroll', 'overlay'].includes(window.getComputedStyle(contentContainer).overflowY);

      if (isContainerScrollable) {
        const containerRect = contentContainer.getBoundingClientRect();
        const relativeTop = rect.top - containerRect.top + contentContainer.scrollTop;

        const visibleTop = contentContainer.scrollTop;
        const visibleBottom = visibleTop + contentContainer.clientHeight;

        if (relativeTop < visibleTop + lineHeight || relativeTop > visibleBottom - lineHeight) {
          contentContainer.scrollBy({
            top: lineHeight * 2 * (relativeTop > visibleBottom - lineHeight ? 1 : -1),
            behavior: 'smooth'
          });
        }
      } else {
        const viewportTop = window.scrollY;
        const viewportBottom = viewportTop + window.innerHeight;
        const absoluteTop = rect.top + window.scrollY;

        if (absoluteTop < viewportTop + lineHeight || absoluteTop > viewportBottom - lineHeight) {
          window.scrollBy({
            top: lineHeight * 2 * (absoluteTop > viewportBottom - lineHeight ? 1 : -1),
            behavior: 'smooth'
          });
        }
      }
    },

    addSpeedSlider() {
      let slider = document.getElementById('rhythm-speed-slider');
      if (slider) slider.remove();

      const container = document.createElement('div');
      container.id = 'rhythm-speed-slider';
      container.style.position = 'fixed';
      container.style.top = '70px';
      container.style.right = '20px';
      container.style.backgroundColor = 'white';
      container.style.padding = '10px';
      container.style.borderRadius = '5px';
      container.style.boxShadow = '0 2px 5px rgba(0,0,0,0.2)';
      container.style.zIndex = '10000';
      container.style.display = 'flex';
      container.style.alignItems = 'center';
      container.style.maxWidth = '300px';

      const label = document.createElement('label');
      label.textContent = '速度: ';
      label.style.marginRight = '8px';
      container.appendChild(label);

      const input = document.createElement('input');
      input.type = 'range';
      input.min = '50';
      input.max = '1000';
      input.step = '50';
      input.value = this.speed;
      input.style.width = '120px';

      const valueDisplay = document.createElement('span');
      valueDisplay.textContent = `${this.speed}ms`;
      valueDisplay.style.marginLeft = '8px';
      valueDisplay.style.minWidth = '60px';

      const self = this;
      input.addEventListener('input', function() {
        const newSpeed = parseInt(this.value);
        self.speed = newSpeed;
        valueDisplay.textContent = `${newSpeed}ms`;

        if (self.intervalId) {
          clearInterval(self.intervalId);
          self.intervalId = setInterval(() => self.moveToNextChar(), newSpeed);
        }
      });

      container.appendChild(input);
      container.appendChild(valueDisplay);
      document.body.appendChild(container);
    },

    restoreOriginalText() {
      const charSpans = document.querySelectorAll('.rhythm-char');
      if (charSpans.length === 0) return;

      const parentNodeMap = new Map();

      charSpans.forEach(span => {
        const parent = span.parentNode;
        if (!parent) return;

        if (!parentNodeMap.has(parent)) {
          parentNodeMap.set(parent, []);
        }
        parentNodeMap.get(parent).push(span);
      });

      parentNodeMap.forEach((spans, parent) => {
        if (spans.length === 0) return;

        spans.sort((a, b) => {
          const indexA = parseInt(a.dataset.rhythmIndex || '0');
          const indexB = parseInt(b.dataset.rhythmIndex || '0');
          return indexA - indexB;
        });

        const groups = [];
        if (spans.length > 0) {
          let currentGroup = [spans[0]];

          for (let i = 1; i < spans.length; i++) {
            const prevIndex = parseInt(spans[i-1].dataset.rhythmIndex || '0');
            const currentIndex = parseInt(spans[i].dataset.rhythmIndex || '0');

            if (currentIndex === prevIndex + 1 && spans[i].previousSibling === spans[i-1]) {
              currentGroup.push(spans[i]);
            } else {
              groups.push(currentGroup);
              currentGroup = [spans[i]];
            }
          }

          groups.push(currentGroup);
        }

        groups.forEach(group => {
          if (group.length === 0) return;

          try {
            const text = group.map(span => span.textContent).join('');
            const textNode = document.createTextNode(text);

            if (group[0].parentNode === parent) {
              parent.replaceChild(textNode, group[0]);
              for (let i = 1; i < group.length; i++) {
                if (group[i].parentNode === parent) {
                  parent.removeChild(group[i]);
                }
              }
            }
          } catch (e) {
            console.error("恢复文本时出错:", e);
          }
        });
      });

      this.charElements = [];
    }
  };

  // ===== 重写模式选择函数 =====
  if (typeof window.readingModeChanged === 'function') {
    const originalReadingModeChanged = window.readingModeChanged;

    window.readingModeChanged = function(mode) {
      console.log(`模式选择: ${mode}`);

      originalReadingModeChanged(mode);

      if (mode === 'rhythm' || mode === 'rhythmReading' || 
          (typeof mode === 'string' && (mode.includes('rhythm') || mode.includes('节奏')))) {
        console.log("选择了节奏阅读模式");
        rhythmReader.start();
      } else {
        if (isActive()) {
          rhythmReader.stop(true);
        }
      }
    };
  }

  // ===== 在页面加载时检查并自动恢复 =====
  function checkAndRestore() {
    if (isActive()) {
      console.log("页面加载时检测到节奏阅读模式已激活，准备恢复");
      setTimeout(() => rhythmReader.start(), 300);
    }
  }

  // DOMContentLoaded
  if (document.readyState === 'loading') {
    document.addEventListener('DOMContentLoaded', checkAndRestore);
  } else {
    setTimeout(checkAndRestore, 0);
  }

  // window.load
  window.addEventListener('load', checkAndRestore);

  // 多次延迟尝试
  [500, 1000, 1500].forEach(delay => {
    setTimeout(() => {
      if (isActive() && !rhythmReader.intervalId) {
        console.log(`延迟${delay}ms后尝试恢复节奏阅读`);
        rhythmReader.start();
      }
    }, delay);
  });

  // ===== 暴露到全局以便调试 =====
  window.rhythmReader = rhythmReader;

  console.log("节奏阅读模式初始化完成，当前状态:", isActive());
})();

const App = () => {
  // 状态定义
  const [pages, setPages] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const [totalPages, setTotalPages] = useState(0);
  const [fileName, setFileName] = useState('');
  const [loading, setLoading] = useState(false);
  const fileRef = useRef(null);
  const [file, setFile] = useState(null);
  const [pdfInstance, setPdfInstance] = useState(null);
  const [extractedTextPages, setExtractedTextPages] = useState([]);
  const [readingMode, setReadingMode] = useState('normal');
  const [posMode, setPosMode] = useState('none');
  const [markdownContent, setMarkdownContent] = useState('');
  const readerContainerRef = useRef(null);
  const autoReadGuideRef = useRef(null);
  const autoReadIntervalRef = useRef(null);
  const [content, setContent] = useState('');
  const [pdfDocument, setPdfDocument] = useState(null);

  // 新增自动阅读器状态
  const [autoReaderState, setAutoReaderState] = useState({
    currentIndex: 0,
    charPositions: [],
    intervalId: null,
    speed: 300,
    pageContainer: null,
    isUserScrolling: false,
    scrollTimeout: null,
    contentLoaded: false
  });

  // 初始化PDF.js
  useEffect(() => {
    pdfjsLib.GlobalWorkerOptions.workerSrc = `https://cdnjs.cloudflare.com/ajax/libs/pdf.js/${pdfjsLib.version}/pdf.worker.min.js`;

    return () => {
      // 清理自动阅读定时器
      if (autoReadIntervalRef.current) {
        clearInterval(autoReadIntervalRef.current);
      }
    };
  }, []);

  // 当阅读模式改变时应用效果
  useEffect(() => {
    if (readingMode === 'follow-mouse') {
      // 确保DOM更新完成后初始化鼠标跟随功能
      setTimeout(() => {
        initializeMouseFollow();
      }, 50);
    }
  }, [readingMode, markdownContent]);

  // 修改PDF处理函数，确保内容正确提取和显示
  const handlePdfFile = async (file) => {
    try {
      const fileURL = URL.createObjectURL(file);
      const loadingTask = pdfjsLib.getDocument(fileURL);
      const pdf = await loadingTask.promise;

      setPdfDocument(pdf);
      setTotalPages(pdf.numPages);
      setCurrentPage(1);

      // 加载第一页
      await extractAndProcessPage(pdf, 1);

      // 添加调试信息
      console.log('PDF加载成功，页数:', pdf.numPages);
    } catch (error) {
      console.error('加载PDF时出错:', error);
      alert('PDF加载失败，请尝试其他文件');
    }
  };

  // 提取和处理PDF页面内容的函数 - 区分目录和正文页面
  const extractAndProcessPage = async (pdf, pageNum) => {
    try {
      const page = await pdf.getPage(pageNum);
      const textContent = await page.getTextContent();
      const viewport = page.getViewport({ scale: 1.0 });

      // 判断当前页面是否为目录页
      const isTableOfContents = await detectIfTocPage(textContent);

      if (isTableOfContents) {
        // 处理目录页面 - 保持原布局，仅调整字间距
        await processTocPage(textContent, viewport);
      } else {
        // 处理正文页面 - 以句号为标准划分段落
        await processContentPage(page, readingMode);
      }

      // 在内容处理完成后，如果是鼠标跟随模式，添加监听器
      if (readingMode === 'follow-mouse') {
        setTimeout(() => {
          addMouseFollowListeners();
        }, 50);
      }

      return true; // 返回成功标志
    } catch (error) {
      console.error('处理PDF页面时出错:', error);
      setContent('<div>无法处理PDF内容</div>');
      return false;
    }
  };

  // 检测是否为目录页
  const detectIfTocPage = async (textContent) => {
    const textItems = textContent.items;
    let fullText = textItems.map(item => item.str).join('');

    // 检查特征: 
    // 1. 页面上包含"目录"字样
    // 2. 行尾通常有页码和前导符（...）
    // 3. 短行居多，且多有规律的缩进
    const hasTocKeywords = /目\s*录|contents|index/i.test(fullText);
    const hasPageNumberPatterns = /\.{2,}|…{1,}\s*\d+/.test(fullText);

    // 检查行的特征
    let shortLinesCount = 0;
    let linesWithNumbersCount = 0;

    // 按Y坐标分组
    const lineMap = new Map();
    textItems.forEach(item => {
      const yKey = Math.round(item.transform[5]);
      if (!lineMap.has(yKey)) {
        lineMap.set(yKey, []);
      }
      lineMap.get(yKey).push(item);
    });

    const lines = [...lineMap.values()];

    lines.forEach(line => {
