  // 应用舒适3D效果
  const applyComfortable3dEffect = text => {
    // 分段处理
    const paragraphs = text.split(/(?<=\.)\s+/);
    let html = '<div class="pdf-content">';
    paragraphs.forEach(paragraph => {
      if (paragraph.trim() === '') return;

