          const effectTypes = ['emboss-3d-text', 'float-3d-text', 'gradient-3d-text'];
          const effectClass = effectTypes[Math.floor(Math.random() * effectTypes.length)];
          processedParagraph += `<span class="${effectClass}">${word}</span>`;
