        }
        lastColor = color;
        result += `<span class="highlight-word ${color}">${word}</span>`;
      } else {
        result += word;
      }
    });
    return result;
  };

  // 处理内容页面 - 按句子划分段落
  const processContentPage = async (page, mode) => {
    try {
      const textContent = await page.getTextContent();
