import React, { useState, useRef, useEffect } from 'react';
import * as pdfjsLib from 'pdfjs-dist';
import * as pdfjsViewer from 'pdfjs-dist/web/pdf_viewer';
import { marked } from 'marked'; // 需要安装: npm install marked
import './styles/Reader.css';
import { pdfjs } from 'pdfjs-dist';

// 设置 PDF.js worker
pdfjsLib.GlobalWorkerOptions.workerSrc = `//cdnjs.cloudflare.com/ajax/libs/pdf.js/${pdfjsLib.version}/pdf.worker.min.js`;

// 全局变量定义
window.currentPage = 1;
window.totalPages = 0;
window.changePage = null;

// 添加全局事件跟踪对象
window.eventTracker = window.eventTracker || {
  mouseFollowHandlers: []
};

// 自动阅读器全局对象
window.autoReader = {
  charPositions: [],
  intervalId: null,
  currentIndex: 0,
  speed: 300,
  pageContainer: null,
  isScrolling: false,
  currentPage: 1,
  isPageChanging: false,
  cleanup: null
};

// 添加新的节奏阅读模式代码
// 全局变量，确保在页面切换后仍然保持状态
window.rhythmReaderGlobal = window.rhythmReaderGlobal || {
  active: false,
  speed: 300
};

// 节奏阅读模式 - 选择即生效版
(function() {
  console.log("节奏阅读模式初始化中...");

  // 停止任何现有实例
  if (window.rhythmReader && window.rhythmReader.stop) {
    window.rhythmReader.stop();
  }

  // 添加小型日志面板（不含按钮，仅用于调试）
  const createLogPanel = function() {
    const panel = document.createElement('div');
    panel.id = 'rhythm-log-panel';
    panel.style.cssText = `
      position: fixed;
      bottom: 10px;
      right: 10px;
      background-color: rgba(0, 0, 0, 0.5);
      color: white;
      padding: 5px;
      border-radius: 3px;
      z-index: 10001;
      font-size: 10px;
      max-width: 200px;
      max-height: 100px;
      overflow: auto;
      opacity: 0.7;
    `;
    document.body.appendChild(panel);
    return panel;
  };

  // 日志函数
  const logMessage = function(message) {
    console.log("[节奏阅读] " + message);
    const panel = document.getElementById('rhythm-log-panel') || createLogPanel();

    const entry = document.createElement('div');
    entry.textContent = message;
    entry.style.marginBottom = '2px';

    // 加入新日志
    if (panel.firstChild) {
      panel.insertBefore(entry, panel.firstChild);
    } else {
      panel.appendChild(entry);
    }

    // 限制日志条数
    while (panel.childNodes.length > 5) {
      panel.removeChild(panel.lastChild);
    }
  };

  // 节奏阅读功能
  const rhythmReader = {
    active: false,
    intervalId: null,
    speed: 300,
    currentIndex: 0,
    readIndices: new Set(),
    charElements: [],

    // 启动节奏阅读
    start: function() {
      // 已经启动则不重复操作
      if (this.active) return;

      logMessage("启动节奏阅读模式");

      // 设置为活动状态
      this.active = true;

      // 初始化文本
      if (!this.initializeTextNodes()) {
        logMessage("错误：无法初始化文本节点");
        this.active = false;
        return;
      }

      // 重置阅读位置
      this.currentIndex = 0;
      this.readIndices.clear();

      // 添加速度滑块
      this.addSpeedSlider();

      // 更新颜色
      this.updateTextColors();

      // 滚动到开始位置
      this.scrollToCurrentChar();

      // 设置阅读间隔
      this.intervalId = setInterval(() => this.moveToNextChar(), this.speed);

      logMessage(`已处理${this.charElements.length}个字符`);
    },

    // 停止节奏阅读
    stop: function() {
      if (!this.active) return;

      logMessage("停止节奏阅读模式");

      if (this.intervalId) {
        clearInterval(this.intervalId);
        this.intervalId = null;
      }

      this.active = false;

      // 恢复原始文本
      this.restoreOriginalText();

      // 移除速度滑块
      const slider = document.getElementById('rhythm-speed-slider');
      if (slider) slider.remove();
    },

    // 简化版初始化文本节点
    initializeTextNodes: function() {
      // 重置字符元素数组
      this.charElements = [];

      // 找到所有文本节点
      const textNodes = [];

      // 使用TreeWalker查找所有可见的文本节点
      const walker = document.createTreeWalker(
        document.body,
        NodeFilter.SHOW_TEXT,
        {
          acceptNode: function(node) {
            // 排除空文本
            if (node.nodeValue.trim() === '') return NodeFilter.FILTER_REJECT;

            const parent = node.parentElement;
            if (!parent) return NodeFilter.FILTER_REJECT;

            // 排除不可见的元素
            const style = window.getComputedStyle(parent);
            if (style.display === 'none' || style.visibility === 'hidden') {
              return NodeFilter.FILTER_REJECT;
            }

            // 排除UI元素
            const tag = parent.tagName.toLowerCase();
            if (['button', 'input', 'select', 'option', 'textarea'].includes(tag)) {
              return NodeFilter.FILTER_REJECT;
            }

            // 排除导航元素
            if (['nav', 'header', 'footer'].includes(tag)) {
              return NodeFilter.FILTER_REJECT;
            }

            // 排除特定ID的元素
            if (parent.id === 'rhythm-log-panel' || parent.id === 'rhythm-speed-slider') {
              return NodeFilter.FILTER_REJECT;
            }

            // 接受长度超过5个字符的文本节点
            if (node.nodeValue.trim().length > 5) {
              return NodeFilter.FILTER_ACCEPT;
            }

            // 排除常见UI元素
            if (/nav|button|next|prev|page/i.test(parent.className) || 
                /nav|button|next|prev|page/i.test(parent.id)) {
              return NodeFilter.FILTER_REJECT;
            }

            return NodeFilter.FILTER_ACCEPT;
          }
        }
      );

      while (walker.nextNode()) {
        textNodes.push(walker.currentNode);
      }

      if (textNodes.length === 0) {
        return false;
      }

      // 为每个文本节点创建字符span
      textNodes.forEach(node => {
        const text = node.nodeValue;
        if (!text || text.trim() === '') return;

        // 创建文档片段来存放字符span
        const fragment = document.createDocumentFragment();

        // 为每个字符创建span
        for (let i = 0; i < text.length; i++) {
          const char = text[i];
          const span = document.createElement('span');
          span.textContent = char;
          span.className = 'rhythm-char';
          span.dataset.rhythmIndex = this.charElements.length;

          // 如果是空白字符，添加一个标志
          if (char.trim() === '') {
            span.dataset.whitespace = 'true';
          }

          // 设置样式
          span.style.cssText = `
            display: inline;
            margin: 0;
            padding: 0;
            border: none;
            background-color: transparent;
          `;

          fragment.appendChild(span);
          this.charElements.push(span);
        }

        // 替换原始节点
        if (node.parentNode) {
          node.parentNode.replaceChild(fragment, node);
        }
      });

      return this.charElements.length > 0;
    },

    // 更新文本颜色
    updateTextColors: function() {
      if (this.charElements.length === 0) return;

      this.charElements.forEach((span, index) => {
        // 确定颜色
        let color;
        let fontWeight = 'inherit';

        if (index === this.currentIndex) {
          color = '#00aa00'; // 当前阅读的字 - 绿色
          fontWeight = 'bold';
        } else if (index === this.currentIndex + 1 || index === this.currentIndex + 2) {
          color = '#aaaaaa'; // 接下来两个字 - 淡灰色
        } else if (this.readIndices.has(index)) {
          color = '#ffffff'; // 已读字符 - 白色（表示已读）
        } else {
          color = 'rgba(160, 160, 160, 0.25)'; // 未读字符 - 非常淡的灰色
        }

        span.style.color = color;
        span.style.fontWeight = fontWeight;
      });
    },

    // 移动到下一个字符
    moveToNextChar: function() {
      if (!this.active || this.charElements.length === 0) return;

      // 将当前字符标记为已读
      this.readIndices.add(this.currentIndex);

      // 移动到下一个非空白字符
      do {
        this.currentIndex++;

        // 检查是否到达末尾
        if (this.currentIndex >= this.charElements.length) {
          logMessage("已到达页尾");
          this.stop();
          return;
        }
      } while (
        this.currentIndex < this.charElements.length && 
        this.charElements[this.currentIndex] && 
        this.charElements[this.currentIndex].dataset.whitespace === 'true'
      );

      // 更新颜色
      this.updateTextColors();

      // 滚动到当前字符
      this.scrollToCurrentChar();
    },

    // 滚动到当前字符
    scrollToCurrentChar: function() {
      const span = this.charElements[this.currentIndex];
      if (!span) return;

      const rect = span.getBoundingClientRect();

      // 窗口滚动
      const viewportHeight = window.innerHeight;
      const idealPosition = viewportHeight * 0.4; // 屏幕40%的位置

      window.scrollTo({
        top: window.scrollY + rect.top - idealPosition,
        behavior: 'smooth'
      });
    },

    // 添加速度滑块
    addSpeedSlider: function() {
      let slider = document.getElementById('rhythm-speed-slider');
      if (slider) slider.remove();

      const container = document.createElement('div');
      container.id = 'rhythm-speed-slider';
      container.style.cssText = `
        position: fixed;
        top: 20px;
        right: 20px;
        background-color: rgba(0, 0, 0, 0.7);
        padding: 10px;
        border-radius: 5px;
        box-shadow: 0 2px 5px rgba(0,0,0,0.2);
        z-index: 10000;
        display: flex;
        align-items: center;
        max-width: 300px;
        color: white;
      `;

      const label = document.createElement('label');
      label.textContent = '速度: ';
      label.style.marginRight = '8px';
      container.appendChild(label);

      const input = document.createElement('input');
      input.type = 'range';
      input.min = '50';
      input.max = '800';
      input.step = '50';
      input.value = this.speed;
      input.style.width = '120px';

      const valueDisplay = document.createElement('span');
      valueDisplay.textContent = `${this.speed}ms`;
      valueDisplay.style.marginLeft = '8px';
      valueDisplay.style.minWidth = '60px';

      const self = this;
      input.addEventListener('input', function() {
        const newSpeed = parseInt(this.value);
        self.speed = newSpeed;
        valueDisplay.textContent = `${newSpeed}ms`;

        if (self.intervalId) {
          clearInterval(self.intervalId);
          self.intervalId = setInterval(() => self.moveToNextChar(), newSpeed);
        }
      });

      container.appendChild(input);
      container.appendChild(valueDisplay);
      document.body.appendChild(container);
    },

    // 恢复原始文本
    restoreOriginalText: function() {
      const charSpans = document.querySelectorAll('.rhythm-char');
      if (charSpans.length === 0) return;

      // 按父节点分组
      const parentNodeMap = new Map();

      charSpans.forEach(span => {
        const parent = span.parentNode;
        if (!parent) return;

        if (!parentNodeMap.has(parent)) {
          parentNodeMap.set(parent, []);
        }
        parentNodeMap.get(parent).push(span);
      });

      // 对每个父节点，将连续的字符span合并回文本节点
      parentNodeMap.forEach((spans, parent) => {
        if (spans.length === 0) return;

        // 排序spans确保顺序正确
        spans.sort((a, b) => {
          const indexA = parseInt(a.dataset.rhythmIndex || '0');
          const indexB = parseInt(b.dataset.rhythmIndex || '0');
          return indexA - indexB;
        });

        // 将所有span的文本合并为一个文本节点
        try {
          const text = spans.map(span => span.textContent).join('');
          const textNode = document.createTextNode(text);

          // 替换第一个span，然后移除其余的
          parent.replaceChild(textNode, spans[0]);
          for (let i = 1; i < spans.length; i++) {
            if (spans[i].parentNode === parent) {
              parent.removeChild(spans[i]);
            }
          }
        } catch (e) {
          console.error("恢复文本时出错:", e);
        }
      });

      // 清除字符元素数组
      this.charElements = [];
    }
  };

  // ===== 关键部分：增强模式选择检测能力 =====

  // 重写模式切换处理函数
  const originalReadingModeChanged = window.readingModeChanged;

  window.readingModeChanged = function(mode) {
    logMessage("模式变更: " + mode);

    // 停止已有的节奏阅读
    rhythmReader.stop();

    // 调用原始函数
    if (typeof originalReadingModeChanged === 'function') {
      try {
        originalReadingModeChanged(mode);
      } catch(e) {
        console.error("原始模式切换函数出错:", e);
      }
    }

    // 只有当选择了节奏阅读模式时才启动
    if (mode === 'rhythm' || mode === 'rhythmReading') {
      // 立即启动
      rhythmReader.start();
    }
  };

  // 监听模式选择下拉菜单的变化
  const setupModeSelectionListeners = function() {
    // 监听各种可能的选择器元素
    const selectors = [
      'select.reading-mode-selector', 
      '.reading-mode-dropdown',
      '#reading-mode-select',
      'select[name="readingMode"]',
      '[data-role="reading-mode-selector"]'
    ];

    for (const selector of selectors) {
      const element = document.querySelector(selector);
      if (!element) continue;

      logMessage("找到模式选择元素: " + selector);

      // 添加更多事件监听
      element.addEventListener('change', function() {
        const value = this.value || '';
        if (value.includes('rhythm')) {
          logMessage("选择了节奏阅读模式");
          rhythmReader.start();
        } else {
          rhythmReader.stop();
        }
      });

      // 也监听点击事件，以防是自定义UI
      element.addEventListener('click', function(e) {
        // 延迟检查，等待UI更新
        setTimeout(function() {
          // 检查当前选择的模式
          if (element.value && element.value.includes('rhythm')) {
            logMessage("点击后检测到节奏阅读模式");
            rhythmReader.start();
          }
        }, 50);
      });
    }

    // 监听所有可能的模式按钮
    const buttons = document.querySelectorAll('button, [role="button"], .mode-option, .reading-mode-option');
    buttons.forEach(button => {
      const id = button.id || '';
      const className = button.className || '';
      const text = button.textContent || '';

      if (id.includes('rhythm') || className.includes('rhythm') || 
          text.toLowerCase().includes('rhythm') || text.includes('节奏')) {

        logMessage("找到节奏阅读模式按钮");

        button.addEventListener('click', function() {
          logMessage("点击了节奏阅读模式按钮");
          rhythmReader.start();
        });
      }
    });
  };

  // 监听文档变化，检测模式切换
  const setupDocumentObserver = function() {
    // 使用MutationObserver监听DOM变化
    const observer = new MutationObserver(function(mutations) {
      // 检查是否有激活的节奏阅读模式元素
      const activeElements = document.querySelectorAll('.active, .selected, [data-active="true"], .reading-mode-active');

      for (const el of activeElements) {
        const id = el.id || '';
        const className = el.className || '';
        const dataMode = el.dataset.mode || '';
        const text = el.textContent || '';

        if (id.includes('rhythm') || className.includes('rhythm') || 
            dataMode.includes('rhythm') || text.toLowerCase().includes('rhythm') || 
            text.includes('节奏')) {

          if (!rhythmReader.active) {
            logMessage("检测到节奏阅读模式被激活");
            rhythmReader.start();
          }
          return;
        }
      }

      // 如果检测到用户正在打开模式选择器，准备捕获选择
      const openDropdowns = document.querySelectorAll('.dropdown.open, .dropdown-menu:not(.hidden), .menu.active');
      if (openDropdowns.length > 0) {
        setupModeSelectionListeners();
      }
    });

    // 观察整个文档变化
    observer.observe(document.body, {
      childList: true,
      subtree: true,
      attributes: true,
      attributeFilter: ['class', 'data-active']
    });
  };

  // 将对象暴露到全局
  window.rhythmReader = rhythmReader;

  // 立即设置各种监听器以确保捕获模式选择
  setupModeSelectionListeners();
  setupDocumentObserver();

  // 初始检查当前是否已经是节奏阅读模式
  setTimeout(function() {
    const activeElements = document.querySelectorAll('.active, .selected, [data-active="true"], .reading-mode-active');

    for (const el of activeElements) {
      const mode = el.dataset.mode || el.id || el.className || '';

      if (mode.includes('rhythm')) {
        logMessage("检测到当前已是节奏阅读模式");
        rhythmReader.start();
        break;
      }
    }

    // 也检查所有下拉菜单和选择器的当前值
    const selectors = document.querySelectorAll('select');
    for (const select of selectors) {
      if (select.value && select.value.includes('rhythm')) {
        logMessage("检测到节奏阅读模式已被选中");
        rhythmReader.start();
        break;
      }
    }
  }, 300);

  logMessage("节奏阅读模式已初始化完成");
})();

