          this.currentIndex++;
          this.displayCurrentSentence();
          progress.textContent = `进度：${this.currentIndex + 1} / ${this.sentences.length}`;
        }
      },

      // 清理资源
      cleanup() {
        if (this.interval) {
          clearInterval(this.interval);
          this.interval = null;
        }
        this.isPlaying = false;
        this.currentIndex = 0;
      }
    };

