import React, { useState, useRef, useEffect } from 'react';
import * as pdfjsLib from 'pdfjs-dist';
import * as pdfjsViewer from 'pdfjs-dist/web/pdf_viewer';
import { marked } from 'marked'; // 需要安装: npm install marked
import './styles/Reader.css';
import { pdfjs } from 'pdfjs-dist';

// 设置 PDF.js worker
pdfjsLib.GlobalWorkerOptions.workerSrc = `//cdnjs.cloudflare.com/ajax/libs/pdf.js/${pdfjsLib.version}/pdf.worker.min.js`;

// 修改自动阅读器对象
let autoReader = {
  currentIndex: 0,
  charPositions: [],
  intervalId: null,
  speed: 300,
  pageContainer: null,
  isScrolling: false,
  currentPage: 1,
  scrollListener: null
};

// 模拟NLP词性标注功能（实际应用中建议使用专业NLP库如nlp.js或调用API）
const tagPartsOfSpeech = (text) => {
  // 定义所有标点符号
  const punctuationMarks = [
    '，', '。', '！', '？', '；', '：', '（', '）', '【', '】', '《', '》', '〈', '〉', '…', '—', '～', '·',
    ',', '.', '!', '?', ';', ':', '(', ')', '[', ']', '{', '}', '-', '...', '..'
  ];

  // 将文本按标点符号分割，保留标点符号
  const segments = text.split(new RegExp(`([${punctuationMarks.map(mark => '\\' + mark).join('')}])`));
  let result = '';
  let colorIndex = 0; // 0: 红色, 1: 蓝色, 2: 黑色
  let currentText = '';

  segments.forEach((segment, index) => {
    // 跳过空字符串和纯空格
    if (!segment || /^\s+$/.test(segment)) {
      result += segment;
      return;
    }

    // 检查是否是标点符号
    const isPunctuation = punctuationMarks.includes(segment);

    // 根据索引选择颜色
    let color;
    switch (colorIndex) {
      case 0:
        color = 'red';
        break;
      case 1:
        color = 'blue';
        break;
      case 2:
        color = 'black';
        break;
      default:
        color = 'black';
    }

    // 将当前文本和标点符号放在同一个颜色块中
    if (isPunctuation) {
      // 如果有累积的文本，先添加文本
      if (currentText) {
        result += `<span class="highlight-word ${color}">${currentText}</span>`;
        currentText = '';
      }
      // 添加标点符号
      result += `<span class="highlight-word ${color}">${segment}</span>`;
      // 切换颜色
      colorIndex = (colorIndex + 1) % 3;
    } else {
      // 累积文本
      currentText += segment;
    }
  });

  // 处理最后可能剩余的文本
  if (currentText) {
    const color = colorIndex === 0 ? 'red' : colorIndex === 1 ? 'blue' : 'black';
    result += `<span class="highlight-word ${color}">${currentText}</span>`;
  }

  return result;
};

const App = () => {
  // 状态定义
  const [pages, setPages] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const [totalPages, setTotalPages] = useState(0);
  const [fileName, setFileName] = useState('');
  const [loading, setLoading] = useState(false);
  const fileRef = useRef(null);
  const [file, setFile] = useState(null);
  const [pdfInstance, setPdfInstance] = useState(null);
  const [extractedTextPages, setExtractedTextPages] = useState([]);
  const [readingMode, setReadingMode] = useState('normal');
  const [posMode, setPosMode] = useState('none');
  const [markdownContent, setMarkdownContent] = useState('');
  const readerContainerRef = useRef(null);
  const autoReadGuideRef = useRef(null);
  const autoReadIntervalRef = useRef(null);
  const [content, setContent] = useState('');
  const [pdfDocument, setPdfDocument] = useState(null);

  // 新增自动阅读器状态
  const [autoReaderState, setAutoReaderState] = useState({
    currentIndex: 0,
    charPositions: [],
    intervalId: null,
    speed: 300,
    pageContainer: null,
    isUserScrolling: false,
    scrollTimeout: null,
    contentLoaded: false
  });

  // 初始化PDF.js
  useEffect(() => {
    pdfjsLib.GlobalWorkerOptions.workerSrc = `https://cdnjs.cloudflare.com/ajax/libs/pdf.js/${pdfjsLib.version}/pdf.worker.min.js`;

    return () => {
      // 清理自动阅读定时器
      if (autoReadIntervalRef.current) {
        clearInterval(autoReadIntervalRef.current);
      }
    };
  }, []);

  // 当阅读模式改变时应用效果
  useEffect(() => {
    if (readingMode === 'follow-mouse') {
      // 确保DOM更新完成后初始化鼠标跟随功能
      setTimeout(() => {
        initializeMouseFollow();
      }, 50);
    }
  }, [readingMode, markdownContent]);

  // 修改PDF处理函数，确保内容正确提取和显示
  const handlePdfFile = async (file) => {
    try {
      const fileURL = URL.createObjectURL(file);
      const loadingTask = pdfjsLib.getDocument(fileURL);
      const pdf = await loadingTask.promise;

      setPdfDocument(pdf);
      setTotalPages(pdf.numPages);
      setCurrentPage(1);

      // 加载第一页
      await extractAndProcessPage(pdf, 1);

      // 添加调试信息
      console.log('PDF加载成功，页数:', pdf.numPages);
    } catch (error) {
      console.error('加载PDF时出错:', error);
      alert('PDF加载失败，请尝试其他文件');
    }
  };

  // 提取和处理PDF页面内容的函数 - 区分目录和正文页面
  const extractAndProcessPage = async (pdf, pageNum) => {
    try {
      const page = await pdf.getPage(pageNum);
      const textContent = await page.getTextContent();
      const viewport = page.getViewport({ scale: 1.0 });

      // 判断当前页面是否为目录页
      const isTableOfContents = await detectIfTocPage(textContent);

      if (isTableOfContents) {
        // 处理目录页面 - 保持原布局，仅调整字间距
        await processTocPage(textContent, viewport);
      } else {
        // 处理正文页面 - 以句号为标准划分段落
        await processContentPage(page, readingMode);
      }

      // 在内容处理完成后，如果是鼠标跟随模式，添加监听器
      if (readingMode === 'follow-mouse') {
        setTimeout(() => {
          addMouseFollowListeners();
        }, 50);
      }

      return true; // 返回成功标志
    } catch (error) {
      console.error('处理PDF页面时出错:', error);
      setContent('<div>无法处理PDF内容</div>');
      return false;
    }
  };

  // 检测是否为目录页
  const detectIfTocPage = async (textContent) => {
    const textItems = textContent.items;
    let fullText = textItems.map(item => item.str).join('');

    // 检查特征: 
    // 1. 页面上包含"目录"字样
    // 2. 行尾通常有页码和前导符（...）
    // 3. 短行居多，且多有规律的缩进
    const hasTocKeywords = /目\s*录|contents|index/i.test(fullText);
    const hasPageNumberPatterns = /\.{2,}|…{1,}\s*\d+/.test(fullText);

    // 检查行的特征
    let shortLinesCount = 0;
    let linesWithNumbersCount = 0;

    // 按Y坐标分组
    const lineMap = new Map();
    textItems.forEach(item => {
      const yKey = Math.round(item.transform[5]);
      if (!lineMap.has(yKey)) {
        lineMap.set(yKey, []);
      }
      lineMap.get(yKey).push(item);
    });

    const lines = [...lineMap.values()];

    lines.forEach(line => {
      const lineText = line.map(item => item.str).join('');
      if (lineText.length < 50) shortLinesCount++;
      if (/\d+$/.test(lineText)) linesWithNumbersCount++;
    });

    const shortLineRatio = shortLinesCount / lines.length;
    const numberLineRatio = linesWithNumbersCount / lines.length;

    return hasTocKeywords || hasPageNumberPatterns || (shortLineRatio > 0.7 && numberLineRatio > 0.3);
  };

  // 处理目录页面 - 严格保持原始布局
  const processTocPage = async (textContent, viewport) => {
    const textItems = textContent.items;

    // 生成HTML，严格保持原始位置和格式
    let htmlContent = '<div class="pdf-toc-page">';

    textItems.forEach(item => {
      // 获取原始位置信息
      const x = item.transform[4];
      const y = viewport.height - item.transform[5]; // 转换Y坐标

      // 获取原始字体信息
      const fontSize = Math.sqrt(item.transform[0] * item.transform[0] + item.transform[1] * item.transform[1]);
      const fontWeight = item.fontName && item.fontName.toLowerCase().includes('bold') ? 'bold' : 'normal';

      // 仅应用字间距，其他完全保持原样
      const spacedText = item.str.split('').join('\u200A');

      // 使用绝对定位和原始样式属性
      htmlContent += `<span style="
        position: absolute; 
        left: ${x}px; 
        top: ${y}px; 
        font-size: ${fontSize}px; 
        font-weight: ${fontWeight};
        letter-spacing: 0.3em;
        white-space: nowrap;
        font-family: inherit;
        color: inherit;
      ">${spacedText}</span>`;
    });

    htmlContent += '</div>';
    setContent(htmlContent);
  };

  // 修改文本处理函数，实现按完整词随机高亮
  const processTextWithHighlight = (text) => {
    // 将文本按空格和标点符号分割成词
    const words = text.split(/([\s.,!?，。！？]+)/);
    let result = '';
    let lastColor = null;

    words.forEach(word => {
      if (!word || /^\s+$/.test(word)) {
        result += word;
        return;
      }

      if (Math.random() < 0.8) {
        let color;
        if (lastColor === 'red') {
          color = 'blue';
        } else if (lastColor === 'blue') {
          color = 'red';
        } else {
          color = Math.random() < 0.5 ? 'red' : 'blue';
        }
        lastColor = color;

        result += `<span class="highlight-word ${color}">${word}</span>`;
      } else {
        result += word;
      }
    });

    return result;
  };

  // 修改processContentPage函数
  const processContentPage = async (page, mode) => {
    try {
      const textContent = await page.getTextContent();
      const textItems = textContent.items;
      let extractedText = textItems.map(item => item.str).join(' ');

      // 移除多余的空格
      extractedText = extractedText.replace(/\s+/g, ' ').trim();

      // 根据阅读模式处理文本
      let processedContent = '';
      switch(mode) {
        case 'normal':
          // 普通模式 - 简单分段显示
          processedContent = formatTextToHtml(extractedText);
          break;
        case 'static':
          // 静态高亮模式 - 应用词性标注和颜色高亮
          processedContent = applyStaticHighlight(extractedText);
          break;
        case 'follow-mouse':
          // 鼠标跟随高亮模式 - 分段落但不预先高亮，由鼠标事件触发高亮
          processedContent = formatTextForMouseFollow(extractedText);
          break;
        default:
          processedContent = formatTextToHtml(extractedText);
      }

      setContent(processedContent);
    } catch (error) {
      console.error('处理内容页面时出错:', error);
      setContent('<div>无法处理PDF内容</div>');
    }
  };

  // 处理TXT文件
  const handleTxtFile = async (file) => {
    try {
      setLoading(true);
      const text = await file.text();
      // 将文本分页处理
      const pageTexts = text.split('\n\n').map(paragraph => [{
        text: paragraph,
        x: 40,
        y: 40,
        fontSize: 16,
        fontFamily: 'serif'
      }]);
      setPages(pageTexts);
      setTotalPages(pageTexts.length);
      setCurrentPage(1);
      setLoading(false);
    } catch (error) {
      console.error('TXT处理错误:', error);
      setLoading(false);
      alert('TXT文件处理失败，请重试');
    }
  };

  // 文件上传处理
  const handleFileUpload = async (e) => {
    const file = e.target.files[0];
    if (!file) return;

    if (file.type === 'application/pdf') {
      await handlePdfFile(file);
    } else if (file.type === 'text/plain') {
      handleTxtFile(file);
    } else {
      alert('请上传PDF或TXT文件');
    }
  };

  // 增强的字符位置获取（绝对位置）
  const getAllCharacterPositions = (container) => {
    const positions = [];
    const walker = document.createTreeWalker(container, NodeFilter.SHOW_TEXT);

    while (walker.nextNode()) {
      const node = walker.currentNode;
      const text = node.textContent;

      if (!text.trim()) continue;

      for (let i = 0; i < text.length; i++) {
        const range = document.createRange();
        range.setStart(node, i);
        range.setEnd(node, i + 1);

        const rect = range.getBoundingClientRect();
        if (rect.width === 0) continue;

        // 计算绝对位置（包含滚动偏移）
        positions.push({
          x: rect.left + window.scrollX + rect.width/2,
          y: rect.top + window.scrollY + rect.height,
          char: text[i],
          page: autoReader.currentPage
        });
      }
    }

    return positions
      .filter(p => p.char.trim() !== '')
      .sort((a, b) => a.y - b.y || a.x - b.x);
  };

  // 创建阅读光标
  const createReaderCursor = () => {
    const cursor = document.createElement('div');
    cursor.id = 'auto-reader-cursor';
    cursor.style.cssText = `
      position: fixed;
      width: 20px;
      height: 20px;
      background: #00ff00;
      border-radius: 50%;
      pointer-events: none;
      transform: translate(-50%, -50%);
      box-shadow: 0 0 15px #00ff00;
      animation: pulse 1s infinite;
      z-index: 9999;
      transition: transform 0.3s cubic-bezier(0.4, 0, 0.2, 1);
    `;
    return cursor;
  };

  // 增强的自动阅读启动
  const startAutoReader = () => {
    stopAutoReader();

    if (autoReader.charPositions.length === 0) {
      handlePageEnd();
      return;
    }

    autoReader.currentIndex = 0;
    autoReader.intervalId = setInterval(() => {
      // 跳过空白字符
      while (autoReader.currentIndex < autoReader.charPositions.length && 
            autoReader.charPositions[autoReader.currentIndex]?.char.trim() === '') {
        autoReader.currentIndex++;
      }

      // 当前页结束处理
      if (autoReader.currentIndex >= autoReader.charPositions.length) {
        handlePageEnd();
        return;
      }

      updateCursorPosition();
      autoReader.currentIndex++;
    }, autoReader.speed);
  };

  // 增强的光标位置更新
  const updateCursorPosition = () => {
    const pos = autoReader.charPositions[autoReader.currentIndex];
    const cursor = document.getElementById('auto-reader-cursor');
    if (!cursor || !pos) return;

    // 直接使用绝对位置
    cursor.style.transform = `translate(${pos.x}px, ${pos.y}px)`;

    // 自动滚动逻辑
    if (!autoReader.isScrolling) {
      const viewportHeight = window.innerHeight;
      const scrollThreshold = viewportHeight * 0.2;
      const targetY = pos.y - viewportHeight * 0.3;

      if (Math.abs(window.scrollY - targetY) > scrollThreshold) {
        window.scrollTo({
          top: targetY,
          behavior: 'smooth'
        });
      }
    }
  };

  // 翻页处理
  const handlePageEnd = async () => {
    if (currentPage < totalPages) {
      await changePage(1);
      autoReader.currentPage = currentPage;
      initializeAutoReader(true); // 强制重新初始化
    } else {
      stopAutoReader();
    }
  };

  // 滚动处理（防抖）
  const handleScroll = () => {
    autoReader.isScrolling = true;
    clearTimeout(autoReader.scrollTimeout);
    autoReader.scrollTimeout = setTimeout(() => {
      autoReader.isScrolling = false;
      // 重新计算位置
      autoReader.charPositions = getAllCharacterPositions(document.querySelector('.page-content'));
      updateCursorPosition(true);
    }, 300);
  };

  // 增强的初始化
  const initializeAutoReader = (force = false) => {
    if (!force && readingMode !== 'auto-reader') return;

    stopAutoReader();

    // 移除旧监听器
    if (autoReader.scrollListener) {
      window.removeEventListener('scroll', autoReader.scrollListener);
    }

    // 获取最新容器
    const container = document.querySelector('.page-content');
    if (!container) return;

    // 收集位置数据
    autoReader.charPositions = getAllCharacterPositions(container);
    console.log('字符位置数量:', autoReader.charPositions.length);

    // 创建光标
    const cursor = createReaderCursor();
    if (!document.getElementById('auto-reader-cursor')) {
      document.body.appendChild(cursor);
    }

    // 添加滚动监听
    autoReader.scrollListener = handleScroll;
    window.addEventListener('scroll', autoReader.scrollListener, { passive: true });

    // 立即开始
    startAutoReader();
  };

  // 停止自动阅读
  const stopAutoReader = () => {
    clearInterval(autoReader.intervalId);
    autoReader.intervalId = null;
    const cursor = document.getElementById('auto-reader-cursor');
    if (cursor) cursor.remove();
  };

  // 调整速度
  const adjustSpeed = (delta) => {
    autoReader.speed = Math.max(100, autoReader.speed + delta);
    setAutoReaderState(prev => ({ ...prev, speed: autoReader.speed }));

    if (autoReader.intervalId) {
      stopAutoReader();
      startAutoReader();
    }
  };

  // 添加自动阅读器样式
  const addAutoReaderStyles = () => {
    const style = document.createElement('style');
    style.textContent = `
      @keyframes pulse {
        0% { transform: translate(-50%, -50%) scale(0.8); opacity: 0.8; }
        50% { transform: translate(-50%, -50%) scale(1.2); opacity: 1; }
        100% { transform: translate(-50%, -50%) scale(0.8); opacity: 0.8; }
      }
      .speed-control {
        position: fixed;
        top: 20px;
        right: 20px;
        background: rgba(0,0,0,0.7);
        padding: 10px;
        border-radius: 8px;
        color: white;
        z-index: 10000;
      }
      .speed-control button {
        margin: 0 5px;
        padding: 5px 10px;
        background: #4CAF50;
        border: none;
        border-radius: 4px;
        color: white;
        cursor: pointer;
      }
    `;
    document.head.appendChild(style);
  };

  // 在组件挂载时添加样式
  useEffect(() => {
    addAutoReaderStyles();
  }, []);

  // 格式化文本为HTML
  const formatTextToHtml = (text) => {
    // 分段
    const paragraphs = text.split(/(?<=。|！|？|\.|\!|\?)\s+/).filter(p => p.trim());

    // 创建HTML内容
    let htmlContent = '<div class="pdf-content">';

    paragraphs.forEach(paragraph => {
      htmlContent += `<p class="pdf-paragraph">${paragraph}</p>`;
    });

    htmlContent += '</div>';
    return htmlContent;
  };

  // 修改后的静态高亮应用函数
  const applyStaticHighlight = (text) => {
    const paragraphs = text.split(/(?<=。|！|？|\.|\!|\?)\s+/).filter(p => p.trim());

    let htmlContent = '<div class="pdf-content">';

    paragraphs.forEach(paragraph => {
      // 新增强制清理逻辑
      const cleanParagraph = paragraph
        .replace(/<span class="text-line">/g, '')
        .replace(/<\/span>/g, '');

      const highlightedText = processTextWithHighlight(cleanParagraph);
      htmlContent += `<p class="pdf-paragraph">${highlightedText}</p>`;
    });

    htmlContent += '</div>';
    return htmlContent;
  };

  // 格式化文本用于鼠标跟随高亮模式
  const formatTextForMouseFollow = (text) => {
    const paragraphs = text.split(/(?<=。|！|？|\.|\!|\?)\s+/).filter(p => p.trim());

    let htmlContent = '<div class="pdf-content mouse-follow-content">';

    paragraphs.forEach(paragraph => {
      // 将段落分割成行
      const lines = paragraph.split('\n').filter(line => line.trim());
      htmlContent += `<p class="pdf-paragraph" data-follow="true" style="position: relative; line-height: 1.5;">`;

      lines.forEach((line, i) => {
        // 每行用span包裹
        htmlContent += `<span class="text-line" style="display: block;">${line}</span>`;
      });

      htmlContent += `</p>`;
    });

    htmlContent += '</div>';
    return htmlContent;
  };

  // 辅助函数：获取鼠标位置下的文本节点
  const getTextNodeAtPosition = (element, x, y) => {
    const nodes = element.childNodes;
    for (let i = 0; i < nodes.length; i++) {
      const node = nodes[i];
      if (node.nodeType === Node.TEXT_NODE) {
        try {
          const range = document.createRange();
          range.selectNodeContents(node);
          const rect = range.getBoundingClientRect();

          if (y >= rect.top && y <= rect.bottom && 
              x >= rect.left && x <= rect.right) {
            return node;
          }
        } catch (error) {
          console.warn('获取文本节点位置时出错:', error);
          continue;
        }
      } else if (node.nodeType === Node.ELEMENT_NODE) {
        const found = getTextNodeAtPosition(node, x, y);
        if (found) return found;
      }
    }
    return null;
  };

  // 处理鼠标移动事件
  const handleMouseMove = (e) => {
    if (readingMode !== 'follow-mouse') return;

    // 清除所有现有高亮
    document.querySelectorAll('.line-highlight').forEach(el => el.remove());

    const mouseTarget = document.elementFromPoint(e.clientX, e.clientY);

    if (mouseTarget && 
       (mouseTarget.classList.contains('pdf-paragraph') || 
        mouseTarget.tagName === 'P' || 
        mouseTarget.tagName === 'SPAN')) {

      const targetParagraph = mouseTarget.closest('.pdf-paragraph');
      if (!targetParagraph) return;

      // 获取段落文本节点
      const textNode = getTextNodeAtPosition(targetParagraph, e.clientX, e.clientY);
      if (!textNode) return;

      try {
        // 获取精确的行位置和尺寸
        const range = document.createRange();
        range.selectNodeContents(textNode);
        const rect = range.getBoundingClientRect();

        // 创建高亮元素
        const lineHighlight = document.createElement('div');
        lineHighlight.className = 'line-highlight mouse-highlight';

        // 计算相对于段落的位置
        const paragraphRect = targetParagraph.getBoundingClientRect();
        const top = rect.top - paragraphRect.top;
        const left = rect.left - paragraphRect.left;

        // 设置高亮样式
        lineHighlight.style.position = 'absolute';
        lineHighlight.style.left = `${left}px`;
        lineHighlight.style.top = `${top}px`;
        lineHighlight.style.width = `${rect.width}px`;
        lineHighlight.style.height = `${rect.height}px`;
        lineHighlight.style.pointerEvents = 'none';
        lineHighlight.style.backgroundColor = 'rgba(255, 165, 0, 0.3)';
        lineHighlight.style.borderRadius = '4px';
        lineHighlight.style.boxShadow = '0 0 8px rgba(255, 165, 0, 0.5)';
        lineHighlight.style.zIndex = '5';
        lineHighlight.style.transition = 'all 0.1s ease';

        // 确保段落有相对定位
        targetParagraph.style.position = 'relative';
        targetParagraph.appendChild(lineHighlight);

        // 计算并滚动到居中位置
        const windowHeight = window.innerHeight;
        const desiredPosition = windowHeight / 2;
        const currentPosition = rect.top;
        const scrollOffset = currentPosition - desiredPosition;

        window.scrollTo({
          top: window.scrollY + scrollOffset,
          behavior: 'smooth'
        });
      } catch (error) {
        console.warn('处理鼠标移动事件时出错:', error);
      }
    }
  };

  // 修改翻页函数
  const changePage = async (delta) => {
    const newPage = currentPage + delta;
    if (newPage >= 1 && newPage <= totalPages && pdfDocument) {
      setCurrentPage(newPage);

      try {
        const page = await pdfDocument.getPage(newPage);
        const textContent = await page.getTextContent();
        const textItems = textContent.items;
        let extractedText = textItems.map(item => item.str).join(' ');
        extractedText = extractedText.replace(/\s+/g, ' ').trim();

        let processedContent = '';
        switch(readingMode) {
          case 'follow-mouse':
            processedContent = formatTextForMouseFollow(extractedText);
            break;
          case 'static':
            processedContent = applyStaticHighlight(extractedText);
            break;
          case 'auto-reader':
            processedContent = formatTextToHtml(extractedText);
            break;
          default:
            processedContent = formatTextToHtml(extractedText);
        }

        const contentArea = document.querySelector('.page-content');
        if (contentArea) {
          contentArea.textContent = '';
          contentArea.innerHTML = processedContent;

          // 根据当前模式重新初始化
          switch(readingMode) {
            case 'follow-mouse':
              initializeMouseFollow();
              break;
            case 'auto-reader':
              // 重置自动阅读器状态并重新初始化
              setAutoReaderState(prev => ({ ...prev, currentIndex: 0 }));
              setTimeout(initializeAutoReader, 50);
              break;
            case 'static':
              void contentArea.offsetHeight;
              break;
          }
        }
      } catch (error) {
        console.error('翻页处理出错:', error);
      }
    }
  };

  // 清除所有高亮
  const clearAllHighlights = () => {
    document.querySelectorAll('.line-highlight').forEach(el => el.remove());
  };

  // 修改后的鼠标跟随初始化函数
  const initializeMouseFollow = () => {
    // 新增延迟补偿
    setTimeout(() => {
      addMouseFollowListeners();

      // 强制触发重排
      const contentArea = document.querySelector('.page-content');
      if (contentArea) {
        void contentArea.offsetHeight;

        // 模拟鼠标移动事件
        const fakeEvent = new MouseEvent('mousemove', {
          clientX: window.innerWidth / 2,
          clientY: window.innerHeight / 2,
          bubbles: true
        });
        contentArea.dispatchEvent(fakeEvent);
      }
    }, 50);
  };

  // 添加鼠标跟随监听器
  const addMouseFollowListeners = () => {
    if (readingMode !== 'follow-mouse') return;

    const contentArea = document.querySelector('.page-content');
    if (!contentArea) return;

    // 清理旧的监听器
    contentArea.removeEventListener('mousemove', handleMouseMove);
    contentArea.removeEventListener('mouseleave', clearAllHighlights);

    // 添加新的监听器
    contentArea.addEventListener('mousemove', handleMouseMove);
    contentArea.addEventListener('mouseleave', clearAllHighlights);

    // 确保所有段落都有正确的样式
    document.querySelectorAll('.pdf-paragraph').forEach(p => {
      p.style.position = 'relative';
      p.style.lineHeight = '1.5';
    });
  };

  // 修改阅读模式切换函数
  const handleReadingModeChange = async (e) => {
    const newMode = e.target.value;

    // 停止自动阅读
    if (readingMode === 'auto-reader') {
      stopAutoReader();
    }

    // 清理旧模式
    cleanupDOM();

    // 更新状态
    setReadingMode(newMode);

    // 应用新模式
    if (pdfDocument && currentPage > 0) {
      try {
        const page = await pdfDocument.getPage(currentPage);
        const textContent = await page.getTextContent();
        const textItems = textContent.items;
        let extractedText = textItems.map(item => item.str).join(' ');
        extractedText = extractedText.replace(/\s+/g, ' ').trim();

        let processedContent = '';
        switch(newMode) {
          case 'follow-mouse':
            processedContent = formatTextForMouseFollow(extractedText);
            break;
          case 'static':
            processedContent = applyStaticHighlight(extractedText);
            break;
          case 'auto-reader':
            processedContent = formatTextToHtml(extractedText);
            break;
          default:
            processedContent = formatTextToHtml(extractedText);
        }

        const contentArea = document.querySelector('.page-content');
        if (contentArea) {
          contentArea.textContent = '';
          contentArea.innerHTML = processedContent;

          // 立即初始化自动阅读器
          if (newMode === 'auto-reader') {
            initializeAutoReader();
          } else {
            setTimeout(() => {
              switch(newMode) {
                case 'follow-mouse':
                  initializeMouseFollow();
                  break;
                case 'static':
                  void contentArea.offsetHeight;
                  break;
              }
            }, 50);
          }
        }
      } catch (error) {
        console.error('应用新阅读模式失败:', error);
      }
    }
  };

  // 清理DOM元素和效果
  const cleanupDOM = () => {
    // 1. 移除所有可能的高亮元素
    const highlights = document.querySelectorAll(
      '.mouse-highlight, .line-highlight, .highlight-word, .reading-line, .text-line'
    );
    highlights.forEach(el => el.remove());

    // 2. 移除事件监听器
    const contentArea = document.querySelector('.page-content');
    if (contentArea) {
      // 克隆内容区域以彻底移除所有事件监听器
      const cleanContent = contentArea.cloneNode(false);
      cleanContent.innerHTML = contentArea.innerHTML;
      contentArea.parentNode.replaceChild(cleanContent, contentArea);
    }

    // 3. 重置段落结构
    const paragraphs = document.querySelectorAll('.pdf-paragraph');
    paragraphs.forEach(paragraph => {
      // 创建新段落并保留基本结构
      const newParagraph = document.createElement('p');
      newParagraph.className = 'pdf-paragraph';
      newParagraph.innerHTML = paragraph.innerHTML
        .replace(/<span class="text-line">/g, '')
        .replace(/<\/span>/g, '');

      // 移除特殊属性
      newParagraph.removeAttribute('data-follow');
      newParagraph.removeAttribute('style');

      if (paragraph.parentNode) {
        paragraph.parentNode.replaceChild(newParagraph, paragraph);
      }
    });

    // 4. 清理自动阅读器
    stopAutoReader();
  };

  return (
    <div className="App">
      <div className="sidebar">
        <div className="logo">
          <h2>ADHD阅读助手</h2>
        </div>
        <label className="upload-btn">
          选择文件
          <input
            type="file"
            accept=".pdf,.txt"
            onChange={handleFileUpload}
            style={{ display: 'none' }}
          />
        </label>
      </div>
      <div className="main-content">
        <div className="header">
          <h1>ADHD阅读助手</h1>
          <div className="controls">
            <div className="mode-selector">
              <select value={readingMode} onChange={handleReadingModeChange}>
                <option value="normal">普通模式</option>
                <option value="static">静态高亮模式</option>
                <option value="follow-mouse">鼠标跟随高亮</option>
                <option value="auto-reader">自动阅读器</option>
              </select>
            </div>
            <div className="mode-selector">
              <select value={posMode} onChange={(e) => setPosMode(e.target.value)}>
                <option value="none">无词性标注</option>
                <option value="noun">名词标注</option>
                <option value="all">全词性标注</option>
              </select>
            </div>
            {readingMode === 'auto-reader' && (
              <div className="speed-control">
                <button onClick={() => adjustSpeed(-100)}>-</button>
                <span>{autoReaderState.speed}ms</span>
                <button onClick={() => adjustSpeed(100)}>+</button>
              </div>
            )}
          </div>
        </div>
        <div className="page-content">
          {content ? (
            <div dangerouslySetInnerHTML={{ __html: content }} />
          ) : (
            <div className="empty-state">
              <p>请上传PDF或TXT文件开始阅读</p>
            </div>
          )}
        </div>
        {content && (
          <div className="nav-buttons">
            <button className="page-btn prev" onClick={() => changePage(-1)} disabled={currentPage === 1}>
              上一页
            </button>
            <span className="page-info">
              第 {currentPage} 页 / 共 {totalPages} 页
            </span>
            <button className="page-btn next" onClick={() => changePage(1)} disabled={currentPage === totalPages}>
              下一页
            </button>
          </div>
        )}
      </div>
    </div>
  );
};

export default App;
