import React, { useState, useRef, useEffect } from 'react';
import * as pdfjsLib from 'pdfjs-dist';
import * as pdfjsViewer from 'pdfjs-dist/web/pdf_viewer';
import { marked } from 'marked'; // 需要安装: npm install marked
import './styles/Reader.css';
import { pdfjs } from 'pdfjs-dist';

// 设置 PDF.js worker
pdfjsLib.GlobalWorkerOptions.workerSrc = `//cdnjs.cloudflare.com/ajax/libs/pdf.js/${pdfjsLib.version}/pdf.worker.min.js`;

// 全局变量定义
window.currentPage = 1;
window.totalPages = 0;
window.changePage = null;

// 自动阅读器全局对象
window.autoReader = {
  charPositions: [],
  intervalId: null,
  currentIndex: 0,
  speed: 300,
  pageContainer: null,
  isScrolling: false,
  currentPage: 1,
  isPageChanging: false,
  cleanup: null
};

// 添加新的节奏阅读模式代码
// 全局变量，确保在页面切换后仍然保持状态
window.rhythmReaderGlobal = window.rhythmReaderGlobal || {
  active: false,
  speed: 300
};

// 节奏阅读模式 - 确保手动翻页后也能自动恢复
(function() {
  console.log("正在初始化节奏阅读模式...");

  // 确保只初始化一次
  if (window.rhythmReadingInitialized) {
    console.log("节奏阅读模式已经初始化过，跳过");
    return;
  }
  window.rhythmReadingInitialized = true;

  // 改进的存储函数，同时使用session和local storage提高可靠性
  function setStorage(key, value) {
    try {
      sessionStorage.setItem(key, value);
      localStorage.setItem(key, value);
    } catch (e) {
      console.error("无法写入存储", e);
    }
  }

  function getStorage(key) {
    try {
      // 优先使用sessionStorage，这样更适合单次会话
      return sessionStorage.getItem(key) || localStorage.getItem(key);
    } catch (e) {
      console.error("无法读取存储", e);
      return null;
    }
  }

  // 状态变量和功能对象
  const rhythmReader = {
    active: false,
    intervalId: null,
    speed: 300,
    currentIndex: 0,
    readIndices: new Set(),
    charElements: [],
    pageTransitionInProgress: false,

    // 初始化文本节点
    initializeTextNodes: function() {
      console.log("【节奏阅读】初始化文本节点...");

    // 重置字符元素数组
      this.charElements = [];

    // 获取内容容器
      const contentContainer = document.querySelector('.page-content');
      if (!contentContainer) {
        console.error("【节奏阅读】找不到内容容器");
        return false;
      }

    // 查找所有文本节点并处理
    const textNodes = [];
      this.findTextNodes(contentContainer, textNodes);

    if (textNodes.length === 0) {
        console.error("【节奏阅读】未找到文本节点");
        this.showNotification("无法启动节奏阅读：未找到文本内容");
      return false;
    }

      console.log(`【节奏阅读】找到 ${textNodes.length} 个文本节点`);

    // 为每个文本节点创建字符span
    textNodes.forEach(node => {
      const text = node.nodeValue;
      if (!text || text.trim() === '') return;

      // 创建文档片段来存放字符span
      const fragment = document.createDocumentFragment();

      // 为每个字符创建span
      for (let i = 0; i < text.length; i++) {
        const char = text[i];
        const span = document.createElement('span');
        span.textContent = char;
        span.className = 'rhythm-char';
          span.dataset.rhythmIndex = this.charElements.length;

        // 如果是空白字符，添加一个标志
        if (char.trim() === '') {
          span.dataset.whitespace = 'true';
        }

          // 保持原始样式，不设置背景
        span.style.display = 'inline';
        span.style.margin = '0';
        span.style.padding = '0';
        span.style.border = 'none';
        span.style.backgroundColor = 'transparent';

        fragment.appendChild(span);
          this.charElements.push(span);
      }

      // 替换原始节点
      if (node.parentNode) {
        node.parentNode.replaceChild(fragment, node);
      }
    });

      console.log(`【节奏阅读】总共创建了 ${this.charElements.length} 个字符span`);
      return this.charElements.length > 0;
    },

  // 递归查找所有文本节点
    findTextNodes: function(element, textNodes) {
    // 处理子节点
    for (let i = 0; i < element.childNodes.length; i++) {
      const node = element.childNodes[i];

      // 如果是文本节点且有内容
      if (node.nodeType === Node.TEXT_NODE && node.nodeValue.trim() !== '') {
        textNodes.push(node);
      } 
      // 如果是元素节点，递归处理
      else if (node.nodeType === Node.ELEMENT_NODE) {
        // 跳过读者UI元素
        if (node.id === 'rhythm-speed-slider' || 
              node.id === 'rhythm-notification' ||
            node.className === 'rhythm-char') {
          continue;
        }
          this.findTextNodes(node, textNodes);
      }
    }
    },

  // 更新文本颜色
    updateTextColors: function() {
    // 更新所有字符的颜色
      this.charElements.forEach((span, index) => {
      // 确定颜色
      let color;
      let fontWeight = 'inherit';

        if (index === this.currentIndex) {
        color = '#00aa00'; // 当前阅读的字 - 绿色
        fontWeight = 'bold';
        } else if (index === this.currentIndex + 1 || index === this.currentIndex + 2) {
        color = '#aaaaaa'; // 接下来两个字 - 淡灰色
        } else if (this.readIndices.has(index)) {
        color = '#ffffff'; // 已读字符 - 白色（表示已读）
      } else {
        color = 'rgba(160, 160, 160, 0.25)'; // 未读字符 - 非常淡的灰色（可见但不明显）
      }

      span.style.color = color;
      span.style.fontWeight = fontWeight;

      // 确保没有背景
      span.style.backgroundColor = 'transparent';
      span.style.background = 'none';
    });
    },

  // 移动到下一个字符
    moveToNextChar: function() {
    // 将当前字符标记为已读
      this.readIndices.add(this.currentIndex);

    // 移动到下一个非空白字符
    do {
        this.currentIndex++;

      // 检查是否到达末尾
        if (this.currentIndex >= this.charElements.length) {
          console.log("【节奏阅读】已到达页尾，尝试翻页");

          // 尝试点击下一页按钮
          if (!this.clickNextPageButton()) {
            // 如果没有找到下一页按钮，完全停止阅读
            this.stop();
          }
          return;
      }
    } while (
        this.currentIndex < this.charElements.length && 
        this.charElements[this.currentIndex] && 
        this.charElements[this.currentIndex].dataset.whitespace === 'true'
    );

    // 更新颜色
      this.updateTextColors();

    // 滚动到当前字符
      this.scrollToCurrentChar();
    },

  // 滚动到当前字符
    scrollToCurrentChar: function() {
      const span = this.charElements[this.currentIndex];
    if (!span) return;

    const rect = span.getBoundingClientRect();
    const contentContainer = document.querySelector('.page-content');

    // 估计行高
    const lineHeight = parseInt(window.getComputedStyle(span).lineHeight) || 
                       Math.max(20, rect.height * 1.5);

    // 确定滚动容器
    const isContainerScrollable = contentContainer && 
      contentContainer.scrollHeight > contentContainer.clientHeight &&
      ['auto', 'scroll', 'overlay'].includes(window.getComputedStyle(contentContainer).overflowY);

    if (isContainerScrollable) {
      // 容器滚动
      const containerRect = contentContainer.getBoundingClientRect();
      const relativeTop = rect.top - containerRect.top + contentContainer.scrollTop;

      const visibleTop = contentContainer.scrollTop;
      const visibleBottom = visibleTop + contentContainer.clientHeight;

      if (relativeTop < visibleTop + lineHeight || relativeTop > visibleBottom - lineHeight) {
        contentContainer.scrollBy({
          top: lineHeight * 2 * (relativeTop > visibleBottom - lineHeight ? 1 : -1),
          behavior: 'smooth'
        });
      }
    } else {
      // 窗口滚动
      const viewportTop = window.scrollY;
      const viewportBottom = viewportTop + window.innerHeight;
      const absoluteTop = rect.top + window.scrollY;

      if (absoluteTop < viewportTop + lineHeight || absoluteTop > viewportBottom - lineHeight) {
        window.scrollBy({
          top: lineHeight * 2 * (absoluteTop > viewportBottom - lineHeight ? 1 : -1),
          behavior: 'smooth'
        });
      }
    }
    },

    // 添加速度滑块
    addSpeedSlider: function() {
      let slider = document.getElementById('rhythm-speed-slider');
      if (slider) slider.remove();

      const container = document.createElement('div');
      container.id = 'rhythm-speed-slider';

      const containerStyles = {
        position: 'fixed',
        top: '70px',
        right: '20px',
        backgroundColor: 'white',
        padding: '10px',
        borderRadius: '5px',
        boxShadow: '0 2px 5px rgba(0,0,0,0.2)',
        zIndex: '10000',
        display: 'flex',
        alignItems: 'center',
        maxWidth: '300px'
      };

      Object.assign(container.style, containerStyles);

      const label = document.createElement('label');
      label.textContent = '速度: ';
      label.style.marginRight = '8px';
      container.appendChild(label);

      const input = document.createElement('input');
      input.type = 'range';
      input.min = '50';
      input.max = '1000';
      input.step = '50';
      input.value = this.speed;
      input.style.width = '120px';

      const valueDisplay = document.createElement('span');
      valueDisplay.textContent = `${this.speed}ms`;
      valueDisplay.style.marginLeft = '8px';
      valueDisplay.style.minWidth = '60px';

      const self = this;
      input.addEventListener('input', function() {
        const newSpeed = parseInt(this.value);
        self.speed = newSpeed;
        valueDisplay.textContent = `${newSpeed}ms`;

        if (self.intervalId) {
          clearInterval(self.intervalId);
          self.intervalId = setInterval(() => self.moveToNextChar(), newSpeed);
        }
      });

      container.appendChild(input);
      container.appendChild(valueDisplay);
      document.body.appendChild(container);
    },

  // 恢复原始文本
    restoreOriginalText: function() {
      const charSpans = document.querySelectorAll('.rhythm-char');
      const parentNodeMap = new Map();

      charSpans.forEach(span => {
        const parent = span.parentNode;
        if (!parent) return;

        if (!parentNodeMap.has(parent)) {
          parentNodeMap.set(parent, []);
        }
        parentNodeMap.get(parent).push(span);
      });

      parentNodeMap.forEach((spans, parent) => {
        if (spans.length === 0) return;

        spans.sort((a, b) => {
          const indexA = parseInt(a.dataset.rhythmIndex || '0');
          const indexB = parseInt(b.dataset.rhythmIndex || '0');
          return indexA - indexB;
        });

        const groups = [];
        if (spans.length > 0) {
          let currentGroup = [spans[0]];

          for (let i = 1; i < spans.length; i++) {
            const prevIndex = parseInt(spans[i-1].dataset.rhythmIndex || '0');
            const currentIndex = parseInt(spans[i].dataset.rhythmIndex || '0');

            if (currentIndex === prevIndex + 1 && spans[i].previousSibling === spans[i-1]) {
              currentGroup.push(spans[i]);
            } else {
              groups.push(currentGroup);
              currentGroup = [spans[i]];
            }
          }

          groups.push(currentGroup);
        }

        groups.forEach(group => {
          if (group.length === 0) return;

          try {
            const text = group.map(span => span.textContent).join('');
            const textNode = document.createTextNode(text);

            if (group[0].parentNode === parent) {
              parent.replaceChild(textNode, group[0]);
              for (let i = 1; i < group.length; i++) {
                if (group[i].parentNode === parent) {
                  parent.removeChild(group[i]);
                }
              }
            }
          } catch (e) {
            console.error("【节奏阅读】恢复文本时出错:", e);
          }
        });
      });

      this.charElements = [];
    },

  // 启动节奏阅读
    start: function() {
      console.log("【节奏阅读】启动中...");

      if (this.pageTransitionInProgress) {
        console.log("【节奏阅读】页面转换正在进行中，延迟启动");
        setTimeout(() => this.start(), 500);
        return;
      }

      this.stop(false);
      this.active = true;
      setStorage('rhythmReadingActive', 'true');

      if (!this.initializeTextNodes()) {
        console.error("【节奏阅读】无法初始化文本节点");
        return;
      }

      this.currentIndex = 0;
      this.readIndices.clear();
      this.addSpeedSlider();
      this.updateTextColors();
      this.scrollToCurrentChar();
      this.intervalId = setInterval(() => this.moveToNextChar(), this.speed);

      console.log("【节奏阅读】成功启动");
      this.showNotification("节奏阅读模式已启动");
      this.hijackPageTurningFunctions();
      this.setupPageNavigationListeners();
    },

    // 停止节奏阅读
    stop: function(showNotification = true) {
      if (this.intervalId) {
        clearInterval(this.intervalId);
        this.intervalId = null;
      }

      if (this.active) {
        console.log("【节奏阅读】停止中...");
        this.active = false;

        // 仅当不是因翻页而停止时才清除活动状态
        if (!this.pageTransitionInProgress) {
          setStorage('rhythmReadingActive', 'false');
        }

        // 恢复原始文本
        this.restoreOriginalText();

        // 移除速度滑块
        const slider = document.getElementById('rhythm-speed-slider');
        if (slider) slider.remove();

        if (showNotification) {
          this.showNotification("节奏阅读模式已停止");
        }
      }
    },

    // 显示通知
    showNotification: function(message) {
      let notification = document.getElementById('rhythm-notification');
      if (notification) notification.remove();

      notification = document.createElement('div');
      notification.id = 'rhythm-notification';
      notification.textContent = message;
      notification.style.position = 'fixed';
      notification.style.top = '50%';
      notification.style.left = '50%';
      notification.style.transform = 'translate(-50%, -50%)';
      notification.style.backgroundColor = 'rgba(0, 0, 0, 0.8)';
      notification.style.color = 'white';
      notification.style.padding = '15px 25px';
      notification.style.borderRadius = '5px';
      notification.style.zIndex = '10001';
      notification.style.fontSize = '18px';

      document.body.appendChild(notification);

      // 2秒后自动消失
      setTimeout(() => {
        if (notification && notification.parentNode) {
          notification.parentNode.removeChild(notification);
        }
      }, 2000);
    },

    // 劫持页面翻转函数
    hijackPageTurningFunctions: function() {
      if (typeof window.changePage === 'function' && !window._originalChangePage) {
        console.log("【节奏阅读】劫持changePage函数");
        window._originalChangePage = window.changePage;

        const self = this;
        window.changePage = function(page) {
          console.log("【节奏阅读】检测到changePage调用，参数:", page);

          const wasActive = self.active;
          self.pageTransitionInProgress = true;

          if (wasActive) {
            self.stop(false);
            setStorage('rhythmReadingActive', 'true'); 
            setStorage('rhythmReadingPending', 'true');
            document.body.dataset.rhythmReadingActive = 'true';
          }

          try {
            if (page === undefined || page === null) {
              console.warn('【节奏阅读】安全处理：page参数为空');
              page = {};
            }

            if (typeof page === 'object' && !page.something) {
              console.log('【节奏阅读】添加空的something对象防止错误');
              page.something = { active: false };
            }

            window._originalChangePage(page);
          } catch (err) {
            console.error("【节奏阅读】翻页函数执行出错:", err);
            self.clickNextPageButton();
          }

          setTimeout(() => {
            self.pageTransitionInProgress = false;

            if (wasActive && getStorage('rhythmReadingPending') === 'true') {
              setStorage('rhythmReadingPending', 'false');
              console.log("【节奏阅读】翻页后重新启动");
              setTimeout(() => self.start(), 500);
            }
          }, 1000);
        };
      }
    },

    // 设置页面导航事件监听 - 加强版
    setupPageNavigationListeners: function() {
      // 查找所有可能的翻页按钮
      console.log("【节奏阅读】正在设置页面导航监听");

      const self = this;

      // 使用事件委托来捕获所有可能的点击
      document.addEventListener('click', function(e) {
        // 检查点击的元素是否可能是翻页按钮
        let target = e.target;
        while (target && target !== document.body) {
          // 检查是否是翻页元素
          if (target.tagName === 'BUTTON' || target.tagName === 'A' || 
              target.getAttribute('role') === 'button') {

            const text = (target.textContent || '').trim().toLowerCase();
            const ariaLabel = (target.getAttribute('aria-label') || '').toLowerCase();

            // 检查是否是翻页元素
            if (text.includes('上一页') || text.includes('下一页') || 
                text.includes('prev') || text.includes('next') ||
                ariaLabel.includes('page') || ariaLabel.includes('页') ||
                text === '<' || text === '>' || text === '←' || text === '→') {

              if (self.active) {
                console.log(`【节奏阅读】检测到可能的翻页元素点击: ${text || ariaLabel}`);

                // 标记页面转换开始
                self.pageTransitionInProgress = true;

                // 保存状态到存储
                setStorage('rhythmReadingActive', 'true');
                setStorage('rhythmReadingPending', 'true');
                document.body.dataset.rhythmReadingActive = 'true';

                // 停止当前的阅读
                self.stop(false);

                // 添加延迟执行的监听器，确保数据在页面加载后恢复
                setTimeout(() => {
                  self.pageTransitionInProgress = false;

                  if (getStorage('rhythmReadingPending') === 'true') {
                    setStorage('rhythmReadingPending', 'false');
                    self.start();
                  }
                }, 1500);
              }

              break;
            }
          }

          target = target.parentNode;
        }
      });

      // 监听键盘导航事件
      document.addEventListener('keydown', function(e) {
        // 检查是否是翻页快捷键
        if (['ArrowLeft', 'ArrowRight', 'PageUp', 'PageDown'].includes(e.key)) {
          if (self.active) {
            console.log(`【节奏阅读】检测到键盘翻页: ${e.key}`);

            // 保存状态到存储
            setStorage('rhythmReadingActive', 'true');
            setStorage('rhythmReadingPending', 'true');

            // 标记页面转换开始
            self.pageTransitionInProgress = true;

            // 停止当前的阅读
            self.stop(false);

            // 添加延迟执行的监听器
            setTimeout(() => {
              self.pageTransitionInProgress = false;

              if (getStorage('rhythmReadingPending') === 'true') {
                setStorage('rhythmReadingPending', 'false');
                self.start();
              }
            }, 1500);
          }
        }
      });

      // 添加popstate监听器捕获浏览器前进/后退导航
      window.addEventListener('popstate', function() {
        if (self.active) {
          console.log("【节奏阅读】检测到历史导航事件");

          // 保存状态
          setStorage('rhythmReadingActive', 'true');
          setStorage('rhythmReadingPending', 'true');

          // 标记页面转换开始
          self.pageTransitionInProgress = true;

          // 停止当前阅读
          self.stop(false);

          // 延迟恢复
          setTimeout(() => {
            self.pageTransitionInProgress = false;

            if (getStorage('rhythmReadingPending') === 'true') {
              setStorage('rhythmReadingPending', 'false');
              self.start();
            }
          }, 1500);
        }
      });
    },

    // 改进的自动翻页函数，更精确地处理状态保存
    clickNextPageButton: function() {
      console.log("【节奏阅读】尝试查找并点击下一页按钮");

      // 查找下一页按钮
      const nextPageSelectors = [
        'button.next-page', 
        'button[aria-label="下一页"]',
        'a.next-page',
        'a[aria-label="下一页"]',
        '.next-page',
        '#next-page',
        'button.next',
        'a.next'
      ];

      let nextPageButton = null;

      // 尝试通过选择器找到按钮
      for (const selector of nextPageSelectors) {
        try {
          const elements = document.querySelectorAll(selector);
          for (const el of elements) {
            if (el.offsetParent !== null) { // 检查元素是否可见
              console.log(`【节奏阅读】找到下一页按钮: ${selector}`);
              nextPageButton = el;
              break;
            }
          }
          if (nextPageButton) break;
        } catch (e) {
          // 某些选择器可能不被支持，忽略错误
        }
      }

      // 如果没找到，尝试按文本内容查找
      if (!nextPageButton) {
        const buttons = document.querySelectorAll('button, a, div[role="button"]');
        for (const button of buttons) {
          const text = button.textContent.trim().toLowerCase();
          if ((text.includes('下一页') || text.includes('next') || text === '>' || text === '→') && 
              button.offsetParent !== null) {
            console.log(`【节奏阅读】通过文本内容找到下一页按钮: ${text}`);
            nextPageButton = button;
            break;
          }
        }
      }

      if (nextPageButton) {
        console.log("【节奏阅读】模拟点击下一页按钮");

        // 明确标记页面转换开始
        this.pageTransitionInProgress = true;

        // 保存状态
        setStorage('rhythmReadingActive', 'true');
        setStorage('rhythmReadingPending', 'true');
        document.body.dataset.rhythmReadingActive = 'true';

        // 恢复原始文本
        this.restoreOriginalText();

        // 停止计时器
        if (this.intervalId) {
          clearInterval(this.intervalId);
          this.intervalId = null;
        }

        // 模拟点击
        nextPageButton.click();

        // 延迟结束页面转换状态
        setTimeout(() => {
          this.pageTransitionInProgress = false;
        }, 1000);

        return true;
  } else {
        console.log("【节奏阅读】未找到下一页按钮");
        return false;
      }
    }
  };

  // 暴露到全局，以便调试和直接调用
  window.rhythmReader = rhythmReader;

  // 额外添加一个全局安全函数用于检查节奏阅读状态
  window.isRhythmActive = function() {
    return rhythmReader.active || getStorage('rhythmReadingActive') === 'true';
  };

  // 添加一个停止函数供外部安全调用
  window.stopRhythmSafely = function() {
    try {
      setStorage('rhythmReadingActive', 'false');
      setStorage('rhythmReadingPending', 'false');

      if (rhythmReader && typeof rhythmReader.stop === 'function') {
        rhythmReader.stop();
      }
    } catch (e) {
      console.error("停止节奏阅读出错", e);
    }
  };

  // 设置页面加载监听器，确保页面转换后能恢复
  function setupPageLoadListener() {
    // DOM内容加载和完全加载的事件监听...

    // 改进的页面变化监测
    const observer = new MutationObserver(function(mutations) {
      // 检测到内容变化且有待处理的节奏阅读时
      if (getStorage('rhythmReadingPending') === 'true') {
        const contentChanges = mutations.some(m => 
          m.type === 'childList' && m.addedNodes.length > 5); // 使用更高的阈值，确保是真正的内容变化

        if (contentChanges) {
          console.log("【节奏阅读】检测到大量DOM变化，可能是页面切换");

          // 给页面一点时间稳定下来
          setTimeout(() => {
            if (getStorage('rhythmReadingPending') === 'true') {
              console.log("【节奏阅读】页面内容变化后，尝试恢复阅读");
              setStorage('rhythmReadingPending', 'false');

              if (!rhythmReader.pageTransitionInProgress) {
                rhythmReader.start();
              }
            }
          }, 800);
        }
      }
    });

    // 观察body变化
    observer.observe(document.body, {
      childList: true,
      subtree: true
    });
  }

  // 设置页面加载监听器
  setupPageLoadListener();

  // 检查是否需要立即恢复阅读状态
  if (getStorage('rhythmReadingActive') === 'true') {
    console.log("【节奏阅读】检测到之前的阅读状态，准备恢复");
    setStorage('rhythmReadingPending', 'false');

    setTimeout(function() {
      rhythmReader.start();
    }, 800);
  }

  console.log("【节奏阅读】初始化完成");
})();

const App = () => {
  // 状态定义
  const [pages, setPages] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const [totalPages, setTotalPages] = useState(0);
  const [fileName, setFileName] = useState('');
  const [loading, setLoading] = useState(false);
  const fileRef = useRef(null);
  const [file, setFile] = useState(null);
  const [pdfInstance, setPdfInstance] = useState(null);
  const [extractedTextPages, setExtractedTextPages] = useState([]);
  const [readingMode, setReadingMode] = useState('normal');
  const [posMode, setPosMode] = useState('none');
  const [markdownContent, setMarkdownContent] = useState('');
  const readerContainerRef = useRef(null);
  const autoReadGuideRef = useRef(null);
  const autoReadIntervalRef = useRef(null);
  const [content, setContent] = useState('');
  const [pdfDocument, setPdfDocument] = useState(null);

  // 新增自动阅读器状态
  const [autoReaderState, setAutoReaderState] = useState({
    currentIndex: 0,
    charPositions: [],
    intervalId: null,
    speed: 300,
    pageContainer: null,
    isUserScrolling: false,
    scrollTimeout: null,
    contentLoaded: false
  });

  // 初始化PDF.js
  useEffect(() => {
    pdfjsLib.GlobalWorkerOptions.workerSrc = `https://cdnjs.cloudflare.com/ajax/libs/pdf.js/${pdfjsLib.version}/pdf.worker.min.js`;

    return () => {
      // 清理自动阅读定时器
      if (autoReadIntervalRef.current) {
        clearInterval(autoReadIntervalRef.current);
      }
    };
  }, []);

  // 当阅读模式改变时应用效果
  useEffect(() => {
    if (readingMode === 'follow-mouse') {
      // 确保DOM更新完成后初始化鼠标跟随功能
      setTimeout(() => {
        initializeMouseFollow();
      }, 50);
    }
  }, [readingMode, markdownContent]);

  // 修改PDF处理函数，确保内容正确提取和显示
  const handlePdfFile = async (file) => {
    try {
      const fileURL = URL.createObjectURL(file);
      const loadingTask = pdfjsLib.getDocument(fileURL);
      const pdf = await loadingTask.promise;

      setPdfDocument(pdf);
      setTotalPages(pdf.numPages);
      setCurrentPage(1);

      // 加载第一页
      await extractAndProcessPage(pdf, 1);

      // 添加调试信息
      console.log('PDF加载成功，页数:', pdf.numPages);
    } catch (error) {
      console.error('加载PDF时出错:', error);
      alert('PDF加载失败，请尝试其他文件');
    }
  };

  // 提取和处理PDF页面内容的函数 - 区分目录和正文页面
  const extractAndProcessPage = async (pdf, pageNum) => {
    try {
      const page = await pdf.getPage(pageNum);
      const textContent = await page.getTextContent();
      const viewport = page.getViewport({ scale: 1.0 });

      // 判断当前页面是否为目录页
      const isTableOfContents = await detectIfTocPage(textContent);

      if (isTableOfContents) {
        // 处理目录页面 - 保持原布局，仅调整字间距
        await processTocPage(textContent, viewport);
      } else {
        // 处理正文页面 - 以句号为标准划分段落
        await processContentPage(page, readingMode);
      }

      // 在内容处理完成后，如果是鼠标跟随模式，添加监听器
      if (readingMode === 'follow-mouse') {
        setTimeout(() => {
          addMouseFollowListeners();
        }, 50);
      }

      return true; // 返回成功标志
    } catch (error) {
      console.error('处理PDF页面时出错:', error);
      setContent('<div>无法处理PDF内容</div>');
      return false;
    }
  };

  // 检测是否为目录页
  const detectIfTocPage = async (textContent) => {
    const textItems = textContent.items;
    let fullText = textItems.map(item => item.str).join('');

    // 检查特征: 
    // 1. 页面上包含"目录"字样
    // 2. 行尾通常有页码和前导符（...）
    // 3. 短行居多，且多有规律的缩进
    const hasTocKeywords = /目\s*录|contents|index/i.test(fullText);
    const hasPageNumberPatterns = /\.{2,}|…{1,}\s*\d+/.test(fullText);

    // 检查行的特征
    let shortLinesCount = 0;
    let linesWithNumbersCount = 0;

    // 按Y坐标分组
    const lineMap = new Map();
    textItems.forEach(item => {
      const yKey = Math.round(item.transform[5]);
      if (!lineMap.has(yKey)) {
        lineMap.set(yKey, []);
      }
      lineMap.get(yKey).push(item);
    });

    const lines = [...lineMap.values()];

    lines.forEach(line => {
      const lineText = line.map(item => item.str).join('');
      if (lineText.length < 50) shortLinesCount++;
      if (/\d+$/.test(lineText)) linesWithNumbersCount++;
    });

    const shortLineRatio = shortLinesCount / lines.length;
    const numberLineRatio = linesWithNumbersCount / lines.length;

    return hasTocKeywords || hasPageNumberPatterns || (shortLineRatio > 0.7 && numberLineRatio > 0.3);
  };

  // 处理目录页面 - 严格保持原始布局
  const processTocPage = async (textContent, viewport) => {
    const textItems = textContent.items;

    // 生成HTML，严格保持原始位置和格式
    let htmlContent = '<div class="pdf-toc-page">';

    textItems.forEach(item => {
      // 获取原始位置信息
      const x = item.transform[4];
      const y = viewport.height - item.transform[5]; // 转换Y坐标

      // 获取原始字体信息
      const fontSize = Math.sqrt(item.transform[0] * item.transform[0] + item.transform[1] * item.transform[1]);
      const fontWeight = item.fontName && item.fontName.toLowerCase().includes('bold') ? 'bold' : 'normal';

      // 仅应用字间距，其他完全保持原样
      const spacedText = item.str.split('').join('\u200A');

      // 使用绝对定位和原始样式属性
      htmlContent += `<span style="
        position: absolute; 
        left: ${x}px; 
        top: ${y}px; 
        font-size: ${fontSize}px; 
        font-weight: ${fontWeight};
        letter-spacing: 0.3em;
        white-space: nowrap;
        font-family: inherit;
        color: inherit;
      ">${spacedText}</span>`;
    });

    htmlContent += '</div>';
    setContent(htmlContent);
  };

  // 获取鼠标位置下的文本节点
  const getTextNodeAtPosition = (element, x, y) => {
    const nodes = element.childNodes;
    for (let i = 0; i < nodes.length; i++) {
      const node = nodes[i];
      if (node.nodeType === Node.TEXT_NODE) {
        const range = document.createRange();
        range.selectNodeContents(node);
        const rects = range.getClientRects();
        for (let j = 0; j < rects.length; j++) {
          const rect = rects[j];
          if (y >= rect.top && y <= rect.bottom && 
              x >= rect.left && x <= rect.right) {
            return node;
          }
        }
      } else if (node.nodeType === Node.ELEMENT_NODE) {
        const found = getTextNodeAtPosition(node, x, y);
        if (found) return found;
      }
    }
    return null;
  };

  // 处理文本高亮
  const processTextWithHighlight = (text) => {
    // 将文本按空格和标点符号分割成词
    const words = text.split(/([\s.,!?，。！？]+)/);
    let result = '';
    let lastColor = null;

    words.forEach(word => {
      if (!word || /^\s+$/.test(word)) {
        result += word;
        return;
      }

      if (Math.random() < 0.8) {
        let color;
        if (lastColor === 'red') {
          color = 'blue';
        } else if (lastColor === 'blue') {
          color = 'red';
        } else {
          color = Math.random() < 0.5 ? 'red' : 'blue';
        }
        lastColor = color;

        result += `<span class="highlight-word ${color}">${word}</span>`;
      } else {
        result += word;
      }
    });

    return result;
  };

  // 修改processContentPage函数
  const processContentPage = async (page, mode) => {
    try {
      const textContent = await page.getTextContent();
      const textItems = textContent.items;
      let extractedText = textItems.map(item => item.str).join(' ');

      // 移除多余的空格
      extractedText = extractedText.replace(/\s+/g, ' ').trim();

      // 根据阅读模式处理文本
      let processedContent = '';
      switch(mode) {
        case 'normal':
          // 普通模式 - 简单分段显示
          processedContent = formatTextToHtml(extractedText);
          break;
        case 'static':
          // 静态高亮模式 - 应用词性标注和颜色高亮
          processedContent = applyStaticHighlight(extractedText);
          break;
        case 'follow-mouse':
          // 鼠标跟随高亮模式 - 分段落但不预先高亮，由鼠标事件触发高亮
          processedContent = formatTextForMouseFollow(extractedText);
          break;
        default:
          processedContent = formatTextToHtml(extractedText);
      }

      setContent(processedContent);
    } catch (error) {
      console.error('处理内容页面时出错:', error);
      setContent('<div>无法处理PDF内容</div>');
    }
  };

  // 处理TXT文件
  const handleTxtFile = async (file) => {
    try {
      setLoading(true);
      const text = await file.text();
      // 将文本分页处理
      const pageTexts = text.split('\n\n').map(paragraph => [{
        text: paragraph,
        x: 40,
        y: 40,
        fontSize: 16,
        fontFamily: 'serif'
      }]);
      setPages(pageTexts);
      setTotalPages(pageTexts.length);
      setCurrentPage(1);
      setLoading(false);
    } catch (error) {
      console.error('TXT处理错误:', error);
      setLoading(false);
      alert('TXT文件处理失败，请重试');
    }
  };

  // 文件上传处理
  const handleFileUpload = async (e) => {
    const file = e.target.files[0];
    if (!file) return;

    if (file.type === 'application/pdf') {
      await handlePdfFile(file);
    } else if (file.type === 'text/plain') {
      handleTxtFile(file);
    } else {
      alert('请上传PDF或TXT文件');
    }
  };

  // 获取所有字符位置（优化版 - 记录字符底部位置）
  const getAllCharacterPositions = (container) => {
    const walker = document.createTreeWalker(container, NodeFilter.SHOW_TEXT);
    const positions = [];

    while (walker.nextNode()) {
      const node = walker.currentNode;
      const text = node.textContent;

      // 跳过完全空白的文本节点
      if (!text.trim()) continue;

      // 逐字符处理
      for (let i = 0; i < text.length; i++) {
        try {
          const range = document.createRange();
          range.setStart(node, i);
          range.setEnd(node, i + 1);

          const rect = range.getBoundingClientRect();
          if (rect.width === 0 && rect.height === 0) continue;

          // 记录字符底部中心位置，而不是中心位置
          positions.push({
            x: rect.left + rect.width/2,  // 水平中心
            y: rect.bottom,               // 字符底部
            char: text[i]
          });
        } catch (error) {
          console.warn('获取字符位置时出错:', error);
          continue;
        }
      }
    }

    // 过滤无效位置并排序
    return positions
      .filter(p => p.char.trim() !== '')
      .sort((a, b) => {
        // 先按行排序，同行按列排序
        const yDiff = a.y - b.y;
        if (Math.abs(yDiff) > 5) { // 5px阈值判断是否同一行
          return yDiff;
        }
        return a.x - b.x;
      });
  };

  // 确保元素在容器中居中的专用函数
  const scrollElementToCenter = (elementY, container) => {
    // 判断是容器滚动还是窗口滚动
    const isContainerScrollable = container.scrollHeight > container.clientHeight && 
      ['auto', 'scroll', 'overlay'].includes(window.getComputedStyle(container).overflowY);

    // 计算需要滚动的目标位置
    let targetScrollTop;

    if (isContainerScrollable) {
      // 容器滚动：计算将元素放在容器中心所需的滚动量
      const containerVisibleHeight = container.clientHeight;
      targetScrollTop = elementY - (containerVisibleHeight / 2);

      // 执行滚动
      container.scrollTo({
        top: Math.max(0, targetScrollTop), // 防止滚动到负值
        behavior: 'smooth'
      });
    } else {
      // 窗口滚动：计算将元素放在窗口中心所需的滚动量
      const absoluteElementY = elementY + container.getBoundingClientRect().top;
      targetScrollTop = absoluteElementY - (window.innerHeight / 2);

      // 执行滚动
      window.scrollTo({
        top: Math.max(0, targetScrollTop), // 防止滚动到负值
        behavior: 'smooth'
      });
    }

    return targetScrollTop;
  };

  // 创建字符底部显示的阅读光标（确保与滚动兼容）
  const createReaderCursor = () => {
    const cursor = document.createElement('div');
    cursor.id = 'auto-reader-cursor';
    cursor.style.cssText = `
      position: absolute;
      width: 10px;
      height: 10px;
      background: #00ff00;
      border-radius: 50%;
      pointer-events: none;
      transform: translateX(-50%);
      box-shadow: 0 0 8px #00ff00;
      animation: pulse-bottom 1s infinite;
      z-index: 9999;
      margin-top: -2px;
    `;

    // 添加动画样式
    if (!document.getElementById('reader-cursor-animation')) {
      const style = document.createElement('style');
      style.id = 'reader-cursor-animation';
      style.textContent = `
        @keyframes pulse-bottom {
          0% { transform: translateX(-50%) scale(1); opacity: 0.8; }
          50% { transform: translateX(-50%) scale(1.2); opacity: 1; }
          100% { transform: translateX(-50%) scale(1); opacity: 0.8; }
        }
      `;
      document.head.appendChild(style);
    }

    return cursor;
  };

  // 检测当前位置是否为句子结束
  const isSentenceEnd = (charPosition, positions, index) => {
    // 标点符号列表，用于判断句子结束
    const sentenceEndMarks = ['.', '。', '!', '！', '?', '？', ';', '；'];

    // 检查当前字符是否为句子结束标记
    if (sentenceEndMarks.includes(charPosition.char)) {
      // 句子结束后通常有空格或是段落末尾
      const nextIndex = index + 1;
      if (nextIndex >= positions.length || // 已到末尾
          positions[nextIndex].char.trim() === '' || // 下一个是空白
          positions[nextIndex].y > charPosition.y + 5) { // 下一个在新行
        return true;
      }
    }

    return false;
  };

  // 查找句子开始位置
  const findSentenceStart = (positions, currentIndex) => {
    let start = currentIndex;

    // 向前查找句子开始
    while (start > 0) {
      const prev = positions[start - 1];
      const current = positions[start];

      // 如果前一个是句子结束标记，或者有明显的换行，认为是新句子开始
      if (isSentenceEnd(prev, positions, start - 1) || 
          current.y > prev.y + 5) { // 有明显换行
        break;
      }

      start--;
    }

    return start;
  };

  // 判断元素是否在可见区域内
  const isInVisibleArea = (elementY, container, threshold = 0.2) => {
    // 判断是容器滚动还是窗口滚动
    const isContainerScrollable = container.scrollHeight > container.clientHeight && 
      ['auto', 'scroll', 'overlay'].includes(window.getComputedStyle(container).overflowY);

    if (isContainerScrollable) {
      // 容器情况
      const visibleTop = container.scrollTop;
      const visibleBottom = visibleTop + container.clientHeight;

      // 元素是否在可见区域的安全范围内（加入阈值缓冲）
      const topThreshold = visibleTop + container.clientHeight * threshold;
      const bottomThreshold = visibleBottom - container.clientHeight * threshold;

      return elementY >= topThreshold && elementY <= bottomThreshold;
    } else {
      // 窗口情况
      const absoluteElementY = elementY + container.getBoundingClientRect().top;
      const visibleTop = window.scrollY;
      const visibleBottom = visibleTop + window.innerHeight;

      // 元素是否在可见区域的安全范围内
      const topThreshold = visibleTop + window.innerHeight * threshold;
      const bottomThreshold = visibleBottom - window.innerHeight * threshold;

      return absoluteElementY >= topThreshold && absoluteElementY <= bottomThreshold;
    }
  };

  // 启动自动阅读（保持一致的轻微滚动行为）
  const startAutoReader = () => {
    if (window.autoReader.charPositions.length === 0) {
      if (window.currentPage < window.totalPages) {
        window.changePage(1);
        return;
      }
      return;
    }

    // 重置索引到开头
    window.autoReader.currentIndex = 0;

    // 获取内容容器
    const contentContainer = document.querySelector('.page-content');
    if (!contentContainer) return;

    if (window.getComputedStyle(contentContainer).position === 'static') {
      contentContainer.style.position = 'relative';
    }

    // 创建或获取光标
    let cursor = document.getElementById('auto-reader-cursor');
    if (!cursor) {
      cursor = createReaderCursor();
      contentContainer.appendChild(cursor);
    }

    // 创建或获取高亮元素
    let highlight = document.getElementById('auto-reader-highlight');
    if (!highlight) {
      highlight = document.createElement('div');
      highlight.id = 'auto-reader-highlight';
      highlight.style.position = 'absolute';
      highlight.style.backgroundColor = 'rgba(0, 255, 0, 0.3)';
      highlight.style.borderRadius = '2px';
      highlight.style.pointerEvents = 'none';
      highlight.style.zIndex = '998';
      contentContainer.appendChild(highlight);
    }

    // 确定是哪个元素需要滚动（容器或窗口）
    const isContainerScrollable = contentContainer.scrollHeight > contentContainer.clientHeight &&
      ['auto', 'scroll', 'overlay'].includes(window.getComputedStyle(contentContainer).overflowY);

    // 估计的行高（用于计算滚动量）
    let lineHeight = 20;

    // 尝试获取实际行高
    if (window.autoReader.charPositions.length > 1) {
      for (let i = 1; i < window.autoReader.charPositions.length; i++) {
        const yDiff = Math.abs(window.autoReader.charPositions[i].y - window.autoReader.charPositions[i-1].y);
        if (yDiff > 5) {
          lineHeight = yDiff;
          break;
        }
      }
    }

    // 记录上次滚动的位置
    let lastScrolledY = -1;
    const scrollSafeZone = lineHeight * 2.5;

    // 设置字符大小的估计值
    let charWidth = 20;
    let charHeight = lineHeight * 0.8;

    // 尝试估计字符宽度
    if (window.autoReader.charPositions.length > 1) {
      for (let i = 1; i < window.autoReader.charPositions.length; i++) {
        if (window.autoReader.charPositions[i].x !== window.autoReader.charPositions[i-1].x) {
          const xDiff = Math.abs(window.autoReader.charPositions[i].x - window.autoReader.charPositions[i-1].x);
          if (xDiff > 0 && xDiff < 50) {
            charWidth = xDiff;
            break;
          }
        }
      }
    }

    window.autoReader.intervalId = setInterval(() => {
      // 跳过空白字符
      while (window.autoReader.currentIndex < window.autoReader.charPositions.length && 
             window.autoReader.charPositions[window.autoReader.currentIndex]?.char.trim() === '') {
        window.autoReader.currentIndex++;
      }

      // 当前页结束处理
      if (window.autoReader.currentIndex >= window.autoReader.charPositions.length) {
        if (window.currentPage < window.totalPages) {
          window.changePage(1);
          clearInterval(window.autoReader.intervalId);
        } else {
          stopAutoReader();
        }
        return;
      }

      const pos = window.autoReader.charPositions[window.autoReader.currentIndex];
      cursor = document.getElementById('auto-reader-cursor');
      highlight = document.getElementById('auto-reader-highlight');

      if (cursor && highlight) {
        const contentRect = contentContainer.getBoundingClientRect();

        const relativeX = pos.x - contentRect.left;
        const relativeY = pos.y - contentRect.top + contentContainer.scrollTop;

        cursor.style.left = `${relativeX}px`;
        cursor.style.top = `${relativeY - contentContainer.scrollTop}px`;

        highlight.style.left = `${relativeX - charWidth/2}px`;
        highlight.style.top = `${relativeY - contentContainer.scrollTop - charHeight}px`;
        highlight.style.width = `${charWidth}px`;
        highlight.style.height = `${charHeight}px`;

        // 滚动处理
        const isInRecentlyScrolledArea = Math.abs(relativeY - lastScrolledY) < scrollSafeZone;

        if (!isInRecentlyScrolledArea) {
          let needsScroll = false;
          let scrollAmount = 0;

          if (isContainerScrollable) {
            const visibleTop = contentContainer.scrollTop;
            const visibleBottom = visibleTop + contentContainer.clientHeight;

            if (relativeY > visibleBottom - lineHeight) {
              needsScroll = true;
              scrollAmount = lineHeight * 2;
            } else if (relativeY < visibleTop + lineHeight) {
              needsScroll = true;
              scrollAmount = -lineHeight * 2;
            }

            if (needsScroll) {
              contentContainer.scrollBy({
                top: scrollAmount,
                behavior: 'smooth'
              });
              lastScrolledY = relativeY;
            }
          } else {
            const absoluteY = pos.y;
            const visibleTop = window.scrollY;
            const visibleBottom = visibleTop + window.innerHeight;

            if (absoluteY > visibleBottom - lineHeight) {
              needsScroll = true;
              scrollAmount = lineHeight * 2;
            } else if (absoluteY < visibleTop + lineHeight) {
              needsScroll = true;
              scrollAmount = -lineHeight * 2;
            }

            if (needsScroll) {
              window.scrollBy({
                top: scrollAmount,
                behavior: 'smooth'
              });
              lastScrolledY = absoluteY;
            }
          }
        }
      }

      window.autoReader.currentIndex++;

    }, window.autoReader.speed);

    // 在停止自动阅读时清除所有高亮
    window.autoReader.cleanup = () => {
      // 移除高亮
      const highlight = document.getElementById('auto-reader-highlight');
      if (highlight) {
        highlight.remove();
      }

      // 移除光标
      const cursor = document.getElementById('auto-reader-cursor');
      if (cursor) {
        cursor.remove();
      }
    };
  };

  // 修改stopAutoReader函数以包含清理高亮
  const stopAutoReader = () => {
    if (window.autoReader.intervalId) {
      clearInterval(window.autoReader.intervalId);
      window.autoReader.intervalId = null;

      // 调用清理函数
      if (window.autoReader.cleanup) {
        window.autoReader.cleanup();
      }
    }
  };

  // 初始化自动阅读模式
  const initializeAutoReader = () => {
    // 清理可能存在的旧数据
    stopAutoReader();

    // 重置翻页状态
    window.autoReader.isPageChanging = false;

    // 收集所有字符位置
    const content = document.querySelector('.page-content');
    if (!content) return;

    // 确保内容滚动到顶部
    if (content.scrollTo) {
      content.scrollTo(0, 0);
    }

    // 确保容器有正确的定位
    if (window.getComputedStyle(content).position === 'static') {
      content.style.position = 'relative';
    }

    // 收集字符位置
    window.autoReader.charPositions = getAllCharacterPositions(content);

    // 如果当前页没有内容，尝试翻页
    if (window.autoReader.charPositions.length === 0) {
      if (currentPage < totalPages && !window.autoReader.isPageChanging) {
        window.autoReader.isPageChanging = true;
        changePage(1);
        return;
      }
      return;
    }

    // 立即开始移动
    startAutoReader();
  };

  // 修改阅读模式切换函数
  const handleReadingModeChange = async (e) => {
    const newMode = e.target.value;

    // 停止当前可能正在运行的自动阅读和节奏阅读
    stopAutoReader();
    if (window.rhythmReader) {
      window.rhythmReader.stop();
    }

    // 清理旧模式
    cleanupDOM();

    // 更新状态
    setReadingMode(newMode);

    // 应用新模式
    if (pdfDocument && currentPage > 0) {
      try {
        const page = await pdfDocument.getPage(currentPage);
        const textContent = await page.getTextContent();
        const textItems = textContent.items;
        let extractedText = textItems.map(item => item.str).join(' ');
        extractedText = extractedText.replace(/\s+/g, ' ').trim();

        let processedContent = '';

        // 应用对应模式的文本处理
        switch(newMode) {
          case 'follow-mouse':
            processedContent = formatTextForMouseFollow(extractedText);
            break;
          case 'static':
            processedContent = applyStaticHighlight(extractedText);
            break;
          case 'auto-reader':
            processedContent = formatTextToHtml(extractedText);
            break;
          case 'rhythm':
            processedContent = formatTextToHtml(extractedText);
            break;
          default:
            processedContent = formatTextToHtml(extractedText);
        }

        const contentArea = document.querySelector('.page-content');
        if (contentArea) {
          contentArea.textContent = '';
          contentArea.innerHTML = processedContent;

          // 滚动到页面顶部
          window.scrollTo(0, 0);
          if (contentArea.scrollTo) {
            contentArea.scrollTo(0, 0);
          }

          // 立即初始化对应模式
          if (newMode === 'auto-reader') {
            setTimeout(initializeAutoReader, 50);
          } else if (newMode === 'follow-mouse') {
            setTimeout(initializeMouseFollow, 50);
          } else if (newMode === 'rhythm') {
            setTimeout(() => {
              if (window.rhythmReader) {
                window.rhythmReader.start();
              }
            }, 50);
          }
        }
      } catch (error) {
        console.error('应用新阅读模式失败:', error);
      }
    }
  };

  // 清理DOM元素和效果
  const cleanupDOM = () => {
    // 1. 移除所有可能的高亮元素
    const highlights = document.querySelectorAll(
      '.mouse-highlight, .line-highlight, .highlight-word, .reading-line, .text-line, .rhythm-char'
    );
    highlights.forEach(el => el.remove());

    // 2. 移除事件监听器
    const contentArea = document.querySelector('.page-content');
    if (contentArea) {
      // 克隆内容区域以彻底移除所有事件监听器
      const cleanContent = contentArea.cloneNode(false);
      cleanContent.innerHTML = contentArea.innerHTML;
      contentArea.parentNode.replaceChild(cleanContent, contentArea);
    }

    // 3. 重置段落结构
      const paragraphs = document.querySelectorAll('.pdf-paragraph');
      paragraphs.forEach(paragraph => {
      // 创建新段落并保留基本结构
      const newParagraph = document.createElement('p');
      newParagraph.className = 'pdf-paragraph';
      newParagraph.innerHTML = paragraph.innerHTML
        .replace(/<span class="text-line">/g, '')
        .replace(/<\/span>/g, '')
        .replace(/<span class="rhythm-char">/g, '')
        .replace(/<\/span>/g, '');

      // 移除特殊属性
      newParagraph.removeAttribute('data-follow');
      newParagraph.removeAttribute('style');

        if (paragraph.parentNode) {
          paragraph.parentNode.replaceChild(newParagraph, paragraph);
        }
      });

    // 4. 清理自动阅读器
    stopAutoReader();

    // 5. 移除节奏阅读速度滑块
    const slider = document.getElementById('rhythm-speed-slider');
    if (slider) slider.remove();
  };

  const handleAutoScroll = () => {
    window.autoReader.isScrolling = true;
    // ... 防抖动逻辑
  };

  const updateCursorPosition = (force = false) => {
    // ... 优化的位置更新和滚动逻辑
  };

  // 处理鼠标移动事件
  const handleMouseMove = (e) => {
    if (readingMode !== 'follow-mouse') return;

    // 清除所有现有高亮
    document.querySelectorAll('.line-highlight').forEach(el => el.remove());

    const mouseTarget = document.elementFromPoint(e.clientX, e.clientY);

    if (mouseTarget && 
       (mouseTarget.classList.contains('pdf-paragraph') || 
        mouseTarget.tagName === 'P' || 
        mouseTarget.tagName === 'SPAN')) {

      const targetParagraph = mouseTarget.closest('.pdf-paragraph');
      if (!targetParagraph) return;

      // 获取段落文本节点
      const textNode = getTextNodeAtPosition(targetParagraph, e.clientX, e.clientY);
      if (!textNode) return;

      // 获取精确的行位置和尺寸
      const range = document.createRange();
      range.selectNodeContents(textNode);
      const rects = range.getClientRects();

      // 找到鼠标所在的具体行
      let targetRect = null;
      for (let i = 0; i < rects.length; i++) {
        const rect = rects[i];
        if (e.clientY >= rect.top && e.clientY <= rect.bottom) {
          targetRect = rect;
          break;
        }
      }

      if (targetRect) {
        // 创建高亮元素
        const lineHighlight = document.createElement('div');
        lineHighlight.className = 'line-highlight mouse-highlight';

        // 计算相对于段落的位置
        const paragraphRect = targetParagraph.getBoundingClientRect();
        const top = targetRect.top - paragraphRect.top;
        const left = targetRect.left - paragraphRect.left;

        // 设置高亮样式
        lineHighlight.style.position = 'absolute';
        lineHighlight.style.left = `${left}px`;
        lineHighlight.style.top = `${top}px`;
        lineHighlight.style.width = `${targetRect.width}px`;
        lineHighlight.style.height = `${targetRect.height}px`;
        lineHighlight.style.pointerEvents = 'none';
        lineHighlight.style.backgroundColor = 'rgba(255, 165, 0, 0.3)';
        lineHighlight.style.borderRadius = '4px';
        lineHighlight.style.boxShadow = '0 0 8px rgba(255, 165, 0, 0.5)';
        lineHighlight.style.zIndex = '5';
        lineHighlight.style.transition = 'all 0.1s ease';

        // 确保段落有相对定位
          targetParagraph.style.position = 'relative';
        targetParagraph.appendChild(lineHighlight);
      }
    }
  };

  // 清理所有高亮
  const clearAllHighlights = () => {
    document.querySelectorAll('.line-highlight').forEach(el => el.remove());
  };

  // 添加自动阅读器样式
  const addAutoReaderStyles = () => {
    const style = document.createElement('style');
    style.textContent = `
      @keyframes pulse {
        0% { transform: translate(-50%, -50%) scale(0.8); opacity: 0.8; }
        50% { transform: translate(-50%, -50%) scale(1.2); opacity: 1; }
        100% { transform: translate(-50%, -50%) scale(0.8); opacity: 0.8; }
      }
      .speed-control {
        position: fixed;
        top: 20px;
        right: 20px;
        background: rgba(0,0,0,0.7);
        padding: 10px;
        border-radius: 8px;
        color: white;
        z-index: 10000;
      }
      .speed-control button {
        margin: 0 5px;
        padding: 5px 10px;
        background: #4CAF50;
        border: none;
        border-radius: 4px;
        color: white;
        cursor: pointer;
      }
    `;
    document.head.appendChild(style);
  };

  // 在组件挂载时添加样式
  useEffect(() => {
    addAutoReaderStyles();
  }, []);

  // 初始化鼠标跟随模式
  const initializeMouseFollow = () => {
    // 新增延迟补偿
    setTimeout(() => {
      addMouseFollowListeners();

      // 强制触发重排
      const contentArea = document.querySelector('.page-content');
      if (contentArea) {
        void contentArea.offsetHeight;

        // 模拟鼠标移动事件
        const fakeEvent = new MouseEvent('mousemove', {
          clientX: window.innerWidth / 2,
          clientY: window.innerHeight / 2,
          bubbles: true
        });
        contentArea.dispatchEvent(fakeEvent);
      }
    }, 50);
  };

  // 调整阅读速度
  const adjustSpeed = (delta) => {
    window.autoReader.speed = Math.max(100, window.autoReader.speed + delta);
    setAutoReaderState(prev => ({ ...prev, speed: window.autoReader.speed }));

    if (window.autoReader.intervalId) {
      stopAutoReader();
      startAutoReader();
    }
  };

  // 修改翻页函数
  const changePage = async (delta) => {
    const newPage = currentPage + delta;

    if (newPage < 1 || newPage > totalPages) return;

    const wasAutoReading = readingMode === 'auto-reader' && window.autoReader.intervalId;
    const wasRhythmReading = readingMode === 'rhythm' && window.rhythmReaderGlobal.active;

    // 如果正在自动阅读或节奏阅读，先停止
    if (wasAutoReading) {
      clearInterval(window.autoReader.intervalId);
      window.autoReader.intervalId = null;
    }

    if (wasRhythmReading && window.rhythmReader) {
      window.rhythmReader.stop();
    }

    // 更新页码
    setCurrentPage(newPage);

    try {
      // 加载新页面
      const page = await pdfDocument.getPage(newPage);
      const textContent = await page.getTextContent();
      const textItems = textContent.items;
      let extractedText = textItems.map(item => item.str).join(' ');
      extractedText = extractedText.replace(/\s+/g, ' ').trim();

      // 按当前阅读模式处理内容
      let processedContent = '';
      switch(readingMode) {
        case 'follow-mouse':
          processedContent = formatTextForMouseFollow(extractedText);
          break;
        case 'static':
          processedContent = applyStaticHighlight(extractedText);
          break;
        case 'auto-reader':
          processedContent = formatTextToHtml(extractedText);
          break;
        case 'rhythm':
          processedContent = formatTextToHtml(extractedText);
          break;
        default:
          processedContent = formatTextToHtml(extractedText);
      }

      // 更新页面内容
      const contentArea = document.querySelector('.page-content');
      if (contentArea) {
        contentArea.textContent = '';
        contentArea.innerHTML = processedContent;

        // 滚动到页面顶部
        window.scrollTo(0, 0);
        if (contentArea.scrollTo) {
          contentArea.scrollTo(0, 0);
        }

        // 重新启动对应的阅读模式
        if (wasAutoReading) {
          setTimeout(initializeAutoReader, 50);
        } else if (wasRhythmReading && window.rhythmReader) {
          setTimeout(() => {
            window.rhythmReader.start();
          }, 50);
        }
      }
    } catch (error) {
      console.error('翻页失败:', error);
    }
  };

  // 修改后的监听器设置函数
  const addMouseFollowListeners = () => {
    if (readingMode !== 'follow-mouse') return;

    const contentArea = document.querySelector('.page-content');
    if (!contentArea) return;

    // 清理旧的监听器
    contentArea.removeEventListener('mousemove', handleMouseMove);
    contentArea.removeEventListener('mouseleave', clearAllHighlights);

    // 添加新的监听器
    contentArea.addEventListener('mousemove', handleMouseMove);
    contentArea.addEventListener('mouseleave', clearAllHighlights);

    // 确保所有段落都有正确的样式
    document.querySelectorAll('.pdf-paragraph').forEach(p => {
      p.style.position = 'relative';
      p.style.lineHeight = '1.5';
    });
  };

  // 格式化文本为HTML
  const formatTextToHtml = (text) => {
    // 分段
    const paragraphs = text.split(/(?<=。|！|？|\.|\!|\?)\s+/).filter(p => p.trim());

    // 创建HTML内容
    let htmlContent = '<div class="pdf-content">';

    paragraphs.forEach(paragraph => {
      htmlContent += `<p class="pdf-paragraph">${paragraph}</p>`;
    });

    htmlContent += '</div>';
    return htmlContent;
  };

  // 应用静态高亮
  const applyStaticHighlight = (text) => {
    const paragraphs = text.split(/(?<=。|！|？|\.|\!|\?)\s+/).filter(p => p.trim());

    let htmlContent = '<div class="pdf-content">';

    paragraphs.forEach(paragraph => {
      // 新增强制清理逻辑
      const cleanParagraph = paragraph
        .replace(/<span class="text-line">/g, '')
        .replace(/<\/span>/g, '');

      const highlightedText = processTextWithHighlight(cleanParagraph);
      htmlContent += `<p class="pdf-paragraph">${highlightedText}</p>`;
    });

    htmlContent += '</div>';
    return htmlContent;
  };

  // 格式化文本用于鼠标跟随高亮模式
  const formatTextForMouseFollow = (text) => {
    const paragraphs = text.split(/(?<=。|！|？|\.|\!|\?)\s+/).filter(p => p.trim());

    let htmlContent = '<div class="pdf-content mouse-follow-content">';

    paragraphs.forEach(paragraph => {
      // 将段落分割成行
      const lines = paragraph.split('\n').filter(line => line.trim());
      htmlContent += `<p class="pdf-paragraph" data-follow="true" style="position: relative; line-height: 1.5;">`;

      lines.forEach((line, i) => {
        // 每行用span包裹
        htmlContent += `<span class="text-line" style="display: block;">${line}</span>`;
      });

      htmlContent += `</p>`;
    });

    htmlContent += '</div>';
    return htmlContent;
  };

  // 强制初始化字符位置（这是关键步骤！）
  function forceInitReader() {
    console.log("强制初始化阅读器...");

    // 1. 如果autoReader或charPositions不存在，创建它们
    if (typeof window.autoReader === 'undefined') {
      window.autoReader = {
        charPositions: [],
        intervalId: null,
        currentIndex: 0,
        speed: 30
      };
    }

    // 2. 如果没有字符位置数据，尝试触发它的创建
    if (!window.autoReader.charPositions || window.autoReader.charPositions.length === 0) {
      console.log("未找到字符位置数据，尝试强制创建...");

      // 直接使用scanTextPositionsSimple函数
      scanTextPositionsSimple();
    }

    return window.autoReader.charPositions && window.autoReader.charPositions.length > 0;
  }

  // 简单的文本位置扫描函数
  function scanTextPositionsSimple() {
    console.log("使用简单文本扫描...");

    window.autoReader.charPositions = [];
    const contentElement = document.querySelector('.page-content');
    if (!contentElement) {
      console.error("找不到内容容器");
      return;
    }

    // 递归处理文本节点
    function processTextNodes(element) {
      if (element.nodeType === Node.TEXT_NODE) {
        const text = element.textContent;
        if (!text || text.trim() === '') return;

        // 获取元素的位置信息
        const range = document.createRange();

        // 为每个字符创建一个独立的范围
        for (let i = 0; i < text.length; i++) {
          try {
            range.setStart(element, i);
            range.setEnd(element, i + 1);

            const rect = range.getBoundingClientRect();
            if (rect.width > 0 && rect.height > 0) {
              window.autoReader.charPositions.push({
                char: text[i],
                x: rect.left + rect.width/2,
                y: rect.top + rect.height/2,
                element: element.parentElement
              });
            }
          } catch (e) {
            console.warn("无法处理字符", e);
          }
        }
      } else if (element.nodeType === Node.ELEMENT_NODE) {
        // 递归处理子元素
        for (const child of element.childNodes) {
          processTextNodes(child);
        }
      }
    }

    processTextNodes(contentElement);
    console.log("扫描完成，找到字符数量:", window.autoReader.charPositions.length);
  }

  return (
    <div className="App">
      <div className="sidebar">
        <div className="logo">
          <h2>ADHD阅读助手</h2>
        </div>
        <label className="upload-btn">
          选择文件
          <input
            type="file"
