import React, { useState, useRef, useEffect } from 'react';
import * as pdfjsLib from 'pdfjs-dist';
import * as pdfjsViewer from 'pdfjs-dist/web/pdf_viewer';
import { marked } from 'marked'; // 需要安装: npm install marked
import './styles/Reader.css';
import { pdfjs } from 'pdfjs-dist';

// 设置 PDF.js worker
pdfjsLib.GlobalWorkerOptions.workerSrc = `//cdnjs.cloudflare.com/ajax/libs/pdf.js/${pdfjsLib.version}/pdf.worker.min.js`;

// 模拟NLP词性标注功能（实际应用中建议使用专业NLP库如nlp.js或调用API）
const tagPartsOfSpeech = (text) => {
  // 定义所有标点符号
  const punctuationMarks = [
    '，', '。', '！', '？', '；', '：', '（', '）', '【', '】', '《', '》', '〈', '〉', '…', '—', '～', '·',
    ',', '.', '!', '?', ';', ':', '(', ')', '[', ']', '{', '}', '-', '...', '..'
  ];

  // 将文本按标点符号分割，保留标点符号
  const segments = text.split(new RegExp(`([${punctuationMarks.map(mark => '\\' + mark).join('')}])`));
  let result = '';
  let colorIndex = 0; // 0: 红色, 1: 蓝色, 2: 黑色
  let currentText = '';

  segments.forEach((segment, index) => {
    // 跳过空字符串和纯空格
    if (!segment || /^\s+$/.test(segment)) {
      result += segment;
      return;
    }

    // 检查是否是标点符号
    const isPunctuation = punctuationMarks.includes(segment);

    // 根据索引选择颜色
    let color;
    switch (colorIndex) {
      case 0:
        color = 'red';
        break;
      case 1:
        color = 'blue';
        break;
      case 2:
        color = 'black';
        break;
      default:
        color = 'black';
    }

    // 将当前文本和标点符号放在同一个颜色块中
    if (isPunctuation) {
      // 如果有累积的文本，先添加文本
      if (currentText) {
        result += `<span class="highlight-word ${color}">${currentText}</span>`;
        currentText = '';
      }
      // 添加标点符号
      result += `<span class="highlight-word ${color}">${segment}</span>`;
      // 切换颜色
      colorIndex = (colorIndex + 1) % 3;
    } else {
      // 累积文本
      currentText += segment;
    }
  });

  // 处理最后可能剩余的文本
  if (currentText) {
    const color = colorIndex === 0 ? 'red' : colorIndex === 1 ? 'blue' : 'black';
    result += `<span class="highlight-word ${color}">${currentText}</span>`;
  }

  return result;
};

const App = () => {
  // 状态定义
  const [pages, setPages] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const [totalPages, setTotalPages] = useState(0);
  const [fileName, setFileName] = useState('');
  const [loading, setLoading] = useState(false);
  const fileRef = useRef(null);
  const [file, setFile] = useState(null);
  const [pdfInstance, setPdfInstance] = useState(null);
  const [extractedTextPages, setExtractedTextPages] = useState([]);
  const [readingMode, setReadingMode] = useState('normal');
  const [posMode, setPosMode] = useState('none');
  const [markdownContent, setMarkdownContent] = useState('');
  const readerContainerRef = useRef(null);
  const autoReadGuideRef = useRef(null);
  const autoReadIntervalRef = useRef(null);
  const [content, setContent] = useState('');
  const [pdfDocument, setPdfDocument] = useState(null);

  // 初始化PDF.js
  useEffect(() => {
    pdfjsLib.GlobalWorkerOptions.workerSrc = `https://cdnjs.cloudflare.com/ajax/libs/pdf.js/${pdfjsLib.version}/pdf.worker.min.js`;

    return () => {
      // 清理自动阅读定时器
      if (autoReadIntervalRef.current) {
        clearInterval(autoReadIntervalRef.current);
      }
    };
  }, []);

  // 当阅读模式改变时应用效果
  useEffect(() => {
    if (readingMode === 'auto-guide') {
      startAutoReadGuide();
    } else if (autoReadIntervalRef.current) {
      clearInterval(autoReadIntervalRef.current);
    }
  }, [readingMode, markdownContent]);

  // 修改PDF处理函数，确保内容正确提取和显示
  const handlePdfFile = async (file) => {
    try {
      const fileURL = URL.createObjectURL(file);
      const loadingTask = pdfjsLib.getDocument(fileURL);
      const pdf = await loadingTask.promise;

      setPdfDocument(pdf);
      setTotalPages(pdf.numPages);
      setCurrentPage(1);

      // 加载第一页
      await extractAndProcessPage(pdf, 1);

      // 添加调试信息
      console.log('PDF加载成功，页数:', pdf.numPages);
    } catch (error) {
      console.error('加载PDF时出错:', error);
      alert('PDF加载失败，请尝试其他文件');
    }
  };

  // 提取和处理PDF页面内容的函数 - 区分目录和正文页面
  const extractAndProcessPage = async (pdf, pageNum) => {
    try {
      const page = await pdf.getPage(pageNum);
      const textContent = await page.getTextContent();
      const viewport = page.getViewport({ scale: 1.0 });

      // 判断当前页面是否为目录页
      const isTableOfContents = await detectIfTocPage(textContent);

      if (isTableOfContents) {
        // 处理目录页面 - 保持原布局，仅调整字间距
        await processTocPage(textContent, viewport);
      } else {
        // 处理正文页面 - 以句号为标准划分段落
        await processContentPage(page, readingMode);
      }

      // 在内容处理完成后，如果是鼠标跟随模式，添加监听器
      if (readingMode === 'follow-mouse') {
        setTimeout(() => {
          addMouseFollowListeners();
        }, 50);
      }

      return true; // 返回成功标志
    } catch (error) {
      console.error('处理PDF页面时出错:', error);
      setContent('<div>无法处理PDF内容</div>');
      return false;
    }
  };

  // 检测是否为目录页
  const detectIfTocPage = async (textContent) => {
    const textItems = textContent.items;
    let fullText = textItems.map(item => item.str).join('');

    // 检查特征: 
    // 1. 页面上包含"目录"字样
    // 2. 行尾通常有页码和前导符（...）
    // 3. 短行居多，且多有规律的缩进
    const hasTocKeywords = /目\s*录|contents|index/i.test(fullText);
    const hasPageNumberPatterns = /\.{2,}|…{1,}\s*\d+/.test(fullText);

    // 检查行的特征
    let shortLinesCount = 0;
    let linesWithNumbersCount = 0;

    // 按Y坐标分组
    const lineMap = new Map();
    textItems.forEach(item => {
      const yKey = Math.round(item.transform[5]);
      if (!lineMap.has(yKey)) {
        lineMap.set(yKey, []);
      }
      lineMap.get(yKey).push(item);
    });

    const lines = [...lineMap.values()];

    lines.forEach(line => {
      const lineText = line.map(item => item.str).join('');
      if (lineText.length < 50) shortLinesCount++;
      if (/\d+$/.test(lineText)) linesWithNumbersCount++;
    });

    const shortLineRatio = shortLinesCount / lines.length;
    const numberLineRatio = linesWithNumbersCount / lines.length;

    return hasTocKeywords || hasPageNumberPatterns || (shortLineRatio > 0.7 && numberLineRatio > 0.3);
  };

  // 处理目录页面 - 严格保持原始布局
  const processTocPage = async (textContent, viewport) => {
    const textItems = textContent.items;

    // 生成HTML，严格保持原始位置和格式
    let htmlContent = '<div class="pdf-toc-page">';

    textItems.forEach(item => {
      // 获取原始位置信息
      const x = item.transform[4];
      const y = viewport.height - item.transform[5]; // 转换Y坐标

      // 获取原始字体信息
      const fontSize = Math.sqrt(item.transform[0] * item.transform[0] + item.transform[1] * item.transform[1]);
      const fontWeight = item.fontName && item.fontName.toLowerCase().includes('bold') ? 'bold' : 'normal';

      // 仅应用字间距，其他完全保持原样
      const spacedText = item.str.split('').join('\u200A');

      // 使用绝对定位和原始样式属性
      htmlContent += `<span style="
        position: absolute; 
        left: ${x}px; 
        top: ${y}px; 
        font-size: ${fontSize}px; 
        font-weight: ${fontWeight};
        letter-spacing: 0.3em;
        white-space: nowrap;
        font-family: inherit;
        color: inherit;
      ">${spacedText}</span>`;
    });

    htmlContent += '</div>';
    setContent(htmlContent);
  };

  // 修改文本处理函数，实现按完整词随机高亮
  const processTextWithHighlight = (text) => {
    // 将文本按空格和标点符号分割成词
    const words = text.split(/([\s.,!?，。！？.!?]+)/);
    let result = '';
    let lastColor = null; // 记录上一个高亮颜色

    words.forEach(word => {
      // 跳过空字符串和纯空格
      if (!word || /^\s+$/.test(word)) {
        result += word;
        return;
      }

      // 增加高亮概率到80%，确保大部分文本被高亮
      if (Math.random() < 0.8) {
        // 确保相邻高亮词颜色不同
        let color;
        if (lastColor === 'red') {
          color = 'blue';
        } else if (lastColor === 'blue') {
          color = 'red';
        } else {
          // 第一次高亮时随机选择颜色
          color = Math.random() < 0.5 ? 'red' : 'blue';
        }
        lastColor = color;

        result += `<span class="highlight-word ${color}">${word}</span>`;
      } else {
        result += word;
      }
    });

    return result;
  };

  // 修改processContentPage函数
  const processContentPage = async (page, mode) => {
    try {
      const textContent = await page.getTextContent();
      const textItems = textContent.items;
      let extractedText = textItems.map(item => item.str).join(' ');

      // 移除多余的空格
      extractedText = extractedText.replace(/\s+/g, ' ').trim();

      // 根据阅读模式处理文本
      let processedContent = '';
      switch(mode) {
        case 'normal':
          // 普通模式 - 简单分段显示
          processedContent = formatTextToHtml(extractedText);
          break;
        case 'static':
          // 静态高亮模式 - 应用词性标注和颜色高亮
          processedContent = applyStaticHighlight(extractedText);
          break;
        case 'follow-mouse':
          // 鼠标跟随高亮模式 - 分段落但不预先高亮，由鼠标事件触发高亮
          processedContent = formatTextForMouseFollow(extractedText);
          break;
        default:
          processedContent = formatTextToHtml(extractedText);
      }

      setContent(processedContent);
    } catch (error) {
      console.error('处理内容页面时出错:', error);
      setContent('<div>无法处理PDF内容</div>');
    }
  };

  // 处理TXT文件
  const handleTxtFile = async (file) => {
    try {
      setLoading(true);
      const text = await file.text();
      // 将文本分页处理
      const pageTexts = text.split('\n\n').map(paragraph => [{
        text: paragraph,
        x: 40,
        y: 40,
        fontSize: 16,
        fontFamily: 'serif'
      }]);
      setPages(pageTexts);
      setTotalPages(pageTexts.length);
      setCurrentPage(1);
      setLoading(false);
    } catch (error) {
      console.error('TXT处理错误:', error);
      setLoading(false);
      alert('TXT文件处理失败，请重试');
    }
  };

  // 文件上传处理
  const handleFileUpload = async (e) => {
    const file = e.target.files[0];
    if (!file) return;

    if (file.type === 'application/pdf') {
      await handlePdfFile(file);
    } else if (file.type === 'text/plain') {
      handleTxtFile(file);
    } else {
      alert('请上传PDF或TXT文件');
    }
  };

  // 翻页函数
  const changePage = async (delta) => {
    const newPage = currentPage + delta;
    if (newPage >= 1 && newPage <= totalPages && pdfDocument) {
      setCurrentPage(newPage);
      await extractAndProcessPage(pdfDocument, newPage);
    }
  };

  // 设置鼠标跟随高亮
  const setupMouseFollowHighlight = () => {
    const pageContent = document.querySelector('.pdf-content');
    if (!pageContent) return;

    pageContent.addEventListener('mousemove', (e) => {
      if (readingMode !== 'follow-mouse') return;

      // 清除之前的高亮
      const prevHighlighted = pageContent.querySelectorAll('.reading-line');
      prevHighlighted.forEach(el => el.classList.remove('reading-line'));

      // 获取鼠标所在的行
      const lines = pageContent.querySelectorAll('.text-line');
      lines.forEach(line => {
        const rect = line.getBoundingClientRect();
        if (e.clientY >= rect.top && e.clientY <= rect.bottom) {
          line.classList.add('reading-line');
        }
      });
    });
  };

  // 自动阅读引导
  const startAutoReadGuide = () => {
    if (autoReadIntervalRef.current) {
      clearInterval(autoReadIntervalRef.current);
    }

    // 创建阅读指示点
    if (!autoReadGuideRef.current) {
      const guide = document.createElement('div');
      guide.className = 'auto-read-guide';
      document.querySelector('.page-content').appendChild(guide);
      autoReadGuideRef.current = guide;
    }

    // 获取页面内所有段落
    const paragraphs = document.querySelectorAll('.page-content p');
    if (!paragraphs.length) return;

    let currentParagraphIndex = 0;
    let currentCharIndex = 0;

    autoReadIntervalRef.current = setInterval(() => {
      if (currentParagraphIndex >= paragraphs.length) {
        clearInterval(autoReadIntervalRef.current);
        return;
      }

      const paragraph = paragraphs[currentParagraphIndex];
      const text = paragraph.textContent;

      if (currentCharIndex >= text.length) {
        currentParagraphIndex++;
        currentCharIndex = 0;
        return;
      }

      // 计算当前字符位置
      const range = document.createRange();
      const textNode = paragraph.firstChild;

      if (textNode && textNode.nodeType === Node.TEXT_NODE) {
        range.setStart(textNode, Math.min(currentCharIndex, textNode.length));
        range.setStart(textNode, Math.min(currentCharIndex, textNode.length));

        const rect = range.getBoundingClientRect();

        // 更新指示点位置
        if (autoReadGuideRef.current) {
          const guide = autoReadGuideRef.current;
          guide.style.left = `${rect.left}px`;
          guide.style.top = `${rect.bottom + 5}px`;

          // 闪烁效果
          guide.style.opacity = 1;
          setTimeout(() => {
            guide.style.opacity = 0.5;
          }, 100);
        }
      }

      currentCharIndex += 1;
    }, 200); // 每秒5个字符的速度
  };

  // 处理阅读模式改变
  const handleReadingModeChange = (e) => {
    // 获取当前和新模式
    const oldMode = readingMode;
    const newMode = e.target.value;

    console.log(`切换阅读模式: ${oldMode} -> ${newMode}`);

    // 立即设置新的阅读模式
    setReadingMode(newMode);

    // 彻底清理所有DOM元素的效果和类
    const cleanupAllEffects = () => {
      console.log('彻底清理所有DOM效果...');

      // 1. 移除所有可能的高亮元素
      const highlights = document.querySelectorAll('.mouse-highlight, .line-highlight, .highlight-word, .reading-line');
      if (highlights.length > 0) {
        console.log(`清理 ${highlights.length} 个高亮元素`);
        highlights.forEach(el => el.remove());
      }

      // 2. 移除所有事件监听器
      const contentArea = document.querySelector('.page-content');
      if (contentArea) {
        contentArea.removeEventListener('mousemove', handleMouseMove);
        contentArea.removeEventListener('mouseleave', clearAllHighlights);

        // 3. 断开MutationObserver
        if (contentArea._highlightObserver) {
          contentArea._highlightObserver.disconnect();
          delete contentArea._highlightObserver;
        }

        // 4. 移除内容区域的所有特殊类
        const pdfContent = contentArea.querySelector('.pdf-content');
        if (pdfContent) {
          pdfContent.classList.remove('mouse-follow-content');
        }
      }

      // 5. 重置所有段落，移除事件监听器
      const paragraphs = document.querySelectorAll('.pdf-paragraph');
      if (paragraphs.length > 0) {
        console.log(`重置 ${paragraphs.length} 个段落元素`);
        paragraphs.forEach(paragraph => {
          // 替换段落元素以清除所有事件监听器
          if (paragraph.parentNode) {
            const newParagraph = paragraph.cloneNode(true);
            paragraph.parentNode.replaceChild(newParagraph, paragraph);
          }
        });
      }
    };

    // 如果有PDF文档且有当前页面
    if (pdfDocument && currentPage > 0) {
      // 立即处理当前页面
      (async () => {
        try {
          console.log(`立即处理当前页面 ${currentPage}...`);

          // 先彻底清理所有效果
          cleanupAllEffects();

          // 获取当前页面的文本内容
          const page = await pdfDocument.getPage(currentPage);
          const textContent = await page.getTextContent();
          const textItems = textContent.items;
          let extractedText = textItems.map(item => item.str).join(' ');

          // 移除多余的空格
          extractedText = extractedText.replace(/\s+/g, ' ').trim();

          // 根据新模式处理文本内容
          let processedContent = '';
          switch(newMode) {
            case 'normal':
              processedContent = formatTextToHtml(extractedText);
              break;
            case 'static':
              processedContent = applyStaticHighlight(extractedText);
              break;
            case 'follow-mouse':
              processedContent = formatTextForMouseFollow(extractedText);
              break;
            default:
              processedContent = formatTextToHtml(extractedText);
          }

          // 设置内容
          setContent(processedContent);

          // 在内容更新后添加适当的监听器
          setTimeout(() => {
            if (newMode === 'follow-mouse') {
              console.log('添加鼠标跟随监听器...');
              addMouseFollowListeners();
            }
          }, 50);

          console.log(`模式 ${newMode} 已成功应用`);
        } catch (error) {
          console.error('模式切换处理错误:', error);
        }
      })();
    } else {
      // 如果没有加载PDF，只清理效果
      cleanupAllEffects();
    }
  };

  // 添加鼠标跟随事件监听器
  const addMouseFollowListeners = () => {
    // 确保只在鼠标跟随模式下添加监听器
    if (readingMode !== 'follow-mouse') {
      console.log('当前不是鼠标跟随模式，不添加监听器');
      return;
    }

    console.log('开始添加鼠标跟随监听器...');

    const contentArea = document.querySelector('.page-content');
    if (!contentArea) {
      console.log('找不到内容区域，无法添加监听器');
      return;
    }

    // 先移除可能存在的监听器
    contentArea.removeEventListener('mousemove', handleMouseMove);
    contentArea.removeEventListener('mouseleave', clearAllHighlights);

    // 添加新的事件监听器
    contentArea.addEventListener('mousemove', handleMouseMove);
    contentArea.addEventListener('mouseleave', clearAllHighlights);

    // 为所有段落添加相对定位
    const setupParagraphs = () => {
      const paragraphs = document.querySelectorAll('.pdf-paragraph');
      if (paragraphs.length === 0) {
        console.log('找不到段落元素');
        return;
      }

      console.log(`处理 ${paragraphs.length} 个段落元素`);

      paragraphs.forEach(paragraph => {
        // 确保段落有相对定位
        paragraph.style.position = 'relative';

        // 移除现有的事件监听
        const newParagraph = paragraph.cloneNode(true);
        if (paragraph.parentNode) {
          paragraph.parentNode.replaceChild(newParagraph, paragraph);
        }

        // 添加新的离开事件监听
        newParagraph.addEventListener('mouseleave', (e) => {
          if (!newParagraph.contains(e.relatedTarget)) {
            const highlights = newParagraph.querySelectorAll('.line-highlight');
            highlights.forEach(h => h.remove());
          }
        });
      });
    };

    // 立即设置段落样式和监听器
    setupParagraphs();

    // 确保之前的observer已断开
    if (contentArea._highlightObserver) {
      contentArea._highlightObserver.disconnect();
    }

    // 设置新的MutationObserver
    const observer = new MutationObserver(() => {
      if (readingMode === 'follow-mouse') {
        setupParagraphs();
      }
    });

    // 观察内容变化
    observer.observe(contentArea, { childList: true, subtree: true });
    contentArea._highlightObserver = observer;

    // 添加鼠标跟随内容类
    const pdfContent = contentArea.querySelector('.pdf-content');
    if (pdfContent && !pdfContent.classList.contains('mouse-follow-content')) {
      pdfContent.classList.add('mouse-follow-content');
    }

    console.log('鼠标跟随监听器设置完成');
  };

  // 处理鼠标移动，创建行高亮
  const handleMouseMove = (e) => {
    // 确保只在鼠标跟随模式下处理
    if (readingMode !== 'follow-mouse') return;

    // 获取鼠标下方元素
    const mouseTarget = document.elementFromPoint(e.clientX, e.clientY);
    if (!mouseTarget) return;

    // 检查是否在段落上
    if (mouseTarget.classList.contains('pdf-paragraph') || 
        mouseTarget.closest('.pdf-paragraph') ||
        mouseTarget.tagName === 'P' || 
        mouseTarget.tagName === 'SPAN') {

      // 获取目标段落
      const targetParagraph = mouseTarget.closest('.pdf-paragraph') || mouseTarget;
      if (!targetParagraph) return;

      // 计算相对位置和行索引
      const paragraphRect = targetParagraph.getBoundingClientRect();
      const relativeY = e.clientY - paragraphRect.top;

      // 获取行高
      const computedStyle = window.getComputedStyle(targetParagraph);
      const fontSize = parseInt(computedStyle.fontSize) || 16;
      const lineHeight = parseInt(computedStyle.lineHeight) || Math.round(fontSize * 1.5);
      const lineIndex = Math.floor(relativeY / lineHeight);

      // 检查当前是否已有高亮
      const highlightSelector = `.line-highlight[data-line-index="${lineIndex}"]`;
      const existingHighlight = targetParagraph.querySelector(highlightSelector);

      // 如果该行没有高亮，创建新高亮
      if (!existingHighlight) {
        // 移除同一段落中的其他高亮
        const otherHighlights = targetParagraph.querySelectorAll('.line-highlight');
        otherHighlights.forEach(h => h.remove());

        // 创建行高亮元素
        const highlight = document.createElement('div');
        highlight.className = 'line-highlight mouse-highlight';
        highlight.dataset.lineIndex = lineIndex;

        // 设置高亮样式
        Object.assign(highlight.style, {
          position: 'absolute',
          left: '0',
          top: `${lineIndex * lineHeight}px`,
          width: '100%',
          height: `${lineHeight}px`,
          backgroundColor: 'rgba(255, 140, 0, 0.3)',
          borderRadius: '4px',
          border: '1px solid rgba(255, 140, 0, 0.4)',
          boxShadow: '0 0 10px rgba(255, 140, 0, 0.5)',
          zIndex: '5',
          opacity: '1',
          pointerEvents: 'none'
        });

        // 确保段落有相对定位
        if (targetParagraph.style.position !== 'relative') {
          targetParagraph.style.position = 'relative';
        }

        // 添加高亮元素
        targetParagraph.appendChild(highlight);
      }
    }
  };

  // 清除所有高亮的辅助函数
  const clearAllHighlights = () => {
    // 清理所有类型的高亮
    const highlights = document.querySelectorAll('.mouse-highlight, .line-highlight, .highlight-word, .reading-line');
    highlights.forEach(el => el.remove());
  };

  // 在组件卸载时清除事件监听器
  useEffect(() => {
    return () => {
      // 使用现有的cleanupAllEffects函数替代removeMouseFollowListeners
      const cleanupAllEffects = () => {
        // 1. 移除所有可能的高亮元素
        const highlights = document.querySelectorAll('.mouse-highlight, .line-highlight, .highlight-word, .reading-line');
        highlights.forEach(el => el.remove());

        // 2. 移除所有事件监听器
        const contentArea = document.querySelector('.page-content');
        if (contentArea) {
          contentArea.removeEventListener('mousemove', handleMouseMove);
          contentArea.removeEventListener('mouseleave', clearAllHighlights);

          // 3. 断开MutationObserver
          if (contentArea._highlightObserver) {
            contentArea._highlightObserver.disconnect();
            delete contentArea._highlightObserver;
          }

          // 4. 移除内容区域的所有特殊类
          const pdfContent = contentArea.querySelector('.pdf-content');
          if (pdfContent) {
            pdfContent.classList.remove('mouse-follow-content');
          }
        }

        // 5. 重置所有段落，移除事件监听器
        const paragraphs = document.querySelectorAll('.pdf-paragraph');
        paragraphs.forEach(paragraph => {
          if (paragraph.parentNode) {
            const newParagraph = paragraph.cloneNode(true);
            paragraph.parentNode.replaceChild(newParagraph, paragraph);
          }
        });
      };

      cleanupAllEffects();
    };
  }, []);

  // 格式化文本为HTML
  const formatTextToHtml = (text) => {
    // 分段
    const paragraphs = text.split(/(?<=。|！|？|\.|\!|\?)\s+/).filter(p => p.trim());

    // 创建HTML内容
    let htmlContent = '<div class="pdf-content">';

    paragraphs.forEach(paragraph => {
      htmlContent += `<p class="pdf-paragraph">${paragraph}</p>`;
    });

    htmlContent += '</div>';
    return htmlContent;
  };

  // 应用静态高亮样式
  const applyStaticHighlight = (text) => {
    // 分段
    const paragraphs = text.split(/(?<=。|！|？|\.|\!|\?)\s+/).filter(p => p.trim());

    // 创建HTML内容
    let htmlContent = '<div class="pdf-content">';

    paragraphs.forEach(paragraph => {
      // 使用processTextWithHighlight函数处理段落
      htmlContent += `<p class="pdf-paragraph">${processTextWithHighlight(paragraph)}</p>`;
    });

    htmlContent += '</div>';
    return htmlContent;
  };

  // 格式化文本用于鼠标跟随高亮模式
  const formatTextForMouseFollow = (text) => {
    // 分段
    const paragraphs = text.split(/(?<=。|！|？|\.|\!|\?)\s+/).filter(p => p.trim());

    // 将文本包装在特殊类中以便于鼠标跟踪
    let htmlContent = '<div class="pdf-content mouse-follow-content">';

    paragraphs.forEach(paragraph => {
      // 为每个段落添加类以便于跟踪，但不添加任何高亮
      htmlContent += `<p class="pdf-paragraph" data-follow="true" style="position: relative;">${paragraph}</p>`;
    });

    htmlContent += '</div>';
    return htmlContent;
  };

  return (
    <div className="App">
      <div className="sidebar">
        <div className="logo">
          <h2>ADHD阅读助手</h2>
        </div>
        <label className="upload-btn">
          选择文件
          <input
            type="file"
            accept=".pdf,.txt"
            onChange={handleFileUpload}
            style={{ display: 'none' }}
          />
        </label>
      </div>
      <div className="main-content">
        <div className="header">
          <h1>ADHD阅读助手</h1>
          <div className="controls">
            <div className="mode-selector">
              <select value={readingMode} onChange={handleReadingModeChange}>
                <option value="normal">普通模式</option>
                <option value="static">静态高亮模式</option>
                <option value="follow-mouse">鼠标跟随高亮</option>
              </select>
            </div>
            <div className="mode-selector">
              <select value={posMode} onChange={(e) => setPosMode(e.target.value)}>
                <option value="none">无词性标注</option>
                <option value="noun">名词标注</option>
                <option value="all">全词性标注</option>
              </select>
            </div>
          </div>
        </div>
        <div className="page-content">
          {content ? (
            <div dangerouslySetInnerHTML={{ __html: content }} />
          ) : (
            <div className="empty-state">
              <p>请上传PDF或TXT文件开始阅读</p>
            </div>
          )}
        </div>
        {content && (
          <div className="nav-buttons">
            <button className="page-btn prev" onClick={() => changePage(-1)} disabled={currentPage === 1}>
              上一页
            </button>
            <span className="page-info">
              第 {currentPage} 页 / 共 {totalPages} 页
            </span>
            <button className="page-btn next" onClick={() => changePage(1)} disabled={currentPage === totalPages}>
              下一页
            </button>
          </div>
        )}
      </div>
    </div>
  );
};

export default App;
