import React, { useState, useRef, useEffect } from 'react';
import * as pdfjsLib from 'pdfjs-dist';
import * as pdfjsViewer from 'pdfjs-dist/web/pdf_viewer';
import { marked } from 'marked'; // 需要安装: npm install marked
import './styles/Reader.css';
import { pdfjs } from 'pdfjs-dist';

// 设置 PDF.js worker
pdfjsLib.GlobalWorkerOptions.workerSrc = `//cdnjs.cloudflare.com/ajax/libs/pdf.js/${pdfjsLib.version}/pdf.worker.min.js`;

// 模拟NLP词性标注功能（实际应用中建议使用专业NLP库如nlp.js或调用API）
const tagPartsOfSpeech = (text) => {
  // 定义所有标点符号
  const punctuationMarks = [
    '，', '。', '！', '？', '；', '：', '（', '）', '【', '】', '《', '》', '〈', '〉', '…', '—', '～', '·',
    ',', '.', '!', '?', ';', ':', '(', ')', '[', ']', '{', '}', '-', '...', '..'
  ];

  // 将文本按标点符号分割，保留标点符号
  const segments = text.split(new RegExp(`([${punctuationMarks.map(mark => '\\' + mark).join('')}])`));
  let result = '';
  let colorIndex = 0; // 0: 红色, 1: 蓝色, 2: 黑色
  let currentText = '';

  segments.forEach((segment, index) => {
    // 跳过空字符串和纯空格
    if (!segment || /^\s+$/.test(segment)) {
      result += segment;
      return;
    }

    // 检查是否是标点符号
    const isPunctuation = punctuationMarks.includes(segment);

    // 根据索引选择颜色
    let color;
    switch (colorIndex) {
      case 0:
        color = 'red';
        break;
      case 1:
        color = 'blue';
        break;
      default:
        color = 'black';
    }

    // 应用ADHD友好的字间距
    const spacedText = segment.split('').join('\u200A');

    // 将当前文本和标点符号放在同一个颜色块中
    if (isPunctuation) {
      // 如果有累积的文本，先添加文本
      if (currentText) {
        result += `<span class="highlight-word ${color}">${currentText.split('').join('\u200A')}</span>`;
        currentText = '';
      }
      // 添加标点符号
      result += `<span class="highlight-word ${color}">${spacedText}</span>`;
      // 切换颜色
      colorIndex = (colorIndex + 1) % 3;
    } else {
      // 累积文本
      currentText += segment;
    }
  });

  // 处理最后可能剩余的文本
  if (currentText) {
    const color = colorIndex === 0 ? 'red' : colorIndex === 1 ? 'blue' : 'black';
    result += `<span class="highlight-word ${color}">${currentText.split('').join('\u200A')}</span>`;
  }

  return result;
};

const App = () => {
  // 状态定义
  const [isDarkMode, setIsDarkMode] = useState(true);
  const [pages, setPages] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const [totalPages, setTotalPages] = useState(0);
  const [fileName, setFileName] = useState('');
  const [loading, setLoading] = useState(false);
  const fileRef = useRef(null); // 添加文件引用
  const [file, setFile] = useState(null);
  const [pdfInstance, setPdfInstance] = useState(null);
  const [extractedTextPages, setExtractedTextPages] = useState([]);
  const [readingMode, setReadingMode] = useState('static'); // static, follow-mouse, auto-guide
  const [highlightColor, setHighlightColor] = useState('#ffeb3b'); // 默认高亮颜色
  const [highlightPOS, setHighlightPOS] = useState(['noun']); // 默认高亮词性
  const [markdownContent, setMarkdownContent] = useState('');
  const readerContainerRef = useRef(null);
  const autoReadGuideRef = useRef(null);
  const autoReadIntervalRef = useRef(null);
  const [content, setContent] = useState('');
  const [pdfDocument, setPdfDocument] = useState(null);

  // 初始化PDF.js
  useEffect(() => {
    pdfjsLib.GlobalWorkerOptions.workerSrc = `https://cdnjs.cloudflare.com/ajax/libs/pdf.js/${pdfjsLib.version}/pdf.worker.min.js`;

    // 设置高亮颜色
    document.documentElement.style.setProperty('--highlight-color', highlightColor);

    return () => {
      // 清理自动阅读定时器
      if (autoReadIntervalRef.current) {
        clearInterval(autoReadIntervalRef.current);
      }
    };
  }, [highlightColor]);

  // 当阅读模式改变时应用效果
  useEffect(() => {
    if (readingMode === 'auto-guide') {
      startAutoReadGuide();
    } else if (autoReadIntervalRef.current) {
      clearInterval(autoReadIntervalRef.current);
    }
  }, [readingMode, markdownContent]);

  // 修改PDF处理函数，确保内容正确提取和显示
  const handlePdfFile = async (file) => {
    try {
      const fileURL = URL.createObjectURL(file);
      const loadingTask = pdfjsLib.getDocument(fileURL);
      const pdf = await loadingTask.promise;

      setPdfDocument(pdf);
      setTotalPages(pdf.numPages);
      setCurrentPage(1);

      // 加载第一页
      await extractAndProcessPage(pdf, 1);

      // 添加调试信息
      console.log('PDF加载成功，页数:', pdf.numPages);
    } catch (error) {
      console.error('加载PDF时出错:', error);
      alert('PDF加载失败，请尝试其他文件');
    }
  };

  // 提取和处理PDF页面内容的函数 - 区分目录和正文页面
  const extractAndProcessPage = async (pdf, pageNum) => {
    try {
      const page = await pdf.getPage(pageNum);
      const textContent = await page.getTextContent();
      const viewport = page.getViewport({ scale: 1.0 });

      // 判断当前页面是否为目录页
      const isTableOfContents = await detectIfTocPage(textContent);

      if (isTableOfContents) {
        // 处理目录页面 - 保持原布局，仅调整字间距
        await processTocPage(textContent, viewport);
      } else {
        // 处理正文页面 - 以句号为标准划分段落
        await processContentPage(page, readingMode);
      }
    } catch (error) {
      console.error('处理PDF页面时出错:', error);
      setContent('<div>无法处理PDF内容</div>');
    }
  };

  // 检测是否为目录页
  const detectIfTocPage = async (textContent) => {
    const textItems = textContent.items;
    let fullText = textItems.map(item => item.str).join('');

    // 检查特征: 
    // 1. 页面上包含"目录"字样
    // 2. 行尾通常有页码和前导符（...）
    // 3. 短行居多，且多有规律的缩进
    const hasTocKeywords = /目\s*录|contents|index/i.test(fullText);
    const hasPageNumberPatterns = /\.{2,}|…{1,}\s*\d+/.test(fullText);

    // 检查行的特征
    let shortLinesCount = 0;
    let linesWithNumbersCount = 0;

    // 按Y坐标分组
    const lineMap = new Map();
    textItems.forEach(item => {
      const yKey = Math.round(item.transform[5]);
      if (!lineMap.has(yKey)) {
        lineMap.set(yKey, []);
      }
      lineMap.get(yKey).push(item);
    });

    const lines = [...lineMap.values()];

    lines.forEach(line => {
      const lineText = line.map(item => item.str).join('');
      if (lineText.length < 50) shortLinesCount++;
      if (/\d+$/.test(lineText)) linesWithNumbersCount++;
    });

    const shortLineRatio = shortLinesCount / lines.length;
    const numberLineRatio = linesWithNumbersCount / lines.length;

    return hasTocKeywords || hasPageNumberPatterns || (shortLineRatio > 0.7 && numberLineRatio > 0.3);
  };

  // 处理目录页面 - 严格保持原始布局
  const processTocPage = async (textContent, viewport) => {
    const textItems = textContent.items;

    // 生成HTML，严格保持原始位置和格式
    let htmlContent = '<div class="pdf-toc-page">';

    textItems.forEach(item => {
      // 获取原始位置信息
      const x = item.transform[4];
      const y = viewport.height - item.transform[5]; // 转换Y坐标

      // 获取原始字体信息
      const fontSize = Math.sqrt(item.transform[0] * item.transform[0] + item.transform[1] * item.transform[1]);
      const fontWeight = item.fontName && item.fontName.toLowerCase().includes('bold') ? 'bold' : 'normal';

      // 仅应用字间距，其他完全保持原样
      const spacedText = item.str.split('').join('\u200A');

      // 使用绝对定位和原始样式属性
      htmlContent += `<span style="
        position: absolute; 
        left: ${x}px; 
        top: ${y}px; 
        font-size: ${fontSize}px; 
        font-weight: ${fontWeight};
        letter-spacing: 0.3em;
        white-space: nowrap;
        font-family: inherit;
        color: inherit;
      ">${spacedText}</span>`;
    });

    htmlContent += '</div>';
    setContent(htmlContent);
  };

  // 修改文本处理函数，实现按完整词随机高亮
  const processTextWithHighlight = (text) => {
    // 将文本按空格和标点符号分割成词
    const words = text.split(/([\s.,!?，。！？.!?]+)/);
    let result = '';
    let lastColor = null; // 记录上一个高亮颜色

    words.forEach(word => {
      // 跳过空字符串和纯空格
      if (!word || /^\s+$/.test(word)) {
        result += word;
        return;
      }

      // 增加高亮概率到60%
      if (Math.random() < 0.6) {
        // 确保相邻高亮词颜色不同
        let color;
        if (lastColor === 'red') {
          color = 'blue';
        } else if (lastColor === 'blue') {
          color = 'red';
        } else {
          // 第一次高亮时随机选择颜色
          color = Math.random() < 0.5 ? 'red' : 'blue';
        }
        lastColor = color;

        result += `<span class="highlight-word ${color}">${word}</span>`;
      } else {
        result += word;
      }
    });

    return result;
  };

  // 修改processContentPage函数
  const processContentPage = async (page, mode) => {
    try {
      const textContent = await page.getTextContent();
      const viewport = page.getViewport({ scale: 1.0 });

      // 生成HTML内容
      let htmlContent = '<div class="pdf-content">';

      // 将文本项按视觉顺序排列（从上到下，从左到右）
      const sortedItems = [...textContent.items].sort((a, b) => {
        if (Math.abs(b.transform[5] - a.transform[5]) < 5) {
          return a.transform[4] - b.transform[4];
        }
        return b.transform[5] - a.transform[5];
      });

      // 构建完整文本
      let fullText = sortedItems.map(item => item.str).join('');

      // 使用正则表达式按句号（和其他句尾标点）分割文本成段落
      const sentenceRegex = /([^。！？.!?]+[。！？.!?])/g;
      const sentences = fullText.match(sentenceRegex) || [fullText];

      // 处理每个句子
      sentences.forEach(sentence => {
        // 应用ADHD友好的字间距
        const spacedText = sentence.split('').join('\u200A');

        // 根据阅读模式处理文本
        let processedText = spacedText;
        if (mode === 'static') {
          // 只在静息模式下应用高亮效果
          processedText = tagPartsOfSpeech(spacedText);
        }

        // 添加段落，设置首行缩进
        htmlContent += `<p class="pdf-paragraph">${processedText}</p>`;
      });

      htmlContent += '</div>';
      setContent(htmlContent);
    } catch (error) {
      console.error('处理PDF页面时出错:', error);
      setContent('<div>无法处理PDF内容</div>');
    }
  };

  // 处理TXT文件
  const handleTxtFile = async (file) => {
    try {
      setLoading(true);
      const text = await file.text();
      // 将文本分页处理
      const pageTexts = text.split('\n\n').map(paragraph => [{
        text: paragraph,
        x: 40,
        y: 40,
        fontSize: 16,
        fontFamily: 'serif'
      }]);
      setPages(pageTexts);
      setTotalPages(pageTexts.length);
      setCurrentPage(1);
      setLoading(false);
    } catch (error) {
      console.error('TXT处理错误:', error);
      setLoading(false);
      alert('TXT文件处理失败，请重试');
    }
  };

  // 文件上传处理
  const handleFileUpload = async (e) => {
    const file = e.target.files[0];
    if (!file) return;

    if (file.type === 'application/pdf') {
      await handlePdfFile(file);
    } else if (file.type === 'text/plain') {
      handleTxtFile(file);
    } else {
      alert('请上传PDF或TXT文件');
    }
  };

  // 翻页函数
  const changePage = async (delta) => {
    const newPage = currentPage + delta;
    if (newPage >= 1 && newPage <= totalPages && pdfDocument) {
      setCurrentPage(newPage);
      await extractAndProcessPage(pdfDocument, newPage);
    }
  };

  // 设置鼠标跟随高亮
  const setupMouseFollowHighlight = () => {
    const pageContent = document.querySelector('.pdf-content');
    if (!pageContent) return;

    pageContent.addEventListener('mousemove', (e) => {
      if (readingMode !== 'follow-mouse') return;

      // 清除之前的高亮
      const prevHighlighted = pageContent.querySelectorAll('.reading-line');
      prevHighlighted.forEach(el => el.classList.remove('reading-line'));

      // 获取鼠标所在的行
      const lines = pageContent.querySelectorAll('.text-line');
      lines.forEach(line => {
        const rect = line.getBoundingClientRect();
        if (e.clientY >= rect.top && e.clientY <= rect.bottom) {
          line.classList.add('reading-line');
        }
      });
    });
  };

  // 自动阅读引导
  const startAutoReadGuide = () => {
    if (autoReadIntervalRef.current) {
      clearInterval(autoReadIntervalRef.current);
    }

    // 创建阅读指示点
    if (!autoReadGuideRef.current) {
      const guide = document.createElement('div');
      guide.className = 'auto-read-guide';
      document.querySelector('.page-content').appendChild(guide);
      autoReadGuideRef.current = guide;
    }

    // 获取页面内所有段落
    const paragraphs = document.querySelectorAll('.page-content p');
    if (!paragraphs.length) return;

    let currentParagraphIndex = 0;
    let currentCharIndex = 0;

    autoReadIntervalRef.current = setInterval(() => {
      if (currentParagraphIndex >= paragraphs.length) {
        clearInterval(autoReadIntervalRef.current);
        return;
      }

      const paragraph = paragraphs[currentParagraphIndex];
      const text = paragraph.textContent;

      if (currentCharIndex >= text.length) {
        currentParagraphIndex++;
        currentCharIndex = 0;
        return;
      }

      // 计算当前字符位置
      const range = document.createRange();
      const textNode = paragraph.firstChild;

      if (textNode && textNode.nodeType === Node.TEXT_NODE) {
        range.setStart(textNode, Math.min(currentCharIndex, textNode.length));
        range.setStart(textNode, Math.min(currentCharIndex, textNode.length));

        const rect = range.getBoundingClientRect();

        // 更新指示点位置
        if (autoReadGuideRef.current) {
          const guide = autoReadGuideRef.current;
          guide.style.left = `${rect.left}px`;
          guide.style.top = `${rect.bottom + 5}px`;

          // 闪烁效果
          guide.style.opacity = 1;
          setTimeout(() => {
            guide.style.opacity = 0.5;
          }, 100);
        }
      }

      currentCharIndex += 1;
    }, 200); // 每秒5个字符的速度
  };

  // 处理阅读模式切换
  const handleReadingModeChange = (e) => {
    const newMode = e.target.value;
    setReadingMode(newMode);

    // 清除所有高亮效果
    const pageContent = document.querySelector('.pdf-content');
    if (pageContent) {
      // 移除所有高亮类
      pageContent.querySelectorAll('.highlight, .reading-line, .noun-highlight').forEach(el => {
        el.classList.remove('highlight', 'reading-line', 'noun-highlight');
      });

      // 重新处理当前文本内容
      const paragraphs = pageContent.querySelectorAll('.pdf-paragraph');
      paragraphs.forEach(paragraph => {
        // 获取原始文本（移除所有HTML标签和字间距）
        const originalText = paragraph.textContent.replace(/\u200A/g, '');
        if (newMode === 'static') {
          // 只在静息模式下应用高亮效果
          paragraph.innerHTML = tagPartsOfSpeech(originalText);
        } else {
          // 其他模式下只应用ADHD友好的字间距
          paragraph.innerHTML = originalText.split('').join('\u200A');
        }
      });
    }

    // 如果是鼠标跟随模式，设置延迟后添加跟随效果
    if (newMode === 'follow-mouse') {
      setTimeout(() => {
        setupMouseFollowHighlight();
      }, 100);
    }
  };

  useEffect(() => {
    // 当阅读模式为follow-mouse时，设置鼠标跟随高亮
    if (readingMode === 'follow-mouse' && content) {
      setTimeout(setupMouseFollowHighlight, 100);
    }
  }, [readingMode, content]);

  return (
    <div className={`App ${isDarkMode ? 'dark' : 'light'}`}>
      <div className="app-container">
        <header className="header">
          <h1>ADHD 阅读助手</h1>
          <div className="controls">
            <button 
              className="upload-btn"
              onClick={() => document.getElementById('fileInput').click()}
            >
              选择文件
            </button>
            <input
              id="fileInput"
              type="file"
              accept=".pdf,.txt"
              style={{ display: 'none' }}
              onChange={handleFileUpload}
            />
            <button className="mode-btn">
              切换明亮模式
            </button>

            {/* 高亮颜色选择 */}
            <div className="color-picker">
              <label>高亮颜色:</label>
              <input 
                type="color" 
                value={highlightColor}
                onChange={(e) => setHighlightColor(e.target.value)}
              />
            </div>

            {/* 阅读模式选择 */}
            <div className="mode-selector">
              <select 
                value={readingMode}
                onChange={handleReadingModeChange}
              >
                <option value="static">高亮静息模式</option>
                <option value="follow-mouse">鼠标跟随高亮</option>
                <option value="auto-guide">自动阅读引导</option>
              </select>
            </div>

            {/* 词性选择 */}
            <div className="pos-selector">
              <select
                value={highlightPOS}
                onChange={(e) => {
                  const options = Array.from(e.target.selectedOptions, option => option.value);
                  setHighlightPOS(options);
                }}
                multiple
              >
                <option value="noun">名词</option>
                <option value="verb">动词</option>
                <option value="adj">形容词</option>
              </select>
            </div>
          </div>
        </header>

        <main className="page-content">
          <div className="page-layout">
            {content ? (
              <div dangerouslySetInnerHTML={{ __html: content }} />
            ) : (
              <div>请上传PDF或TXT文件</div>
            )}

            {totalPages > 1 && (
              <div className="nav-buttons">
                <button 
                  className="nav-button" 
                  onClick={() => changePage(-1)} 
                  disabled={currentPage <= 1}
                >
                  上一页
                </button>
                <span>第 {currentPage} / {totalPages} 页</span>
                <button 
                  className="nav-button" 
                  onClick={() => changePage(1)} 
                  disabled={currentPage >= totalPages}
                >
                  下一页
                </button>
              </div>
            )}
          </div>
        </main>
      </div>
    </div>
  );
};

export default App;
