import React, { useState, useRef } from 'react';
import * as pdfjsLib from 'pdfjs-dist';
import * as pdfjsViewer from 'pdfjs-dist/web/pdf_viewer';
import './styles/Reader.css';
import { pdfjs } from 'pdfjs-dist';

// 设置 PDF.js worker
pdfjsLib.GlobalWorkerOptions.workerSrc = `//cdnjs.cloudflare.com/ajax/libs/pdf.js/${pdfjsLib.version}/pdf.worker.min.js`;

const App = () => {
  // 状态定义
  const [isDarkMode, setIsDarkMode] = useState(true);
  const [pages, setPages] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const [totalPages, setTotalPages] = useState(0);
  const [fileName, setFileName] = useState('');
  const [loading, setLoading] = useState(false);
  const fileRef = useRef(null); // 添加文件引用
  const [file, setFile] = useState(null);
  const [pdfInstance, setPdfInstance] = useState(null);

  // 处理PDF文件
  const handlePdfFile = async (file) => {
    try {
      const fileReader = new FileReader();
      fileReader.onload = async function() {
        const typedarray = new Uint8Array(this.result);
        const pdf = await pdfjsLib.getDocument(typedarray).promise;
        setPdfInstance(pdf);
        setTotalPages(pdf.numPages);
        renderPage(pdf, currentPage);
      };
      fileReader.readAsArrayBuffer(file);
    } catch (error) {
      console.error('Error:', error);
    }
  };

  const renderPage = async (pdf, pageNumber) => {
    try {
      const page = await pdf.getPage(pageNumber);

      // 增加缩放比例使文本更大且更宽
      const viewport = page.getViewport({ scale: 2.2 });

      const pageContainer = document.querySelector('.page-content');
      if (pageContainer) {
        pageContainer.innerHTML = '';

        // 创建页面容器
        const pageDiv = document.createElement('div');
        pageDiv.className = 'pdf-page';

        // 创建文本层
        const textLayerDiv = document.createElement('div');
        textLayerDiv.className = 'textLayer';
        textLayerDiv.style.width = `${viewport.width}px`;
        textLayerDiv.style.height = `${viewport.height}px`;

        // 获取文本内容
        const textContent = await page.getTextContent();

        // 使用PDF.js的文本层渲染器
        const renderTextLayer = pdfjsLib.renderTextLayer({
          textContent: textContent,
          container: textLayerDiv,
          viewport: viewport,
          textDivs: []
        });

        pageDiv.appendChild(textLayerDiv);
        pageContainer.appendChild(pageDiv);

        // 等待文本渲染完成后应用样式
        await renderTextLayer.promise;

        // 应用字间距
        const textElements = textLayerDiv.querySelectorAll('span');
        textElements.forEach(span => {
          const originalText = span.textContent;
          span.textContent = originalText.split('').join('\u2006\u2006\u2006');
          span.style.letterSpacing = '0.3em';
          span.style.color = '#000';
        });
      }
    } catch (error) {
      console.error('Error rendering page:', error);
    }
  };

  // 处理TXT文件
  const handleTxtFile = async (file) => {
    try {
      setLoading(true);
      const text = await file.text();
      // 将文本分页处理
      const pageTexts = text.split('\n\n').map(paragraph => [{
        text: paragraph,
        x: 40,
        y: 40,
        fontSize: 16,
        fontFamily: 'serif'
      }]);
      setPages(pageTexts);
      setTotalPages(pageTexts.length);
      setCurrentPage(1);
      setLoading(false);
    } catch (error) {
      console.error('TXT处理错误:', error);
      setLoading(false);
      alert('TXT文件处理失败，请重试');
    }
  };

  // 文件上传处理
  const handleFileUpload = async (event) => {
    const file = event.target.files[0];
    if (!file) return;

    if (file.size > 100 * 1024 * 1024) {
      alert('文件大小不能超过100MB');
      return;
    }

    setFileName(file.name);
    setPages([]); // 清空现有页面

    if (file.type === 'application/pdf') {
      await handlePdfFile(file);
    } else if (file.type === 'text/plain' || file.name.endsWith('.txt')) {
      await handleTxtFile(file);
    }
  };

  // 翻页函数
  const changePage = async (delta) => {
    const newPage = currentPage + delta;
    if (newPage >= 1 && newPage <= totalPages && pdfInstance) {
      setCurrentPage(newPage);
      renderPage(pdfInstance, newPage);
    }
  };

  return (
    <div className={`App ${isDarkMode ? 'dark' : 'light'}`}>
      <div className="app-container">
        <header className="header">
          <h1>ADHD 阅读助手</h1>
          <div className="controls">
            <button 
              className="upload-btn"
              onClick={() => document.getElementById('fileInput').click()}
            >
              选择文件
            </button>
            <input
              id="fileInput"
              type="file"
              accept=".pdf,.txt"
              style={{ display: 'none' }}
              onChange={(e) => {
                const file = e.target.files[0];
                if (file) {
                  setFile(file);
                  handlePdfFile(file);
                }
              }}
            />
            <button className="mode-btn">
              切换明亮模式
            </button>
          </div>
        </header>

        <div className="reader-container">
          <button 
            className="page-btn prev"
            onClick={() => changePage(-1)}
            disabled={currentPage <= 1}
          >
            ←
          </button>

          <div className="page-content">
            {!file && (
              <div className="upload-hint">
                请上传文件开始阅读
                <br />
                支持的格式: PDF, TXT (最大100MB)
              </div>
            )}
          </div>

          <button 
            className="page-btn next"
            onClick={() => changePage(1)}
            disabled={currentPage >= totalPages}
          >
            →
          </button>
        </div>

        {file && (
          <div className="page-info">
            第 {currentPage} 页 / 共 {totalPages} 页
          </div>
        )}
      </div>
    </div>
  );
};

export default App;
