import React, { useState } from 'react';
import * as pdfjsLib from 'pdfjs-dist';
import './styles/Reader.css';

// 确保使用正确的 worker 版本
const workerSrc = require('pdfjs-dist/build/pdf.worker.entry');
pdfjsLib.GlobalWorkerOptions.workerSrc = workerSrc;

function App() {
  const [isDarkMode, setIsDarkMode] = useState(true);
  const [content, setContent] = useState('');
  const [currentPage, setCurrentPage] = useState(1);
  const [totalPages, setTotalPages] = useState(0);
  const [fileName, setFileName] = useState('');
  const [loading, setLoading] = useState(false);

  // 处理PDF文件
  const handlePdfFile = async (file) => {
    try {
      setLoading(true);
      const fileArrayBuffer = await file.arrayBuffer();
      const pdf = await pdfjsLib.getDocument({
        data: fileArrayBuffer,
      }).promise;

      setTotalPages(pdf.numPages);

      // 读取第一页
      const page = await pdf.getPage(1);
      const textContent = await page.getTextContent();
      const textItems = textContent.items.map(item => item.str);
      const text = textItems.join(' ');
      
      setContent(text);
      setLoading(false);
    } catch (error) {
      console.error('PDF处理错误:', error);
      setLoading(false);
      alert('PDF文件处理失败，请重试');
    }
  };

  // 处理TXT文件
  const handleTxtFile = async (file) => {
    try {
      setLoading(true);
      const text = await file.text();
      setContent(text);
      setLoading(false);
    } catch (error) {
      console.error('TXT处理错误:', error);
      setLoading(false);
      alert('TXT文件处理失败，请重试');
    }
  };

  // 文件上传处理
  const handleFileUpload = async (event) => {
    const file = event.target.files[0];
    if (!file) return;

    if (file.size > 100 * 1024 * 1024) {
      alert('文件大小不能超过100MB');
      return;
    }

    setFileName(file.name);
    setContent('');

    if (file.type === 'application/pdf') {
      await handlePdfFile(file);
    } else if (file.type === 'text/plain' || file.name.endsWith('.txt')) {
      await handleTxtFile(file);
    }
  };

  return (
    <div className="App dark">
      <div className="app-container">
        <h1 className="app-title">ADHD 阅读助手</h1>
        
        <div className="control-panel">
          <input
            type="file"
            accept=".txt,.pdf"
            onChange={handleFileUpload}
            id="file-upload"
            className="file-input"
          />
          <label htmlFor="file-upload" className="control-btn">
            选择文件
          </label>
          
          {fileName && (
            <span className="file-info">
              当前文件: {fileName}
            </span>
          )}
        </div>

        <div className="reader-container">
          {loading ? (
            <div className="loading">
              <div className="loading-spinner"></div>
              <div>正在加载内容，请稍候...</div>
            </div>
          ) : content ? (
            <div className="book-view">
              <div className="page-content">
                <div className="text-content">
                  {content}
                </div>
              </div>
            </div>
          ) : (
            <div className="upload-prompt">
              <p>请上传文件开始阅读</p>
              <p className="supported-formats">支持的格式: PDF, TXT (最大100MB)</p>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

export default App;
