  // 简化鼠标跟随监听器函数，只保留核心功能
  const addMouseFollowListeners = () => {
    console.log('添加鼠标跟随监听器...');

    // 获取内容区域
    const contentArea = document.querySelector('.page-content');
    if (!contentArea) {
      console.error('找不到内容区域');
      return;
    }

    // 确保监听器只添加一次
    contentArea.removeEventListener('mousemove', handleMouseMove, true);
    contentArea.removeEventListener('mouseleave', clearAllHighlights, true);

    // 添加事件监听器
    contentArea.addEventListener('mousemove', handleMouseMove, true);
    contentArea.addEventListener('mouseleave', clearAllHighlights, true);

    // 设置段落样式
    const paragraphs = document.querySelectorAll('.pdf-paragraph');
    paragraphs.forEach((paragraph, index) => {
      paragraph.style.position = 'relative';
      paragraph.style.lineHeight = '2';
      paragraph.style.color = 'rgb(240, 240, 240)';
      paragraph.dataset.index = index;
      paragraph.dataset.follow = 'true';
    });

    // 添加特殊类名
    const pdfContent = contentArea.querySelector('.pdf-content');
    if (pdfContent) {
      pdfContent.classList.add('mouse-follow-content');
    }

    console.log('鼠标跟随监听器设置完成');
  };

  // 优化鼠标移动处理函数
  const handleMouseMove = (e) => {
    // 添加额外的调试信息
    const currentMode = readingMode;

    // 检查当前模式
    if (currentMode !== 'follow-mouse') {
      console.log(`当前模式 ${currentMode} 非鼠标跟随，忽略鼠标移动`);
      return;
    }

    // 使用requestAnimationFrame限制处理频率
    if (handleMouseMove.frameId) {
      cancelAnimationFrame(handleMouseMove.frameId);
    }

    handleMouseMove.frameId = requestAnimationFrame(() => {
      try {
        // 获取鼠标位置元素
        const mouseTarget = document.elementFromPoint(e.clientX, e.clientY);
        if (!mouseTarget) {
          console.log('鼠标下方无元素');
          return;
        }

        // 查找包含段落
        const targetParagraph = mouseTarget.closest('.pdf-paragraph');
        if (!targetParagraph) {
          console.log('鼠标下方无段落元素');
          return;
        }

        console.log('找到段落元素，创建高亮');

        // 清除现有高亮
        const allHighlights = document.querySelectorAll('.line-highlight, .mouse-highlight');
        if (allHighlights.length > 0) {
          console.log(`清除 ${allHighlights.length} 个现有高亮`);
          allHighlights.forEach(h => {
            if (h && h.parentNode) {
              h.parentNode.removeChild(h);
            }
          });
        }

        // 获取段落位置信息
        const paragraphRect = targetParagraph.getBoundingClientRect();

        // 计算相对位置
        const relativeY = e.clientY - paragraphRect.top;

        // 获取计算样式
        const style = window.getComputedStyle(targetParagraph);

        // 获取字体大小和行高
        const fontSize = parseInt(style.fontSize) || 16;
        let lineHeight = parseInt(style.lineHeight) || fontSize * 2;
        if (style.lineHeight === 'normal') {
          lineHeight = fontSize * 2;
        }

        // 计算行索引
        const lineIndex = Math.floor(relativeY / lineHeight);
        if (lineIndex < 0) {
          console.log('行索引无效');
          return;
        }

        // 计算高亮位置
        const lineTop = lineIndex * lineHeight;
        const paddingTop = parseInt(style.paddingTop) || 0;
        const borderTop = parseInt(style.borderTopWidth) || 0;
        const textStartY = paddingTop + borderTop;

        // 创建高亮元素
        const highlight = document.createElement('div');
        highlight.className = 'line-highlight mouse-highlight';
        highlight.dataset.lineIndex = lineIndex;

        // 设置高亮样式
        Object.assign(highlight.style, {
          position: 'absolute',
          left: '0',
          top: `${lineTop + textStartY}px`,
          width: '100%',
          height: `${lineHeight * 0.85}px`,
          backgroundColor: 'rgba(255, 140, 0, 0.25)',
          borderRadius: '3px',
          border: '1px solid rgba(255, 140, 0, 0.3)',
          boxShadow: '0 0 6px rgba(255, 140, 0, 0.3)',
          zIndex: '5',
          opacity: '1',
          pointerEvents: 'none'
        });

        // 确保段落有相对定位
        if (targetParagraph.style.position !== 'relative') {
          targetParagraph.style.position = 'relative';
        }

        // 添加高亮元素
        targetParagraph.appendChild(highlight);
        console.log(`已在行 ${lineIndex} 添加高亮`);
      } catch (error) {
        console.error('鼠标移动处理错误:', error);
      } finally {
        handleMouseMove.frameId = null;
      }
    });
  };

  // 格式化文本为HTML
  const formatTextToHtml = (text) => {
    // 分段
    const paragraphs = text.split(/(?<=。|！|？|\.|\!|\?)\s+/).filter(p => p.trim());

    // 创建HTML内容
    let htmlContent = '<div class="pdf-content">';

    paragraphs.forEach(paragraph => {
      htmlContent += `<p class="pdf-paragraph">${paragraph}</p>`;
    });

    htmlContent += '</div>';
    return htmlContent;
  };

  // 应用静态高亮样式 - 修复文字减少问题
  const applyStaticHighlight = (text) => {
    console.log(`静态高亮模式：处理文本长度 ${text.length}`);

    // 保存原始文本
    const originalText = text;

    // 分段，使用更简单可靠的方法
    const paragraphs = text.split(/(?<=。|！|？|\.|\!|\?)\s*/).filter(p => p.trim().length > 0);

    // 创建HTML内容
    let htmlContent = '<div class="pdf-content static-highlight-content">';

    // 处理段落 - 使用同步处理避免延迟问题
    if (paragraphs.length === 0) {
      // 直接使用原始文本
      console.log('分段失败，使用原始文本');
      htmlContent += `<p class="pdf-paragraph">${originalText}</p>`;
    } else {
      // 同步处理每个段落，确保不丢失文本
      paragraphs.forEach((paragraph, index) => {
        if (!paragraph || paragraph.trim().length === 0) {
          return; // 跳过空段落
        }

        let processedText;
        try {
          // 使用词性标注函数处理
          processedText = tagPartsOfSpeech(paragraph.trim());

          // 确保处理结果不为空
          if (!processedText || processedText.trim().length === 0) {
            console.warn(`段落 ${index} 处理结果为空，使用原始文本`);
            processedText = paragraph.trim();
          }
        } catch (error) {
          console.error(`段落 ${index} 处理出错，使用原始文本:`, error);
          processedText = paragraph.trim();
        }

        // 添加到HTML内容
        htmlContent += `<p class="pdf-paragraph" data-index="${index}">${processedText}</p>`;
      });
    }

    htmlContent += '</div>';

