      htmlContent += `<p class="pdf-paragraph" data-follow="true" style="position: relative; line-height: 2;">${text}</p>`;
    } else {
      // 为每个段落添加样式和属性
      paragraphs.forEach((paragraph, index) => {
        const paragraphContent = paragraph.trim();
        if (paragraphContent.length > 0) {
          htmlContent += `
            <p class="pdf-paragraph" 
