  // 自动阅读引导
  const startAutoReadGuide = () => {
    if (autoReadIntervalRef.current) {
      clearInterval(autoReadIntervalRef.current);
    }

    // 创建阅读指示点
    if (!autoReadGuideRef.current) {
      const guide = document.createElement('div');
      guide.className = 'auto-read-guide';
      document.querySelector('.page-content').appendChild(guide);
      autoReadGuideRef.current = guide;
    }

    // 获取页面内所有段落
    const paragraphs = document.querySelectorAll('.page-content p');
    if (!paragraphs.length) return;

    let currentParagraphIndex = 0;
    let currentCharIndex = 0;

    autoReadIntervalRef.current = setInterval(() => {
      if (currentParagraphIndex >= paragraphs.length) {
        clearInterval(autoReadIntervalRef.current);
        return;
      }

      const paragraph = paragraphs[currentParagraphIndex];
      const text = paragraph.textContent;

      if (currentCharIndex >= text.length) {
        currentParagraphIndex++;
        currentCharIndex = 0;
        return;
      }

      // 计算当前字符位置
      const range = document.createRange();
      const textNode = paragraph.firstChild;

      if (textNode && textNode.nodeType === Node.TEXT_NODE) {
        range.setStart(textNode, Math.min(currentCharIndex, textNode.length));
        range.setStart(textNode, Math.min(currentCharIndex, textNode.length));

        const rect = range.getBoundingClientRect();

        // 更新指示点位置
        if (autoReadGuideRef.current) {
          const guide = autoReadGuideRef.current;
          guide.style.left = `${rect.left}px`;
          guide.style.top = `${rect.bottom + 5}px`;

          // 闪烁效果
          guide.style.opacity = 1;
          setTimeout(() => {
            guide.style.opacity = 0.5;
          }, 100);
        }
      }

