import React, { useState } from 'react';
import { Box, Container, Paper, Typography, ThemeProvider, createTheme } from '@mui/material';
import FileUpload from './components/FileUpload';
import Reader from './components/Reader';
import './styles/Reader.css';
import ReadingControls from './components/ReadingControls';

const theme = createTheme({
  palette: {
    mode: 'light',
    primary: {
      main: '#1976d2',
    },
    secondary: {
      main: '#dc004e',
    },
  },
});

function App() {
  const [text, setText] = useState('');
  const [highlightColor, setHighlightColor] = useState('#ffeb3b');
  const [readingMode, setReadingMode] = useState('static'); // 'static' or 'dynamic'
  const [highlightedWords, setHighlightedWords] = useState([]);
  const [selectedPos, setSelectedPos] = useState(['noun']); // 默认高亮名词
  const [isDarkMode, setIsDarkMode] = useState(false);
  const [content, setContent] = useState([]);
  const [fileName, setFileName] = useState('');

  const handleFileUpload = (event) => {
    const file = event.target.files[0];
    if (file) {
      setFileName(file.name);
      
      const reader = new FileReader();
      reader.onload = (e) => {
        const text = e.target.result;
        // 将文本按段落分割
        const pages = text.split(/\n\s*\n/)
                        .filter(page => page.trim())
                        .map(page => page.trim());
        console.log('Loaded content:', pages); // 调试用
        setContent(pages);
      };
      reader.readAsText(file);
    }
  };

  return (
    <ThemeProvider theme={theme}>
      <Container maxWidth="lg">
        <Box sx={{ my: 4 }}>
          <Typography variant="h3" component="h1" gutterBottom align="center">
            ADHD阅读助手
          </Typography>
          
          <Paper elevation={3} sx={{ p: 3, mb: 3 }}>
            <FileUpload setText={setText} />
          </Paper>

          <Paper elevation={3} sx={{ p: 3, mb: 3 }}>
            <ReadingControls
              highlightColor={highlightColor}
              setHighlightColor={setHighlightColor}
              readingMode={readingMode}
              setReadingMode={setReadingMode}
              selectedPos={selectedPos}
              setSelectedPos={setSelectedPos}
            />
          </Paper>

          <Paper elevation={3} sx={{ p: 3 }}>
            <Reader content={content} isDarkMode={isDarkMode} />
          </Paper>
        </Box>
      </Container>
      <button 
        className={`mode-toggle ${isDarkMode ? 'dark' : 'light'}`}
        onClick={() => setIsDarkMode(!isDarkMode)}
      >
        切换{isDarkMode ? '明亮' : '暗黑'}模式
      </button>
    </ThemeProvider>
  );
}

export default App;
