import React, { useState, useRef } from 'react';
import * as pdfjsLib from 'pdfjs-dist';
import * as pdfjsViewer from 'pdfjs-dist/web/pdf_viewer';
import './styles/Reader.css';
import { pdfjs } from 'pdfjs-dist';

// 设置 PDF.js worker
pdfjsLib.GlobalWorkerOptions.workerSrc = `//cdnjs.cloudflare.com/ajax/libs/pdf.js/${pdfjsLib.version}/pdf.worker.min.js`;

function App() {
  // 状态定义
  const [isDarkMode, setIsDarkMode] = useState(true);
  const [pages, setPages] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const [totalPages, setTotalPages] = useState(0);
  const [fileName, setFileName] = useState('');
  const [loading, setLoading] = useState(false);
  const fileRef = useRef(null); // 添加文件引用

  // 处理PDF文件
  const handlePdfFile = async (file) => {
    try {
      const fileReader = new FileReader();
      fileReader.onload = async function() {
        const typedarray = new Uint8Array(this.result);
        
        const loadingTask = pdfjsLib.getDocument(typedarray);
        const pdf = await loadingTask.promise;
        console.log('PDF loaded');
        
        const page = await pdf.getPage(1);
        console.log('Page loaded');
        
        const viewport = page.getViewport({ scale: 1.5 });
        
        // 创建一个新的容器div
        const containerDiv = document.createElement('div');
        containerDiv.className = 'pdf-container';
        containerDiv.style.position = 'relative';
        containerDiv.style.width = `${viewport.width}px`;
        containerDiv.style.height = `${viewport.height}px`;
        
        // 创建并设置canvas
        const canvas = document.createElement('canvas');
        const context = canvas.getContext('2d');
        canvas.height = viewport.height;
        canvas.width = viewport.width;
        containerDiv.appendChild(canvas);
        
        // 获取页面容器并清空
        const pageContainer = document.querySelector('.page-content');
        if (!pageContainer) {
          console.error('Page container not found');
          return;
        }
        
        pageContainer.innerHTML = '';
        pageContainer.appendChild(containerDiv);
        
        console.log('Starting render');
        
        try {
          await page.render({
            canvasContext: context,
            viewport: viewport
          }).promise;
          
          console.log('Render complete');
          
          const textContent = await page.getTextContent();
          console.log('Text content loaded', textContent);
          
          // 创建文本层
          const textLayerDiv = document.createElement('div');
          textLayerDiv.className = 'textLayer';
          textLayerDiv.style.width = `${viewport.width}px`;
          textLayerDiv.style.height = `${viewport.height}px`;
          containerDiv.appendChild(textLayerDiv);
          
          // 渲染文本层
          const renderTextLayer = pdfjsLib.renderTextLayer({
            textContent: textContent,
            container: textLayerDiv,
            viewport: viewport,
            textDivs: []
          });
          
          await renderTextLayer.promise;
          console.log('Text layer rendered');
          
          // 应用字间距
          const textElements = textLayerDiv.querySelectorAll('span');
          textElements.forEach(span => {
            span.style.letterSpacing = '2em';
            span.style.color = '#000';
          });
          
        } catch (renderError) {
          console.error('Render error:', renderError);
        }
      };
      
      fileReader.readAsArrayBuffer(file);
    } catch (error) {
      console.error('Error:', error);
    }
  };

  // 处理TXT文件
  const handleTxtFile = async (file) => {
    try {
      setLoading(true);
      const text = await file.text();
      // 将文本分页处理
      const pageTexts = text.split('\n\n').map(paragraph => [{
        text: paragraph,
        x: 40,
        y: 40,
        fontSize: 16,
        fontFamily: 'serif'
      }]);
      setPages(pageTexts);
      setTotalPages(pageTexts.length);
      setCurrentPage(1);
      setLoading(false);
    } catch (error) {
      console.error('TXT处理错误:', error);
      setLoading(false);
      alert('TXT文件处理失败，请重试');
    }
  };

  // 文件上传处理
  const handleFileUpload = async (event) => {
    const file = event.target.files[0];
    if (!file) return;

    if (file.size > 100 * 1024 * 1024) {
      alert('文件大小不能超过100MB');
      return;
    }

    setFileName(file.name);
    setPages([]); // 清空现有页面

    if (file.type === 'application/pdf') {
      await handlePdfFile(file);
    } else if (file.type === 'text/plain' || file.name.endsWith('.txt')) {
      await handleTxtFile(file);
    }
  };

  // 加载指定页面
  const loadPage = async (pageNum) => {
    if (!fileRef.current) return;

    try {
      setLoading(true);
      const fileArrayBuffer = await fileRef.current.arrayBuffer();
      const pdf = await pdfjsLib.getDocument({
        data: fileArrayBuffer,
      }).promise;

      const page = await pdf.getPage(pageNum);
      const viewport = page.getViewport({ scale: 1.0 });
      const textContent = await page.getTextContent();

      const pageText = textContent.items.map(item => ({
        text: item.str,
        x: item.transform[4],
        y: viewport.height - item.transform[5],
        fontSize: Math.sqrt((item.transform[0] * item.transform[0]) + (item.transform[1] * item.transform[1])),
        fontFamily: item.fontName || 'serif'
      }));

      setPages(prev => {
        const newPages = [...prev];
        newPages[pageNum - 1] = { content: pageText, width: viewport.width, height: viewport.height };
        return newPages;
      });
      setLoading(false);
    } catch (error) {
      console.error('页面加载错误:', error);
      setLoading(false);
    }
  };

  // 处理翻页
  const handlePageChange = (direction) => {
    const newPage = direction === 'next' 
      ? Math.min(currentPage + 1, totalPages)
      : Math.max(currentPage - 1, 1);
    
    setCurrentPage(newPage);
    if (!pages[newPage - 1]) {
      loadPage(newPage);
    }
  };

  return (
    <div className={`App ${isDarkMode ? 'dark' : 'light'}`}>
      <div className="app-container">
        <h1 className="app-title">ADHD 阅读助手</h1>
        
        <div className="control-panel">
          <input
            type="file"
            accept=".txt,.pdf"
            onChange={handleFileUpload}
            id="file-upload"
            className="file-input"
          />
          <label htmlFor="file-upload" className="control-btn">
            选择文件
          </label>
          
          {fileName && (
            <span className="file-info">
              当前文件: {fileName}
            </span>
          )}
          
          <button 
            className="control-btn"
            onClick={() => setIsDarkMode(prev => !prev)}
          >
            切换{isDarkMode ? '明亮' : '暗黑'}模式
          </button>
        </div>

        <div className="reader-container">
          {loading ? (
            <div className="loading">
              <div className="loading-spinner"></div>
              <div>正在加载内容，请稍候...</div>
            </div>
          ) : pages.length > 0 ? (
            <div className="book-view">
              <button 
                className="page-btn prev" 
                onClick={() => handlePageChange('prev')}
                disabled={currentPage === 1}
              >
                ‹
              </button>
              
              <div className="page-content">
                <div className="page-layout">
                  {pages[currentPage - 1]?.content.map((item, index) => (
                    <span
                      key={index}
                      style={{
                        position: 'absolute',
                        left: `${item.x}px`,
                        top: `${item.y}px`,
                        fontSize: `${item.fontSize}px`,
                        fontFamily: item.fontFamily,
                        letterSpacing: `${item.letterSpacing}px`,
                        whiteSpace: 'pre',
                        color: isDarkMode ? '#e0e0e0' : '#2c2c2c',
                        textRendering: 'optimizeLegibility',
                        WebkitFontSmoothing: 'antialiased',
                        MozOsxFontSmoothing: 'grayscale',
                      }}
                    >
                      {item.text}
                    </span>
                  ))}
                </div>
              </div>

              <button 
                className="page-btn next" 
                onClick={() => handlePageChange('next')}
                disabled={currentPage === totalPages}
              >
                ›
              </button>

              <div className="page-number">
                {currentPage} / {totalPages}
              </div>
            </div>
          ) : (
            <div className="upload-prompt">
              <p>请上传文件开始阅读</p>
              <p className="supported-formats">支持的格式: PDF, TXT (最大100MB)</p>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

export default App;
