import React, { useState, useRef } from 'react';
import * as pdfjsLib from 'pdfjs-dist';
import './styles/Reader.css';

// 确保使用正确的 worker 版本
const workerSrc = require('pdfjs-dist/build/pdf.worker.entry');
pdfjsLib.GlobalWorkerOptions.workerSrc = workerSrc;

function App() {
  // 状态定义
  const [isDarkMode, setIsDarkMode] = useState(true);
  const [pages, setPages] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const [totalPages, setTotalPages] = useState(0);
  const [fileName, setFileName] = useState('');
  const [loading, setLoading] = useState(false);
  const fileRef = useRef(null); // 添加文件引用

  // 处理PDF文件
  const handlePdfFile = async (file) => {
    try {
      setLoading(true);
      fileRef.current = file;

      const fileReader = new FileReader();
      fileReader.onload = async function() {
        const typedarray = new Uint8Array(this.result);
        const pdf = await pdfjsLib.getDocument(typedarray).promise;
        const page = await pdf.getPage(1);
        
        // 增加渲染尺寸
        const viewport = page.getViewport({ scale: 2.0 }); // 增加缩放比例
        
        const canvas = document.createElement('canvas');
        const context = canvas.getContext('2d');
        
        // 设置更大的画布尺寸
        canvas.height = viewport.height;
        canvas.width = viewport.width;
        
        // 渲染配置
        const renderContext = {
          canvasContext: context,
          viewport: viewport,
          enableWebGL: true
        };
        
        await page.render(renderContext).promise;

        setTotalPages(pdf.numPages);

        // 修改文本提取和处理的部分
        const processTextContent = async (page) => {
          const textContent = await page.getTextContent();
          return textContent.items.map(item => {
            return {
              ...item,
              str: item.str,  // 不再使用split和join，直接使用CSS控制间距
              transform: [
                item.transform[0],
                item.transform[1],
                item.transform[2],
                item.transform[3],
                item.transform[4],
                item.transform[5]
              ],
              width: item.width * 2.5, // 增加宽度以适应更大的字间距
              letterSpacing: '2em' // 增加到2em
            };
          });
        };

        // 渲染文本内容
        const renderTextLayer = (textContent, container) => {
          const textLayerDiv = document.createElement('div');
          textLayerDiv.className = 'textLayer';
          container.appendChild(textLayerDiv);

          textContent.forEach(item => {
            const textElement = document.createElement('span');
            textElement.textContent = item.str;
            textElement.style.fontSize = `${item.fontSize}px`;
            textElement.style.fontFamily = item.fontFamily || 'sans-serif';
            textElement.style.letterSpacing = '2em'; // 增加到2em
            textElement.style.position = 'absolute';
            textElement.style.left = `${item.transform[4]}px`;
            textElement.style.top = `${item.transform[5]}px`;
            textLayerDiv.appendChild(textElement);
          });
        };

        const pageText = await processTextContent(page);
        renderTextLayer(pageText, canvas);

        setPages([{ content: pageText, width: viewport.width, height: viewport.height }]);
        setCurrentPage(1);
        setLoading(false);
      };
      fileReader.readAsArrayBuffer(file);
    } catch (error) {
      console.error('Error loading PDF:', error);
      setLoading(false);
      alert('PDF文件处理失败，请重试');
    }
  };

  // 处理TXT文件
  const handleTxtFile = async (file) => {
    try {
      setLoading(true);
      const text = await file.text();
      // 将文本分页处理
      const pageTexts = text.split('\n\n').map(paragraph => [{
        text: paragraph,
        x: 40,
        y: 40,
        fontSize: 16,
        fontFamily: 'serif'
      }]);
      setPages(pageTexts);
      setTotalPages(pageTexts.length);
      setCurrentPage(1);
      setLoading(false);
    } catch (error) {
      console.error('TXT处理错误:', error);
      setLoading(false);
      alert('TXT文件处理失败，请重试');
    }
  };

  // 文件上传处理
  const handleFileUpload = async (event) => {
    const file = event.target.files[0];
    if (!file) return;

    if (file.size > 100 * 1024 * 1024) {
      alert('文件大小不能超过100MB');
      return;
    }

    setFileName(file.name);
    setPages([]); // 清空现有页面

    if (file.type === 'application/pdf') {
      await handlePdfFile(file);
    } else if (file.type === 'text/plain' || file.name.endsWith('.txt')) {
      await handleTxtFile(file);
    }
  };

  // 加载指定页面
  const loadPage = async (pageNum) => {
    if (!fileRef.current) return;

    try {
      setLoading(true);
      const fileArrayBuffer = await fileRef.current.arrayBuffer();
      const pdf = await pdfjsLib.getDocument({
        data: fileArrayBuffer,
      }).promise;

      const page = await pdf.getPage(pageNum);
      const viewport = page.getViewport({ scale: 1.0 });
      const textContent = await page.getTextContent();

      const pageText = textContent.items.map(item => ({
        text: item.str,
        x: item.transform[4],
        y: viewport.height - item.transform[5],
        fontSize: Math.sqrt((item.transform[0] * item.transform[0]) + (item.transform[1] * item.transform[1])),
        fontFamily: item.fontName || 'serif'
      }));

      setPages(prev => {
        const newPages = [...prev];
        newPages[pageNum - 1] = { content: pageText, width: viewport.width, height: viewport.height };
        return newPages;
      });
      setLoading(false);
    } catch (error) {
      console.error('页面加载错误:', error);
      setLoading(false);
    }
  };

  // 处理翻页
  const handlePageChange = (direction) => {
    const newPage = direction === 'next' 
      ? Math.min(currentPage + 1, totalPages)
      : Math.max(currentPage - 1, 1);
    
    setCurrentPage(newPage);
    if (!pages[newPage - 1]) {
      loadPage(newPage);
    }
  };

  return (
    <div className={`App ${isDarkMode ? 'dark' : 'light'}`}>
      <div className="app-container">
        <h1 className="app-title">ADHD 阅读助手</h1>
        
        <div className="control-panel">
          <input
            type="file"
            accept=".txt,.pdf"
            onChange={handleFileUpload}
            id="file-upload"
            className="file-input"
          />
          <label htmlFor="file-upload" className="control-btn">
            选择文件
          </label>
          
          {fileName && (
            <span className="file-info">
              当前文件: {fileName}
            </span>
          )}
          
          <button 
            className="control-btn"
            onClick={() => setIsDarkMode(prev => !prev)}
          >
            切换{isDarkMode ? '明亮' : '暗黑'}模式
          </button>
        </div>

        <div className="reader-container">
          {loading ? (
            <div className="loading">
              <div className="loading-spinner"></div>
              <div>正在加载内容，请稍候...</div>
            </div>
          ) : pages.length > 0 ? (
            <div className="book-view">
              <button 
                className="page-btn prev" 
                onClick={() => handlePageChange('prev')}
                disabled={currentPage === 1}
              >
                ‹
              </button>
              
              <div className="page-content">
                <div className="page-layout">
                  {pages[currentPage - 1]?.content.map((item, index) => (
                    <span
                      key={index}
                      style={{
                        position: 'absolute',
                        left: `${item.x}px`,
                        top: `${item.y}px`,
                        fontSize: `${item.fontSize}px`,
                        fontFamily: item.fontFamily,
                        letterSpacing: `${item.letterSpacing}px`,
                        whiteSpace: 'pre',
                        color: isDarkMode ? '#e0e0e0' : '#2c2c2c',
                        textRendering: 'optimizeLegibility',
                        WebkitFontSmoothing: 'antialiased',
                        MozOsxFontSmoothing: 'grayscale',
                      }}
                    >
                      {item.text}
                    </span>
                  ))}
                </div>
              </div>

              <button 
                className="page-btn next" 
                onClick={() => handlePageChange('next')}
                disabled={currentPage === totalPages}
              >
                ›
              </button>

              <div className="page-number">
                {currentPage} / {totalPages}
              </div>
            </div>
          ) : (
            <div className="upload-prompt">
              <p>请上传文件开始阅读</p>
              <p className="supported-formats">支持的格式: PDF, TXT (最大100MB)</p>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

export default App;
