import React, { useState, useRef } from 'react';
import * as pdfjsLib from 'pdfjs-dist';
import * as pdfjsViewer from 'pdfjs-dist/web/pdf_viewer';
import './styles/Reader.css';
import { pdfjs } from 'pdfjs-dist';

// 设置 PDF.js worker
pdfjsLib.GlobalWorkerOptions.workerSrc = `//cdnjs.cloudflare.com/ajax/libs/pdf.js/${pdfjsLib.version}/pdf.worker.min.js`;

const App = () => {
  // 状态定义
  const [isDarkMode, setIsDarkMode] = useState(true);
  const [pages, setPages] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const [totalPages, setTotalPages] = useState(0);
  const [fileName, setFileName] = useState('');
  const [loading, setLoading] = useState(false);
  const fileRef = useRef(null); // 添加文件引用
  const [file, setFile] = useState(null);
  const [pdfText, setPdfText] = useState('');

  // 处理PDF文件
  const handlePdfFile = async (file) => {
    try {
      const fileReader = new FileReader();
      fileReader.onload = async function() {
        const typedarray = new Uint8Array(this.result);
        const pdf = await pdfjsLib.getDocument(typedarray).promise;
        setTotalPages(pdf.numPages);
        
        // 获取当前页
        const page = await pdf.getPage(currentPage);
        
        // 提取文本内容
        const textContent = await page.getTextContent();
        const textItems = textContent.items.map(item => {
          // 为每个字符添加间距
          return item.str.split('').join('\u2004\u2004\u2004\u2004');
        });
        
        // 设置提取的文本
        setPdfText(textItems.join(' '));
        
        const pageContainer = document.querySelector('.page-content');
        if (pageContainer) {
          pageContainer.innerHTML = '';
          
          // 创建文本显示容器
          const textContainer = document.createElement('div');
          textContainer.className = 'text-container';
          textContainer.style.letterSpacing = '2em';
          textContainer.style.lineHeight = '2';
          textContainer.style.whiteSpace = 'pre-wrap';
          textContainer.textContent = textItems.join(' ');
          
          pageContainer.appendChild(textContainer);
        }
      };
      fileReader.readAsArrayBuffer(file);
    } catch (error) {
      console.error('Error:', error);
    }
  };

  // 处理TXT文件
  const handleTxtFile = async (file) => {
    try {
      setLoading(true);
      const text = await file.text();
      // 将文本分页处理
      const pageTexts = text.split('\n\n').map(paragraph => [{
        text: paragraph,
        x: 40,
        y: 40,
        fontSize: 16,
        fontFamily: 'serif'
      }]);
      setPages(pageTexts);
      setTotalPages(pageTexts.length);
      setCurrentPage(1);
      setLoading(false);
    } catch (error) {
      console.error('TXT处理错误:', error);
      setLoading(false);
      alert('TXT文件处理失败，请重试');
    }
  };

  // 文件上传处理
  const handleFileUpload = async (event) => {
    const file = event.target.files[0];
    if (!file) return;

    if (file.size > 100 * 1024 * 1024) {
      alert('文件大小不能超过100MB');
      return;
    }

    setFileName(file.name);
    setPages([]); // 清空现有页面

    if (file.type === 'application/pdf') {
      await handlePdfFile(file);
    } else if (file.type === 'text/plain' || file.name.endsWith('.txt')) {
      await handleTxtFile(file);
    }
  };

  // 翻页函数
  const changePage = async (delta) => {
    const newPage = currentPage + delta;
    if (newPage >= 1 && newPage <= totalPages) {
      setCurrentPage(newPage);
      if (file) {
        handlePdfFile(file);
      }
    }
  };

  return (
    <div className={`App ${isDarkMode ? 'dark' : 'light'}`}>
      <div className="app-container">
        <header className="header">
          <h1>ADHD 阅读助手</h1>
          <div className="controls">
            <button 
              className="upload-btn"
              onClick={() => document.getElementById('fileInput').click()}
            >
              选择文件
            </button>
            <input
              id="fileInput"
              type="file"
              accept=".pdf,.txt"
              style={{ display: 'none' }}
              onChange={(e) => {
                const file = e.target.files[0];
                if (file) {
                  setFile(file);
                  handlePdfFile(file);
                }
              }}
            />
            <button className="mode-btn">
              切换明亮模式
            </button>
          </div>
        </header>
        
        <div className="reader-container">
          <button 
            className="page-btn prev"
            onClick={() => changePage(-1)}
            disabled={currentPage <= 1}
          >
            ←
          </button>

          <div className="page-content">
            {!file && (
              <div className="upload-hint">
                请上传文件开始阅读
                <br />
                支持的格式: PDF, TXT (最大100MB)
              </div>
            )}
          </div>

          <button 
            className="page-btn next"
            onClick={() => changePage(1)}
            disabled={currentPage >= totalPages}
          >
            →
          </button>
        </div>

        {file && (
          <div className="page-info">
            {currentPage} / {totalPages}
          </div>
        )}
      </div>
    </div>
  );
};

export default App;
