import React, { useState, useRef } from 'react';
import { pdfjs } from 'pdfjs-dist';
import './styles/Reader.css';

// 设置 PDF.js worker
pdfjs.GlobalWorkerOptions.workerSrc = `//cdnjs.cloudflare.com/ajax/libs/pdf.js/${pdfjs.version}/pdf.worker.min.js`;

function App() {
  const [isDarkMode, setIsDarkMode] = useState(false);
  const [pages, setPages] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const [totalPages, setTotalPages] = useState(0);
  const [fileName, setFileName] = useState('');
  const [loading, setLoading] = useState(false);
  const fileRef = useRef(null); // 用于存储当前文件的引用

  // 处理PDF文件
  const handlePdfFile = async (file) => {
    try {
      setLoading(true);
      const fileArrayBuffer = await file.arrayBuffer();
      const pdf = await pdfjs.getDocument(fileArrayBuffer).promise;
      setTotalPages(pdf.numPages);
      fileRef.current = file; // 保存文件引用
      
      // 加载第一页
      const page = await pdf.getPage(1);
      const viewport = page.getViewport({ scale: 1.5 });
      
      // 创建canvas
      const canvas = document.createElement('canvas');
      const context = canvas.getContext('2d');
      canvas.height = viewport.height;
      canvas.width = viewport.width;

      // 渲染PDF页面
      await page.render({
        canvasContext: context,
        viewport: viewport
      }).promise;

      setPages(prevPages => [...prevPages, canvas.toDataURL()]);
      setLoading(false);
    } catch (error) {
      console.error('PDF处理错误:', error);
      alert('PDF文件处理失败，请重试');
      setLoading(false);
    }
  };

  // 加载指定页面
  const loadPage = async (pageNumber) => {
    if (!fileRef.current) return; // 检查是否有文件

    try {
      setLoading(true);
      const fileArrayBuffer = await fileRef.current.arrayBuffer();
      const pdf = await pdfjs.getDocument(fileArrayBuffer).promise;
      const page = await pdf.getPage(pageNumber);
      const viewport = page.getViewport({ scale: 1.5 });
      
      const canvas = document.createElement('canvas');
      const context = canvas.getContext('2d');
      canvas.height = viewport.height;
      canvas.width = viewport.width;

      await page.render({
        canvasContext: context,
        viewport: viewport
      }).promise;

      setPages(prevPages => {
        const newPages = [...prevPages];
        newPages[pageNumber - 1] = canvas.toDataURL();
        return newPages;
      });
      setLoading(false);
    } catch (error) {
      console.error('页面加载错误:', error);
      setLoading(false);
    }
  };

  // 文件上传处理
  const handleFileUpload = async (event) => {
    const file = event.target.files[0];
    if (!file) return;

    // 检查文件大小（100MB限制）
    if (file.size > 100 * 1024 * 1024) {
      alert('文件大小不能超过100MB');
      return;
    }

    setFileName(file.name);
    setPages([]);
    setCurrentPage(1);

    if (file.type === 'application/pdf') {
      await handlePdfFile(file);
    } else {
      // 处理文本文件
      try {
        const text = await file.text();
        // 将文本按页分割（这里以空行为分隔符）
        const textPages = text.split('\n\n').filter(page => page.trim());
        setPages(textPages);
        setTotalPages(textPages.length);
      } catch (error) {
        console.error('文件读取失败:', error);
        alert('文件读取失败，请重试');
      }
    }
  };

  // 翻页处理
  const handlePageChange = (direction) => {
    const newPage = direction === 'next' 
      ? Math.min(currentPage + 1, totalPages)
      : Math.max(currentPage - 1, 1);
    
    setCurrentPage(newPage);
    if (pages[newPage - 1] === undefined) {
      loadPage(newPage);
    }
  };

  return (
    <div className={`App ${isDarkMode ? 'dark' : 'light'}`}>
      <div className="app-header">
        <h1>ADHD 阅读助手</h1>
        <div className="controls">
          <input
            type="file"
            accept=".txt,.md,.pdf"
            onChange={handleFileUpload}
            id="file-upload"
            max="104857600" // 100MB
          />
          <label htmlFor="file-upload">选择文件</label>
          {fileName && <span>当前文件: {fileName}</span>}
          <button onClick={() => setIsDarkMode(!isDarkMode)}>
            切换{isDarkMode ? '明亮' : '暗黑'}模式
          </button>
        </div>
      </div>

      <div className="reader-container">
        {loading ? (
          <div className="loading">加载中...</div>
        ) : pages.length > 0 ? (
          <div className="book-view">
            <button 
              className="page-btn prev" 
              onClick={() => handlePageChange('prev')}
              disabled={currentPage === 1}
            >
              ‹
            </button>
            <div className="page-content">
              {pages[currentPage - 1]?.startsWith('data:image') ? (
                <img src={pages[currentPage - 1]} alt={`页面 ${currentPage}`} />
              ) : (
                <div className="text-content">
                  {pages[currentPage - 1]}
                </div>
              )}
            </div>
            <button 
              className="page-btn next" 
              onClick={() => handlePageChange('next')}
              disabled={currentPage === totalPages}
            >
              ›
            </button>
            <div className="page-number">
              {currentPage} / {totalPages}
            </div>
          </div>
        ) : (
          <div className="upload-prompt">
            <p>请上传文件开始阅读</p>
            <p>支持的格式: PDF, TXT, MD (最大100MB)</p>
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
