import React, { useState, useEffect, useRef } from 'react';

const Reader = ({ content, isDarkMode }) => {
  const [readingMode, setReadingMode] = useState('highlight');
  const [autoReadingEnabled, setAutoReadingEnabled] = useState(false);
  const [currentPage, setCurrentPage] = useState(0);
  const [currentSentenceIndex, setCurrentSentenceIndex] = useState(0);
  const contentRef = useRef(null);

  // 确保内容存在
  useEffect(() => {
    console.log('Reader received content:', content); // 调试用
  }, [content]);

  // 将当前页面的文本分割成句子
  const sentences = content[currentPage]?.split(/(?<=\.|\!|\?)\s+/) || [];

  // 自动阅读功能
  useEffect(() => {
    if (readingMode === 'dynamic' && autoReadingEnabled) {
      const timer = setInterval(() => {
        setCurrentSentenceIndex(prev => 
          prev < sentences.length - 1 ? prev + 1 : prev
        );
      }, 2000);

      return () => clearInterval(timer);
    }
  }, [autoReadingEnabled, readingMode, sentences]);

  // 鼠标移动处理
  const handleMouseMove = (e) => {
    if (readingMode === 'dynamic' && !autoReadingEnabled && contentRef.current) {
      const rect = contentRef.current.getBoundingClientRect();
      const y = e.clientY - rect.top;
      const lineHeight = 24; // 假设每行高度为24px
      const currentLine = Math.floor(y / lineHeight);
      setCurrentSentenceIndex(currentLine);
    }
  };

  return (
    <div className={`reader-container ${isDarkMode ? 'dark' : 'light'}`}>
      <div className="reader-controls">
        <button onClick={() => setReadingMode('highlight')}>
          {readingMode === 'highlight' ? '✓ 静息高亮模式' : '静息高亮模式'}
        </button>
        <button onClick={() => setReadingMode('dynamic')}>
          {readingMode === 'dynamic' ? '✓ 动态阅读模式' : '动态阅读模式'}
        </button>
        {readingMode === 'dynamic' && (
          <button onClick={() => setAutoReadingEnabled(!autoReadingEnabled)}>
            {autoReadingEnabled ? '关闭自动阅读' : '开启自动阅读'}
          </button>
        )}
      </div>

      <div 
        ref={contentRef}
        className="reader-content"
        onMouseMove={handleMouseMove}
      >
        {content && content[currentPage] ? (
          readingMode === 'highlight' ? (
            <div className="content-text">
              {content[currentPage].split('\n').map((paragraph, idx) => (
                <p key={idx}>{paragraph.trim()}</p>
              ))}
            </div>
          ) : (
            <div className="content-text">
              {sentences.map((sentence, index) => (
                <span
                  key={index}
                  className={`sentence ${
                    autoReadingEnabled || index === currentSentenceIndex
                      ? 'active'
                      : 'dimmed'
                  }`}
                >
                  {sentence}{' '}
                </span>
              ))}
            </div>
          )
        ) : (
          <div className="no-content">无内容可显示</div>
        )}
      </div>

      {content.length > 0 && (
        <div className="page-navigation">
          <button 
            onClick={() => setCurrentPage(prev => Math.max(0, prev - 1))}
            disabled={currentPage === 0}
          >
            上一页
          </button>
          <span className="page-info">
            第 {currentPage + 1} 页 / 共 {content.length} 页
          </span>
          <button 
            onClick={() => setCurrentPage(prev => 
              Math.min(content.length - 1, prev + 1)
            )}
            disabled={currentPage === content.length - 1}
          >
            下一页
          </button>
        </div>
      )}
    </div>
  );
};

export default Reader;
