      // æŒ‰çˆ¶èŠ‚ç‚¹åˆ†ç»„
      const parentMap = new Map();
      charSpans.forEach(span => {
        const parent = span.parentNode;
        if (!parent) return;
        if (!parentMap.has(parent)) {
          parentMap.set(parent, []);
        }
        parentMap.get(parent).push(span);
      });

      // å¯¹æ¯ä¸ªçˆ¶èŠ‚ç‚¹ï¼Œå°†å­—ç¬¦spanåˆå¹¶å›æ–‡æœ¬èŠ‚ç‚¹
      parentMap.forEach((spans, parent) => {
        if (spans.length === 0) return;

        // æ’åºspansç¡®ä¿é¡ºåºæ­£ç¡®
        spans.sort((a, b) => {
          const indexA = parseInt(a.dataset.rhythmIndex || '0');
          const indexB = parseInt(b.dataset.rhythmIndex || '0');
          return indexA - indexB;
        });

        // å°†æ‰€æœ‰spançš„æ–‡æœ¬åˆå¹¶ä¸ºä¸€ä¸ªæ–‡æœ¬èŠ‚ç‚¹
        try {
          const text = spans.map(span => span.textContent).join('');
          const textNode = document.createTextNode(text);

          // æ›¿æ¢ç¬¬ä¸€ä¸ªspanï¼Œç„¶åç§»é™¤å…¶ä½™çš„
          parent.replaceChild(textNode, spans[0]);
          for (let i = 1; i < spans.length; i++) {
            if (spans[i].parentNode === parent) {
              parent.removeChild(spans[i]);
            }
          }
        } catch (e) {
          console.error("æ¢å¤æ–‡æœ¬æ—¶å‡ºé”™:", e);
        }
      });
      this.charElements = [];
      updateDOMInfo("<span style='color:#f44336'>â–  èŠ‚å¥é˜…è¯»å·²åœæ­¢</span>");
    },
    // æ›´æ–°é¢œè‰²
    updateTextColors: function () {
      if (this.charElements.length === 0) return;
      this.charElements.forEach((span, index) => {
        let color,
          fontWeight = 'inherit';
        if (index === this.currentIndex) {
          color = '#00aa00'; // å½“å‰å­—ç¬¦ - ç»¿è‰²
          fontWeight = 'bold';
        } else if (index === this.currentIndex + 1 || index === this.currentIndex + 2) {
          color = '#aaaaaa'; // ä¸‹ä¸€ä¸ªå­—ç¬¦ - ç°è‰²
        } else if (this.readIndices.has(index)) {
          color = '#ffffff'; // å·²è¯»å­—ç¬¦ - ç™½è‰²
        } else {
          color = 'rgba(160, 160, 160, 0.25)'; // æœªè¯»å­—ç¬¦ - æ·¡ç°è‰²
        }
        span.style.color = color;
        span.style.fontWeight = fontWeight;
      });
    },
    // ç§»åŠ¨åˆ°ä¸‹ä¸€ä¸ªå­—ç¬¦
    moveToNextChar: function () {
      if (!this.active || this.charElements.length === 0) return;
      this.readIndices.add(this.currentIndex);
      do {
        this.currentIndex++;
        if (this.currentIndex >= this.charElements.length) {
          updateDOMInfo("<span style='color:#FF9800'>ğŸ å·²åˆ°è¾¾æ–‡æœ¬æœ«å°¾</span>");
          this.stop();
          return;
        }
      } while (this.currentIndex < this.charElements.length && this.charElements[this.currentIndex] && this.charElements[this.currentIndex].dataset.whitespace === 'true');
      this.updateTextColors();
      this.scrollToCurrentChar();
    },
    // æ»šåŠ¨åˆ°å½“å‰å­—ç¬¦
    scrollToCurrentChar: function () {
      const span = this.charElements[this.currentIndex];
      if (!span) return;
      const rect = span.getBoundingClientRect();
      window.scrollTo({
        top: window.scrollY + rect.top - window.innerHeight * 0.4,
        behavior: 'smooth'
      });
    }
  };

