  // æ·»åŠ é¼ æ ‡è·Ÿéšç›‘å¬å™¨
  const addMouseFollowListeners = () => {
    // è·å–å†…å®¹å®¹å™¨
    const contentArea = document.querySelector('.mouse-follow-content');
    if (!contentArea) {
      console.error('æœªæ‰¾åˆ°é¼ æ ‡è·Ÿéšå†…å®¹åŒºåŸŸ');
      return;
    }

    // ä¸Šä¸€ä¸ªé«˜äº®çš„è¡Œ
    let lastHighlightedLine = null;

    // ç›‘å¬é¼ æ ‡ç§»åŠ¨ - ç²¾ç¡®çš„è¡Œæ£€æµ‹
    const mouseMoveHandler = e => {
      // è·å–é¼ æ ‡ä½ç½®
      const mouseY = e.clientY;

      // è·å–æ‰€æœ‰è¡Œ
      const allLines = contentArea.querySelectorAll('.follow-line');
      let foundLine = null;
      let minDistance = Infinity;

      // å¯»æ‰¾é¼ æ ‡æ‰€åœ¨çš„ç²¾ç¡®è¡Œ
      allLines.forEach(line => {
        const rect = line.getBoundingClientRect();
        const lineCenter = rect.top + rect.height / 2;
        const distance = Math.abs(mouseY - lineCenter);

        // å¦‚æœé¼ æ ‡åœ¨è¡ŒèŒƒå›´å†…ï¼Œè®¡ç®—åˆ°è¡Œä¸­å¿ƒçš„è·ç¦»
        if (mouseY >= rect.top && mouseY <= rect.bottom) {
          if (distance < minDistance) {
            minDistance = distance;
            foundLine = line;
          }
        }
      });

      // æ›´æ–°é«˜äº®çŠ¶æ€
      if (lastHighlightedLine && lastHighlightedLine !== foundLine) {
        // ç§»é™¤æ—§é«˜äº®
        lastHighlightedLine.classList.remove('highlighted');
      }
      if (foundLine) {
        // æ·»åŠ æ–°é«˜äº®
        foundLine.classList.add('highlighted');
        lastHighlightedLine = foundLine;

        // è°ƒè¯•è¾“å‡º
        console.log('å½“å‰é«˜äº®è¡Œ:', foundLine.textContent.substring(0, 30) + '...');
      } else if (lastHighlightedLine) {
        // å¦‚æœæ²¡æœ‰æ‰¾åˆ°æ–°è¡Œï¼Œä¸”æœ‰ä¸Šä¸€ä¸ªé«˜äº®è¡Œï¼Œç§»é™¤å®ƒ
        lastHighlightedLine.classList.remove('highlighted');
        lastHighlightedLine = null;
      }
    };

    // é¼ æ ‡ç¦»å¼€å¤„ç†
    const mouseLeaveHandler = () => {
      // æ¸…é™¤å½“å‰é«˜äº®
      if (lastHighlightedLine) {
        lastHighlightedLine.classList.remove('highlighted');
        lastHighlightedLine = null;
      }
    };

    // å­˜å‚¨å¤„ç†å‡½æ•°å¼•ç”¨
    window.eventTracker = window.eventTracker || {
      mouseFollowHandlers: []
    };
    window.eventTracker.mouseFollowHandlers.push({
      element: contentArea,
      event: 'mousemove',
      handler: mouseMoveHandler
    }, {
      element: contentArea,
      event: 'mouseleave',
      handler: mouseLeaveHandler
    });

    // æ·»åŠ äº‹ä»¶ç›‘å¬å™¨
    contentArea.addEventListener('mousemove', mouseMoveHandler);
