import React, { useState, useRef, useEffect } from 'react';
import * as pdfjsLib from 'pdfjs-dist';
import * as pdfjsViewer from 'pdfjs-dist/web/pdf_viewer';
import { marked } from 'marked'; // éœ€è¦å®‰è£…: npm install marked
import './styles/Reader.css';
import { pdfjs } from 'pdfjs-dist';

// è®¾ç½® PDF.js worker
pdfjsLib.GlobalWorkerOptions.workerSrc = `//cdnjs.cloudflare.com/ajax/libs/pdf.js/${pdfjsLib.version}/pdf.worker.min.js`;

// å…¨å±€å˜é‡å®šä¹‰
window.currentPage = 1;
window.totalPages = 0;
window.changePage = null;

// æ·»åŠ å…¨å±€äº‹ä»¶è·Ÿè¸ªå¯¹è±¡
window.eventTracker = window.eventTracker || {
  mouseFollowHandlers: []
};

// è‡ªåŠ¨é˜…è¯»å™¨å…¨å±€å¯¹è±¡
window.autoReader = {
  charPositions: [],
  intervalId: null,
  currentIndex: 0,
  speed: 300,
  pageContainer: null,
  isScrolling: false,
  currentPage: 1,
  isPageChanging: false,
  cleanup: null
};

// æ·»åŠ æ–°çš„èŠ‚å¥é˜…è¯»æ¨¡å¼ä»£ç 
// å…¨å±€å˜é‡ï¼Œç¡®ä¿åœ¨é¡µé¢åˆ‡æ¢åä»ç„¶ä¿æŒçŠ¶æ€
window.rhythmReaderGlobal = window.rhythmReaderGlobal || {
  active: false,
  speed: 300
};

// èŠ‚å¥é˜…è¯»æ¨¡å¼ - ä¸“ç”¨PDFå†…å®¹ç‰ˆ
(function() {
  console.log("èŠ‚å¥é˜…è¯»æ¨¡å¼åˆå§‹åŒ– - PDFä¸“ç”¨ç‰ˆ");

  // æ·»åŠ è°ƒè¯•é¢æ¿
  const addDebugPanel = function() {
    const panel = document.createElement('div');
    panel.id = 'rhythm-debug-panel';
    panel.style.cssText = `
      position: fixed;
      bottom: 10px;
      right: 10px;
      background-color: rgba(0, 0, 0, 0.8);
      color: white;
      padding: 10px;
      border-radius: 5px;
      z-index: 99999;
      font-size: 12px;
      width: 250px;
    `;

    const title = document.createElement('div');
    title.textContent = "PDFèŠ‚å¥é˜…è¯»æ§åˆ¶";
    title.style.fontWeight = 'bold';
    title.style.marginBottom = '8px';
    panel.appendChild(title);

    // DOMç»“æ„æŸ¥çœ‹å™¨
    const domInfo = document.createElement('div');
    domInfo.id = 'rhythm-dom-info';
    domInfo.style.cssText = `
      font-size: 10px;
      margin-bottom: 8px;
      max-height: 80px;
      overflow-y: auto;
      background: #333;
      padding: 5px;
      border-radius: 3px;
    `;
    panel.appendChild(domInfo);

    // æ‰‹åŠ¨è¾“å…¥é€‰æ‹©å™¨
    const selectorInput = document.createElement('input');
    selectorInput.id = 'rhythm-selector';
    selectorInput.placeholder = "è¾“å…¥é€‰æ‹©å™¨ (ä¾‹å¦‚: #pdf-content)";
    selectorInput.value = "#pdf-content";
    selectorInput.style.cssText = `
      width: 100%;
      padding: 5px;
      margin-bottom: 8px;
      background: #333;
      color: white;
      border: 1px solid #555;
      border-radius: 3px;
    `;
    panel.appendChild(selectorInput);

    const startButton = document.createElement('button');
    startButton.textContent = "ä½¿ç”¨æ­¤é€‰æ‹©å™¨å¯åŠ¨";
    startButton.style.cssText = `
      padding: 5px;
      width: 100%;
      background: #4CAF50;
      color: white;
      border: none;
      border-radius: 3px;
      cursor: pointer;
      margin-bottom: 8px;
    `;
    startButton.onclick = function() {
      const selector = selectorInput.value;
      rhythmReader.startWithSelector(selector);
    };
    panel.appendChild(startButton);

    // æŸ¥æ‰¾æŒ‰é’®åŒºåŸŸ
    const findButtonsArea = document.createElement('div');
    findButtonsArea.style.display = 'flex';
    findButtonsArea.style.marginBottom = '8px';

    const findByIdButton = document.createElement('button');
    findByIdButton.textContent = "æŒ‰IDæŸ¥æ‰¾";
    findByIdButton.style.cssText = `
      flex: 1;
      padding: 3px;
      background: #2196F3;
      color: white;
      border: none;
      border-radius: 3px;
      cursor: pointer;
      margin-right: 4px;
      font-size: 11px;
    `;
    findByIdButton.onclick = function() {
      findElementById('pdf-content');
    };
    findButtonsArea.appendChild(findByIdButton);

    const findByClassButton = document.createElement('button');
    findByClassButton.textContent = "æŒ‰ClassæŸ¥æ‰¾";
    findByClassButton.style.cssText = `
      flex: 1;
      padding: 3px;
      background: #9C27B0;
      color: white;
      border: none;
      border-radius: 3px;
      cursor: pointer;
      font-size: 11px;
    `;
    findByClassButton.onclick = function() {
      findElementByClass('pdf-content');
    };
    findButtonsArea.appendChild(findByClassButton);

    panel.appendChild(findButtonsArea);

    const scanButton = document.createElement('button');
    scanButton.textContent = "æ‰«æDOMç»“æ„";
    scanButton.style.cssText = `
      width: 100%;
      padding: 5px;
      background: #FF9800;
      color: white;
      border: none;
      border-radius: 3px;
      cursor: pointer;
      margin-bottom: 8px;
    `;
    scanButton.onclick = function() {
      scanDOMStructure();
    };
    panel.appendChild(scanButton);

    const stopButton = document.createElement('button');
    stopButton.textContent = "åœæ­¢é˜…è¯»";
    stopButton.style.cssText = `
      width: 100%;
      padding: 5px;
      background: #f44336;
      color: white;
      border: none;
      border-radius: 3px;
      cursor: pointer;
    `;
    stopButton.onclick = function() {
      rhythmReader.stop();
    };
    panel.appendChild(stopButton);

    document.body.appendChild(panel);
    return panel;
  };

  // æ—¥å¿—å‡½æ•° - æ›´æ–°DOMä¿¡æ¯åŒºåŸŸ
  const updateDOMInfo = function(message) {
    console.log("[PDFèŠ‚å¥é˜…è¯»] " + message);

    const domInfo = document.getElementById('rhythm-dom-info');
    if (domInfo) {
      domInfo.innerHTML = message;
    }
  };

  // ä½¿ç”¨IDæŸ¥æ‰¾å…ƒç´ 
  const findElementById = function(id) {
    updateDOMInfo(`å°è¯•æŸ¥æ‰¾IDä¸º '${id}' çš„å…ƒç´ ...`);

    // ç›´æ¥ä½¿ç”¨getElementById
    const element = document.getElementById(id);
    if (element) {
      updateDOMInfo(`<span style="color:#4CAF50">âœ“ æˆåŠŸ</span> æ‰¾åˆ°IDä¸º '${id}' çš„å…ƒç´ :<br>${element.tagName} (å†…å®¹é•¿åº¦: ${element.textContent.length})`);

      // è‡ªåŠ¨è®¾ç½®ä¸ºå½“å‰é€‰æ‹©å™¨
      const selectorInput = document.getElementById('rhythm-selector');
      if (selectorInput) {
        selectorInput.value = `#${id}`;
      }

      return element;
    }

    // å°è¯•ä½¿ç”¨å±æ€§é€‰æ‹©å™¨
    let elemByAttr = document.querySelector(`[id="${id}"]`);
    if (elemByAttr) {
      updateDOMInfo(`<span style="color:#4CAF50">âœ“ æˆåŠŸ</span> é€šè¿‡å±æ€§é€‰æ‹©å™¨æ‰¾åˆ°IDä¸º '${id}' çš„å…ƒç´ :<br>${elemByAttr.tagName} (å†…å®¹é•¿åº¦: ${elemByAttr.textContent.length})`);

      // è‡ªåŠ¨è®¾ç½®ä¸ºå½“å‰é€‰æ‹©å™¨
      const selectorInput = document.getElementById('rhythm-selector');
      if (selectorInput) {
        selectorInput.value = `[id="${id}"]`;
      }

      return elemByAttr;
    }

    // å°è¯•éƒ¨åˆ†åŒ¹é…
    const partialMatches = document.querySelectorAll(`[id*="${id}"]`);
    if (partialMatches.length > 0) {
      let result = `æ‰¾åˆ° ${partialMatches.length} ä¸ªIDåŒ…å« '${id}' çš„å…ƒç´ :<br>`;

      partialMatches.forEach((el, index) => {
        result += `${index+1}. ${el.tagName} id="${el.id}" (å†…å®¹é•¿åº¦: ${el.textContent.length})<br>`;
      });

      updateDOMInfo(result);

      // å¦‚æœåªæœ‰ä¸€ä¸ªåŒ¹é…ï¼Œè‡ªåŠ¨è®¾ç½®
      if (partialMatches.length === 1) {
        const selectorInput = document.getElementById('rhythm-selector');
        if (selectorInput) {
          selectorInput.value = `[id*="${id}"]`;
        }
      }

      return partialMatches[0];
    }

    updateDOMInfo(`<span style="color:#f44336">âœ— å¤±è´¥</span> æœªæ‰¾åˆ°IDä¸ºæˆ–åŒ…å« '${id}' çš„å…ƒç´ `);
    return null;
  };

  // ä½¿ç”¨ClassæŸ¥æ‰¾å…ƒç´ 
  const findElementByClass = function(className) {
    updateDOMInfo(`å°è¯•æŸ¥æ‰¾Classä¸ºæˆ–åŒ…å« '${className}' çš„å…ƒç´ ...`);

    // å°è¯•ç²¾ç¡®åŒ¹é…
    const exactMatches = document.getElementsByClassName(className);
    if (exactMatches.length > 0) {
      let result = `<span style="color:#4CAF50">âœ“ æˆåŠŸ</span> æ‰¾åˆ° ${exactMatches.length} ä¸ªClassä¸º '${className}' çš„å…ƒç´ :<br>`;

      Array.from(exactMatches).forEach((el, index) => {
        result += `${index+1}. ${el.tagName} class="${el.className}" (å†…å®¹é•¿åº¦: ${el.textContent.length})<br>`;
      });

      updateDOMInfo(result);

      // å¦‚æœåªæœ‰ä¸€ä¸ªåŒ¹é…ï¼Œè‡ªåŠ¨è®¾ç½®
      if (exactMatches.length === 1) {
        const selectorInput = document.getElementById('rhythm-selector');
        if (selectorInput) {
          selectorInput.value = `.${className}`;
        }
      }

      return exactMatches[0];
    }

    // å°è¯•éƒ¨åˆ†åŒ¹é…
    const partialMatches = document.querySelectorAll(`[class*="${className}"]`);
    if (partialMatches.length > 0) {
      let result = `æ‰¾åˆ° ${partialMatches.length} ä¸ªClassåŒ…å« '${className}' çš„å…ƒç´ :<br>`;

      partialMatches.forEach((el, index) => {
        result += `${index+1}. ${el.tagName} class="${el.className}" (å†…å®¹é•¿åº¦: ${el.textContent.length})<br>`;
      });

      updateDOMInfo(result);

      // å¦‚æœåªæœ‰ä¸€ä¸ªåŒ¹é…ï¼Œè‡ªåŠ¨è®¾ç½®
      if (partialMatches.length === 1) {
        const selectorInput = document.getElementById('rhythm-selector');
        if (selectorInput) {
          selectorInput.value = `[class*="${className}"]`;
        }
      }

      return partialMatches[0];
    }

    updateDOMInfo(`<span style="color:#f44336">âœ— å¤±è´¥</span> æœªæ‰¾åˆ°Classä¸ºæˆ–åŒ…å« '${className}' çš„å…ƒç´ `);
    return null;
  };

  // æ‰«æDOMç»“æ„
  const scanDOMStructure = function() {
    updateDOMInfo("æ­£åœ¨æ‰«æDOMç»“æ„...");

    // æŸ¥æ‰¾å¯èƒ½çš„PDFç›¸å…³å…ƒç´ 
    const pdfElements = [];

    // æ‰¾åˆ°æ‰€æœ‰IDæˆ–classåŒ…å«pdfçš„å…ƒç´ 
    const pdfIdElements = document.querySelectorAll('[id*="pdf"], [class*="pdf"]');
    pdfElements.push(...Array.from(pdfIdElements));

    // æ‰¾åˆ°æ‰€æœ‰å¤§çš„å†…å®¹å®¹å™¨
    const contentElements = document.querySelectorAll('div, section, article, main');
    const largeContentElements = Array.from(contentElements).filter(el => el.textContent.length > 500);

    // æŒ‰å†…å®¹é•¿åº¦æ’åº
    largeContentElements.sort((a, b) => b.textContent.length - a.textContent.length);

    // å–å‰5ä¸ªæœ€å¤§çš„
    const topContentElements = largeContentElements.slice(0, 5);

    // æ ¹æ®æ˜¯å¦åŒ…å«pdfç›¸å…³è¯æ±‡ï¼Œç»„åˆç»“æœ
    const result = [...pdfElements, ...topContentElements];

    // å»é‡
    const uniqueElements = [];
    const seen = new Set();

    result.forEach(el => {
      // ä½¿ç”¨å…ƒç´ è·¯å¾„ä½œä¸ºå”¯ä¸€æ ‡è¯†
      const path = getElementPath(el);
      if (!seen.has(path)) {
        seen.add(path);
        uniqueElements.push(el);
      }
    });

    // æ˜¾ç¤ºç»“æœ
    if (uniqueElements.length > 0) {
      let htmlResult = `æ‰¾åˆ° ${uniqueElements.length} ä¸ªå¯èƒ½çš„å†…å®¹å®¹å™¨:<br>`;

      uniqueElements.forEach((el, index) => {
        const id = el.id ? `id="${el.id}"` : '';
        const className = el.className ? `class="${el.className}"` : '';
        const selector = getElementSelector(el);

        htmlResult += `<div style="margin-bottom:5px;cursor:pointer;border-bottom:1px solid #555;padding-bottom:3px" 
                           onclick="document.getElementById('rhythm-selector').value='${selector}'">
                           ${index+1}. ${el.tagName} ${id} ${className} 
                           <br><small style="color:#aaa">å†…å®¹: ${el.textContent.length}å­—èŠ‚, é€‰æ‹©å™¨: ${selector}</small>
                       </div>`;
      });

      updateDOMInfo(htmlResult);
    } else {
      updateDOMInfo("<span style='color:#f44336'>âœ— æœªæ‰¾åˆ°ä»»ä½•å¯èƒ½çš„å†…å®¹å®¹å™¨</span>");
    }
  };

  // è·å–å…ƒç´ çš„å”¯ä¸€è·¯å¾„
  const getElementPath = function(element) {
    let path = '';
    while (element && element !== document.body) {
      let selector = element.tagName.toLowerCase();
      if (element.id) {
        selector += `#${element.id}`;
      } else if (element.className) {
        selector += `.${element.className.replace(/\s+/g, '.')}`;
      }
      path = selector + '>' + path;
      element = element.parentElement;
    }
    return path;
  };

  // è·å–å…ƒç´ çš„ç®€å•é€‰æ‹©å™¨
  const getElementSelector = function(element) {
    if (element.id) {
      return `#${element.id}`;
    } else if (element.className) {
      return `.${element.className.split(' ')[0]}`;
    } else {
      return element.tagName.toLowerCase();
    }
  };

  // èŠ‚å¥é˜…è¯»æ ¸å¿ƒåŠŸèƒ½
  const rhythmReader = {
    active: false,
    intervalId: null,
    speed: 300,
    currentIndex: 0,
    readIndices: new Set(),
    charElements: [],

    // ä½¿ç”¨é€‰æ‹©å™¨å¯åŠ¨
    startWithSelector: function(selector) {
      if (this.active) {
        this.stop();
      }

      updateDOMInfo(`å°è¯•ä½¿ç”¨é€‰æ‹©å™¨ '${selector}' å¯åŠ¨...`);

      try {
        const element = document.querySelector(selector);

        if (!element) {
          updateDOMInfo(`<span style="color:#f44336">âœ— å¤±è´¥</span> é€‰æ‹©å™¨ '${selector}' æœªåŒ¹é…ä»»ä½•å…ƒç´ `);
          return;
        }

        updateDOMInfo(`<span style="color:#4CAF50">âœ“ æˆåŠŸ</span> é€‰æ‹©å™¨ '${selector}' åŒ¹é…åˆ°å…ƒç´ :<br>${element.tagName} (å†…å®¹é•¿åº¦: ${element.textContent.length})`);

        this.startWithElement(element);
      } catch (e) {
        updateDOMInfo(`<span style="color:#f44336">âœ— é”™è¯¯</span> é€‰æ‹©å™¨è¯­æ³•é”™è¯¯: ${e.message}`);
      }
    },

    // ä½¿ç”¨å…ƒç´ å¯åŠ¨
    startWithElement: function(element) {
      if (this.active) return;

      // è®¾ç½®ä¸ºæ´»åŠ¨çŠ¶æ€
      this.active = true;

      // åˆå§‹åŒ–æ–‡æœ¬èŠ‚ç‚¹
      this.charElements = [];
      const textNodes = [];

      // æŸ¥æ‰¾æ–‡æœ¬èŠ‚ç‚¹
      const walkTextNodes = function(element, collect) {
        // æ’é™¤è¿™äº›æ ‡ç­¾
        const excludeTags = ['BUTTON', 'INPUT', 'SELECT', 'TEXTAREA', 'SCRIPT', 'STYLE'];

        if (excludeTags.includes(element.tagName)) return;

        for (let i = 0; i < element.childNodes.length; i++) {
          const node = element.childNodes[i];
          if (node.nodeType === Node.TEXT_NODE && node.nodeValue.trim() !== '') {
            collect.push(node);
          } else if (node.nodeType === Node.ELEMENT_NODE) {
            if (!excludeTags.includes(node.tagName)) {
              walkTextNodes(node, collect);
            }
          }
        }
      };

      walkTextNodes(element, textNodes);

      if (textNodes.length === 0) {
        updateDOMInfo(`<span style="color:#f44336">âœ— å¤±è´¥</span> åœ¨é€‰å®šå…ƒç´ ä¸­æœªæ‰¾åˆ°æ–‡æœ¬èŠ‚ç‚¹`);
        this.active = false;
        return;
      }

      // ä¸ºæ¯ä¸ªæ–‡æœ¬èŠ‚ç‚¹åˆ›å»ºå­—ç¬¦span
      textNodes.forEach(node => {
        const text = node.nodeValue;
        if (!text) return;

        const fragment = document.createDocumentFragment();

        for (let i = 0; i < text.length; i++) {
          const char = text[i];
          const span = document.createElement('span');
          span.textContent = char;
          span.className = 'rhythm-char';
          span.dataset.rhythmIndex = this.charElements.length;

          if (char.trim() === '') {
            span.dataset.whitespace = 'true';
          }

          span.style.cssText = `
            display: inline;
            margin: 0;
            padding: 0;
            border: none;
            background-color: transparent;
          `;

          fragment.appendChild(span);
          this.charElements.push(span);
        }

        if (node.parentNode) {
          node.parentNode.replaceChild(fragment, node);
        }
      });

      if (this.charElements.length === 0) {
        updateDOMInfo(`<span style="color:#f44336">âœ— å¤±è´¥</span> æœªèƒ½åˆ›å»ºå­—ç¬¦span`);
        this.active = false;
        return;
      }

      updateDOMInfo(`<span style="color:#4CAF50">âœ“ æˆåŠŸ</span> åˆ›å»ºäº† ${this.charElements.length} ä¸ªå­—ç¬¦span`);

      // é‡ç½®ä½ç½®
      this.currentIndex = 0;
      this.readIndices.clear();

      // æ›´æ–°é¢œè‰²
      this.updateTextColors();

      // æ»šåŠ¨åˆ°å¼€å§‹ä½ç½®
      this.scrollToCurrentChar();

      // è®¾ç½®é—´éš”
      this.intervalId = setInterval(() => this.moveToNextChar(), this.speed);

      // åœ¨DOMä¿¡æ¯åŒºåŸŸæ˜¾ç¤ºæˆåŠŸæ¶ˆæ¯
      updateDOMInfo(`<span style="color:#4CAF50">âœ“ èŠ‚å¥é˜…è¯»æ¨¡å¼å·²å¯åŠ¨</span><br>å¤„ç†äº† ${this.charElements.length} ä¸ªå­—ç¬¦<br>ä½¿ç”¨æ–¹å‘é”® â† â†’ å¯æ‰‹åŠ¨æ§åˆ¶`);

      // æ·»åŠ é”®ç›˜æ§åˆ¶
      document.addEventListener('keydown', this.handleKeyDown);
    },

    // å¤„ç†é”®ç›˜æ§åˆ¶
    handleKeyDown: function(e) {
      if (!rhythmReader.active) return;

      if (e.key === 'ArrowRight') {
        // å³ç®­å¤´ - å‰è¿›
        if (rhythmReader.intervalId) {
          clearInterval(rhythmReader.intervalId);
          rhythmReader.intervalId = null;
        }
        rhythmReader.moveToNextChar();
      } else if (e.key === 'ArrowLeft') {
        // å·¦ç®­å¤´ - åé€€
        if (rhythmReader.intervalId) {
          clearInterval(rhythmReader.intervalId);
          rhythmReader.intervalId = null;
        }

        // åé€€ä¸€ä¸ªå­—ç¬¦
        if (rhythmReader.currentIndex > 0) {
          rhythmReader.currentIndex--;
          rhythmReader.updateTextColors();
          rhythmReader.scrollToCurrentChar();
        }
      } else if (e.key === ' ') {
        // ç©ºæ ¼ - æš‚åœ/ç»§ç»­
        e.preventDefault();

        if (rhythmReader.intervalId) {
          clearInterval(rhythmReader.intervalId);
          rhythmReader.intervalId = null;
          updateDOMInfo("<span style='color:#FF9800'>â¸ èŠ‚å¥é˜…è¯»å·²æš‚åœ</span><br>æŒ‰ç©ºæ ¼ç»§ç»­ï¼Œæ–¹å‘é”®æ‰‹åŠ¨æ§åˆ¶");
        } else {
          rhythmReader.intervalId = setInterval(() => rhythmReader.moveToNextChar(), rhythmReader.speed);
          updateDOMInfo("<span style='color:#4CAF50'>â–¶ èŠ‚å¥é˜…è¯»å·²æ¢å¤</span>");
        }
      }
    },

    // å¼€å§‹èŠ‚å¥é˜…è¯» - è‡ªåŠ¨é€‰æ‹©å®¹å™¨
    start: function() {
      // å…ˆå°è¯•æœ€å¯èƒ½çš„é€‰æ‹©å™¨
      const selectors = [
        '#pdf-content',
        '.pdf-content',
        '[id*="pdf"]',
        '[class*="pdf"]',
        'article',
        'main',
        '.article',
        '.content'
      ];

      for (const selector of selectors) {
        try {
          const element = document.querySelector(selector);
          if (element && element.textContent.length > 100) {
            updateDOMInfo(`è‡ªåŠ¨é€‰æ‹©é€‰æ‹©å™¨: ${selector}`);
            return this.startWithSelector(selector);
          }
        } catch (e) {}
      }

      // å¦‚æœæ²¡æœ‰æ‰¾åˆ°åˆé€‚çš„é€‰æ‹©å™¨ï¼Œå°è¯•æ‰«æDOM
      scanDOMStructure();
    },

    // åœæ­¢
    stop: function() {
      if (!this.active) return;

      if (this.intervalId) {
        clearInterval(this.intervalId);
        this.intervalId = null;
      }

      this.active = false;

      // ç§»é™¤é”®ç›˜äº‹ä»¶ç›‘å¬
      document.removeEventListener('keydown', this.handleKeyDown);

      // æ¢å¤åŸå§‹æ–‡æœ¬
      const charSpans = document.querySelectorAll('.rhythm-char');

      // æŒ‰çˆ¶èŠ‚ç‚¹åˆ†ç»„
      const parentMap = new Map();

      charSpans.forEach(span => {
        const parent = span.parentNode;
        if (!parent) return;

        if (!parentMap.has(parent)) {
          parentMap.set(parent, []);
        }
        parentMap.get(parent).push(span);
      });

      // å¯¹æ¯ä¸ªçˆ¶èŠ‚ç‚¹ï¼Œå°†å­—ç¬¦spanåˆå¹¶å›æ–‡æœ¬èŠ‚ç‚¹
      parentMap.forEach((spans, parent) => {
        if (spans.length === 0) return;

        // æ’åºspansç¡®ä¿é¡ºåºæ­£ç¡®
        spans.sort((a, b) => {
          const indexA = parseInt(a.dataset.rhythmIndex || '0');
          const indexB = parseInt(b.dataset.rhythmIndex || '0');
          return indexA - indexB;
        });

        // å°†æ‰€æœ‰spançš„æ–‡æœ¬åˆå¹¶ä¸ºä¸€ä¸ªæ–‡æœ¬èŠ‚ç‚¹
        try {
          const text = spans.map(span => span.textContent).join('');
          const textNode = document.createTextNode(text);

          // æ›¿æ¢ç¬¬ä¸€ä¸ªspanï¼Œç„¶åç§»é™¤å…¶ä½™çš„
          parent.replaceChild(textNode, spans[0]);
          for (let i = 1; i < spans.length; i++) {
            if (spans[i].parentNode === parent) {
              parent.removeChild(spans[i]);
            }
          }
        } catch (e) {
          console.error("æ¢å¤æ–‡æœ¬æ—¶å‡ºé”™:", e);
        }
      });

      this.charElements = [];
      updateDOMInfo("<span style='color:#f44336'>â–  èŠ‚å¥é˜…è¯»å·²åœæ­¢</span>");
    },

    // æ›´æ–°é¢œè‰²
    updateTextColors: function() {
      if (this.charElements.length === 0) return;

      this.charElements.forEach((span, index) => {
        let color, fontWeight = 'inherit';

        if (index === this.currentIndex) {
          color = '#00aa00'; // å½“å‰å­—ç¬¦ - ç»¿è‰²
          fontWeight = 'bold';
        } else if (index === this.currentIndex + 1 || index === this.currentIndex + 2) {
          color = '#aaaaaa'; // ä¸‹ä¸€ä¸ªå­—ç¬¦ - ç°è‰²
        } else if (this.readIndices.has(index)) {
          color = '#ffffff'; // å·²è¯»å­—ç¬¦ - ç™½è‰²
        } else {
          color = 'rgba(160, 160, 160, 0.25)'; // æœªè¯»å­—ç¬¦ - æ·¡ç°è‰²
        }

        span.style.color = color;
        span.style.fontWeight = fontWeight;
      });
    },

    // ç§»åŠ¨åˆ°ä¸‹ä¸€ä¸ªå­—ç¬¦
    moveToNextChar: function() {
      if (!this.active || this.charElements.length === 0) return;

      this.readIndices.add(this.currentIndex);

      do {
        this.currentIndex++;

        if (this.currentIndex >= this.charElements.length) {
          updateDOMInfo("<span style='color:#FF9800'>ğŸ å·²åˆ°è¾¾æ–‡æœ¬æœ«å°¾</span>");
          this.stop();
          return;
        }
      } while (
        this.currentIndex < this.charElements.length && 
        this.charElements[this.currentIndex] && 
        this.charElements[this.currentIndex].dataset.whitespace === 'true'
      );

      this.updateTextColors();
      this.scrollToCurrentChar();
    },

    // æ»šåŠ¨åˆ°å½“å‰å­—ç¬¦
    scrollToCurrentChar: function() {
      const span = this.charElements[this.currentIndex];
      if (!span) return;

      const rect = span.getBoundingClientRect();

      window.scrollTo({
        top: window.scrollY + rect.top - (window.innerHeight * 0.4),
        behavior: 'smooth'
      });
    }
  };

  // æ¨¡å¼åˆ‡æ¢å¤„ç†
  const originalReadingModeChanged = window.readingModeChanged;

  window.readingModeChanged = function(mode) {
    console.log(`æ¨¡å¼å˜æ›´ä¸º: ${mode}`);

    // å…ˆåœæ­¢å·²æœ‰å®ä¾‹
    if (rhythmReader.active) {
      rhythmReader.stop();
    }

    // è°ƒç”¨åŸå§‹å‡½æ•°
    if (typeof originalReadingModeChanged === 'function') {
      try {
        originalReadingModeChanged(mode);
      } catch(e) {
        console.error("åŸå§‹å‡½æ•°é”™è¯¯:", e);
      }
    }

    // èŠ‚å¥é˜…è¯»æ¨¡å¼
    if (mode === 'rhythm' || mode === 'rhythmReading') {
      console.log("é€‰æ‹©äº†èŠ‚å¥é˜…è¯»æ¨¡å¼ï¼Œç«‹å³å¯åŠ¨");

      // å°è¯•å¤šç§å¯èƒ½çš„é€‰æ‹©å™¨
      setTimeout(() => {
        rhythmReader.startWithSelector('#pdf-content');
      }, 300);
    }
  };

  // å°†å¯¹è±¡æš´éœ²åˆ°å…¨å±€
  window.rhythmReader = rhythmReader;

  // ç­‰å¾…DOMåŠ è½½å®Œæˆ
  document.addEventListener('DOMContentLoaded', function() {
    // å»¶è¿Ÿä¸€ç‚¹ä»¥ç¡®ä¿æ‰€æœ‰å†…å®¹åŠ è½½å®Œæ¯•
    setTimeout(function() {
      // æ·»åŠ è°ƒè¯•é¢æ¿
      addDebugPanel();

      // æ£€æŸ¥å½“å‰æ˜¯å¦æ˜¯èŠ‚å¥é˜…è¯»æ¨¡å¼
      const selects = document.querySelectorAll('select');
      for (const select of selects) {
        if ((select.value || '').includes('rhythm')) {
          console.log("å½“å‰å·²é€‰æ‹©èŠ‚å¥é˜…è¯»æ¨¡å¼");
          rhythmReader.startWithSelector('#pdf-content');
          break;
        }
      }

      // åœ¨ä¸å¹²æ‰°å…¶ä»–åŠŸèƒ½çš„æƒ…å†µä¸‹ï¼Œå°è¯•æ¢æµ‹pdf-content
      setTimeout(function() {
        findElementById('pdf-content');
      }, 500);
    }, 300);
  });

  // å¦‚æœDOMå·²ç»åŠ è½½å®Œæˆï¼Œç›´æ¥åˆå§‹åŒ–
  if (document.readyState !== 'loading') {
    addDebugPanel();
    setTimeout(function() {
      findElementById('pdf-content');
    }, 500);
  }

  console.log("PDFèŠ‚å¥é˜…è¯»æ¨¡å¼åˆå§‹åŒ–å®Œæˆ");
})();

// å®šä¹‰ä¸€ä¸ªå…¨å±€å‡½æ•°ç”¨äºä»åº”ç”¨ä»£ç ä¸­å¯åŠ¨èŠ‚å¥é˜…è¯»
window.activateRhythmReadingMode = function() {
  console.log("å°è¯•ä»åº”ç”¨ä»£ç ä¸­å¯åŠ¨èŠ‚å¥é˜…è¯»æ¨¡å¼");
  if (window.rhythmReader) {
    if (window.rhythmReader.active) {
      console.log("èŠ‚å¥é˜…è¯»æ¨¡å¼å·²ç»åœ¨è¿è¡Œ");
    } else {
      window.rhythmReader.start();
    }
  } else {
    console.error("èŠ‚å¥é˜…è¯»æ¨¡å¼æœªåˆå§‹åŒ–");
    alert("æ— æ³•å¯åŠ¨èŠ‚å¥é˜…è¯»æ¨¡å¼ï¼šç»„ä»¶æœªåˆå§‹åŒ–");
