  // å°†æ–‡æœ¬åˆ†å‰²æˆå¥å­
  const splitIntoSentences = (text) => {
    // åŒ¹é…å¥å·ã€é—®å·ã€æ„Ÿå¹å·åè·Ÿç©ºæ ¼æˆ–ç»“æŸçš„æ¨¡å¼
    // æ”¯æŒä¸­è‹±æ–‡æ ‡ç‚¹ç¬¦å·
    const sentenceRegex = /([.!?ã€‚ï¼ï¼Ÿï¼›;]+\s*)/g;

    // åˆ†å‰²æ–‡æœ¬æˆå¥å­
    const sentences = [];
    let lastIndex = 0;
    let match;

    while ((match = sentenceRegex.exec(text)) !== null) {
      const endIndex = match.index + match[0].length;
      const sentence = text.substring(lastIndex, endIndex).trim();

      // é¿å…æ·»åŠ ç©ºå¥å­
      if (sentence) {
        sentences.push(sentence);
      }

      lastIndex = endIndex;
    }

    // å¤„ç†æœ€åä¸€éƒ¨åˆ†ï¼ˆå¦‚æœæ²¡æœ‰ä»¥å¥å·ç­‰ç»“å°¾ï¼‰
    if (lastIndex < text.length) {
      const lastSentence = text.substring(lastIndex).trim();
      if (lastSentence) {
        sentences.push(lastSentence);
      }
    }

    return sentences;
  };

  // ä¸“é—¨ä¸ºé—ªè¯»æ¨¡å¼åˆ†å‰²æ–‡æœ¬ï¼Œä»¥ä»»æ„æ ‡ç‚¹ç¬¦å·ä¸ºåˆ†éš”ç‚¹
  const splitIntoFlashReadingSegments = (text) => {
    // åŒ¹é…ä»»æ„æ ‡ç‚¹ç¬¦å·åŒ…æ‹¬ä¸­è‹±æ–‡æ ‡ç‚¹
    const sentenceRegex = /([,.!?:;ï¼Œã€‚ï¼ï¼Ÿï¼šï¼›ã€]+\s*)/g;

    // åˆ†å‰²æ–‡æœ¬æˆçŸ­å¥
    const segments = [];
    let lastIndex = 0;
    let match;

    while ((match = sentenceRegex.exec(text)) !== null) {
      const endIndex = match.index + match[0].length;
      const segment = text.substring(lastIndex, endIndex).trim();

      // é¿å…æ·»åŠ ç©ºæ®µè½
      if (segment) {
        segments.push(segment);
      }

      lastIndex = endIndex;
    }

    // å¤„ç†æœ€åä¸€éƒ¨åˆ†ï¼ˆå¦‚æœæ²¡æœ‰ä»¥æ ‡ç‚¹ç¬¦å·ç»“å°¾ï¼‰
    if (lastIndex < text.length) {
      const lastSegment = text.substring(lastIndex).trim();
      if (lastSegment) {
        segments.push(lastSegment);
      }
    }

    return segments;
  };

  // æ ¼å¼åŒ–å¥å­ä¸ºHTML
