          }, 100);
          break;
        default:
          processedContent = formatSentencesToHtml(sentences);
      }

      // è®¾ç½®å†…å®¹
      setContent(processedContent);
      setFileName(file.name);

      // ç®€å•æ¨¡æ‹Ÿé¡µæ•°
      setTotalPages(1);
