  // è·å–æ‰€æœ‰å­—ç¬¦ä½ç½®ï¼ˆä¼˜åŒ–ç‰ˆ - è®°å½•å­—ç¬¦åº•éƒ¨ä½ç½®ï¼‰
  const getAllCharacterPositions = (container) => {
    const walker = document.createTreeWalker(container, NodeFilter.SHOW_TEXT);
    const positions = [];

    while (walker.nextNode()) {
      const node = walker.currentNode;
      const text = node.textContent;

      // è·³è¿‡å®Œå…¨ç©ºç™½çš„æ–‡æœ¬èŠ‚ç‚¹
      if (!text.trim()) continue;

      // é€å­—ç¬¦å¤„ç†
      for (let i = 0; i < text.length; i++) {
        try {
          const range = document.createRange();
          range.setStart(node, i);
          range.setEnd(node, i + 1);

          const rect = range.getBoundingClientRect();
          if (rect.width === 0 && rect.height === 0) continue;

          // è®°å½•å­—ç¬¦åº•éƒ¨ä¸­å¿ƒä½ç½®ï¼Œè€Œä¸æ˜¯ä¸­å¿ƒä½ç½®
          positions.push({
            x: rect.left + rect.width/2,  // æ°´å¹³ä¸­å¿ƒ
            y: rect.bottom,               // å­—ç¬¦åº•éƒ¨
            char: text[i]
          });
        } catch (error) {
          console.warn('è·å–å­—ç¬¦ä½ç½®æ—¶å‡ºé”™:', error);
          continue;
        }
      }
    }

    // è¿‡æ»¤æ— æ•ˆä½ç½®å¹¶æ’åº
    return positions
      .filter(p => p.char.trim() !== '')
      .sort((a, b) => {
        // å…ˆæŒ‰è¡Œæ’åºï¼ŒåŒè¡ŒæŒ‰åˆ—æ’åº
        const yDiff = a.y - b.y;
        if (Math.abs(yDiff) > 5) { // 5pxé˜ˆå€¼åˆ¤æ–­æ˜¯å¦åŒä¸€è¡Œ
          return yDiff;
        }
        return a.x - b.x;
      });
  };

  // ç¡®ä¿å…ƒç´ åœ¨å®¹å™¨ä¸­å±…ä¸­çš„ä¸“ç”¨å‡½æ•°
  const scrollElementToCenter = (elementY, container) => {
    // åˆ¤æ–­æ˜¯å®¹å™¨æ»šåŠ¨è¿˜æ˜¯çª—å£æ»šåŠ¨
    const isContainerScrollable = container.scrollHeight > container.clientHeight && 
      ['auto', 'scroll', 'overlay'].includes(window.getComputedStyle(container).overflowY);

    // è®¡ç®—éœ€è¦æ»šåŠ¨çš„ç›®æ ‡ä½ç½®
    let targetScrollTop;

    if (isContainerScrollable) {
      // å®¹å™¨æ»šåŠ¨ï¼šè®¡ç®—å°†å…ƒç´ æ”¾åœ¨å®¹å™¨ä¸­å¿ƒæ‰€éœ€çš„æ»šåŠ¨é‡
      const containerVisibleHeight = container.clientHeight;
      targetScrollTop = elementY - (containerVisibleHeight / 2);

      // æ‰§è¡Œæ»šåŠ¨
      container.scrollTo({
        top: Math.max(0, targetScrollTop), // é˜²æ­¢æ»šåŠ¨åˆ°è´Ÿå€¼
        behavior: 'smooth'
      });
    } else {
      // çª—å£æ»šåŠ¨ï¼šè®¡ç®—å°†å…ƒç´ æ”¾åœ¨çª—å£ä¸­å¿ƒæ‰€éœ€çš„æ»šåŠ¨é‡
      const absoluteElementY = elementY + container.getBoundingClientRect().top;
      targetScrollTop = absoluteElementY - (window.innerHeight / 2);

      // æ‰§è¡Œæ»šåŠ¨
      window.scrollTo({
        top: Math.max(0, targetScrollTop), // é˜²æ­¢æ»šåŠ¨åˆ°è´Ÿå€¼
        behavior: 'smooth'
      });
    }

    return targetScrollTop;
  };

  // åˆ›å»ºå­—ç¬¦åº•éƒ¨æ˜¾ç¤ºçš„é˜…è¯»å…‰æ ‡ï¼ˆç¡®ä¿ä¸æ»šåŠ¨å…¼å®¹ï¼‰
  const createReaderCursor = () => {
    const cursor = document.createElement('div');
    cursor.id = 'auto-reader-cursor';
    cursor.style.cssText = `
      position: absolute;
      width: 10px;
      height: 10px;
