    `;

    // æ·»åŠ åŠ¨ç”»æ ·å¼
    if (!document.getElementById('reader-cursor-animation')) {
      const style = document.createElement('style');
      style.id = 'reader-cursor-animation';
      style.textContent = `
        @keyframes pulse-bottom {
          0% { transform: translateX(-50%) scale(1); opacity: 0.8; }
