            className="mode-select"
          >
            <option value="normal">æ™®é€šæ¨¡å¼</option>
            <option value="static">é™æ€é«˜äº®</option>
            <option value="follow-mouse">é¼ æ ‡è·Ÿéš</option>
            <option value="rhythm">èŠ‚å¥é˜…è¯»</option>
            <option value="flash-reading">é—ªè¯»æ¨¡å¼</option>
          </select>
        </div>

        <div className="upload-section">
