        const lineHighlight = document.createElement('div');
        lineHighlight.className = 'line-highlight mouse-highlight';

        // 计算相对于段落的位置
        const paragraphRect = targetParagraph.getBoundingClientRect();
        const top = targetRect.top - paragraphRect.top;
        const left = targetRect.left - paragraphRect.left;

        // 设置高亮样式
        lineHighlight.style.position = 'absolute';
        lineHighlight.style.left = `${left}px`;
        lineHighlight.style.top = `${top}px`;
        lineHighlight.style.width = `${targetRect.width}px`;
        lineHighlight.style.height = `${targetRect.height}px`;
        lineHighlight.style.pointerEvents = 'none';
        lineHighlight.style.backgroundColor = 'rgba(255, 165, 0, 0.3)';
        lineHighlight.style.borderRadius = '4px';
        lineHighlight.style.boxShadow = '0 0 8px rgba(255, 165, 0, 0.5)';
        lineHighlight.style.zIndex = '5';
        lineHighlight.style.transition = 'all 0.1s ease';

