    const valueDisplay = document.createElement('span');
    valueDisplay.textContent = `${speed}ms`;
    valueDisplay.style.marginLeft = '8px';
    valueDisplay.style.minWidth = '60px';

    input.addEventListener('input', function() {
      const newSpeed = parseInt(this.value);
      speed = newSpeed;
      window.rhythmReaderGlobal.speed = newSpeed;
      valueDisplay.textContent = `${newSpeed}ms`;

      if (intervalId) {
        clearInterval(intervalId);
        intervalId = setInterval(moveToNextChar, newSpeed);
      }
    });

    container.appendChild(input);
    container.appendChild(valueDisplay);
    document.body.appendChild(container);
  }

  // 初始化文本节点（将文本分解为单独的字符span）
  function initializeTextNodes() {
    // 重置字符元素数组
    charElements = [];

    // 获取内容容器
    const contentContainer = document.querySelector('.page-content') || document.body;

    // 查找所有文本节点并处理
    const textNodes = [];
    findTextNodes(contentContainer, textNodes);

    if (textNodes.length === 0) {
      console.error("未找到文本节点");
      return false;
    }

    console.log(`找到 ${textNodes.length} 个文本节点`);

    // 为每个文本节点创建字符span
    textNodes.forEach(node => {
      const text = node.nodeValue;
      if (!text || text.trim() === '') return;

      // 创建文档片段来存放字符span
      const fragment = document.createDocumentFragment();

      // 为每个字符创建span
      for (let i = 0; i < text.length; i++) {
        const char = text[i];
        const span = document.createElement('span');
        span.textContent = char;
        span.className = 'rhythm-char';
        span.dataset.rhythmIndex = charElements.length;

        // 如果是空白字符，添加一个标志
        if (char.trim() === '') {
          span.dataset.whitespace = 'true';
        }

        // 保持原始样式，重要：不设置任何背景
        span.style.display = 'inline';
        span.style.margin = '0';
        span.style.padding = '0';
        span.style.border = 'none';
        span.style.backgroundColor = 'transparent';

        fragment.appendChild(span);
        charElements.push(span);
      }

      // 替换原始节点
      if (node.parentNode) {
        node.parentNode.replaceChild(fragment, node);
      }
    });

    console.log(`总共创建了 ${charElements.length} 个字符span`);

    // 估计行高
    if (charElements.length > 1) {
      for (let i = 1; i < charElements.length; i++) {
        const span1 = charElements[i-1];
        const span2 = charElements[i];
        if (span1 && span2) {
          const rect1 = span1.getBoundingClientRect();
          const rect2 = span2.getBoundingClientRect();
          const yDiff = Math.abs(rect2.top - rect1.top);
          if (yDiff > 5) { // 认为有垂直距离差的是不同行
            lineHeight = yDiff;
            scrollSafeZone = lineHeight * 2.5;
            console.log(`估计行高为 ${lineHeight}px`);
            break;
          }
        }
      }
    }

    return charElements.length > 0;
  }

  // 递归查找所有文本节点
  function findTextNodes(element, textNodes) {
    // 处理子节点
    for (let i = 0; i < element.childNodes.length; i++) {
      const node = element.childNodes[i];

      // 如果是文本节点且有内容
      if (node.nodeType === Node.TEXT_NODE && node.nodeValue.trim() !== '') {
        textNodes.push(node);
      } 
      // 如果是元素节点，递归处理
      else if (node.nodeType === Node.ELEMENT_NODE) {
        // 跳过读者UI元素
        if (node.id === 'rhythm-speed-slider' || 
            node.className === 'rhythm-char') {
          continue;
        }
        findTextNodes(node, textNodes);
      }
    }
  }

  // 更新文本颜色
  function updateTextColors() {
    // 更新所有字符的颜色
    charElements.forEach((span, index) => {
      // 确定颜色
      let color;
      let fontWeight = 'inherit';

      if (index === currentIndex) {
        color = '#00aa00'; // 当前阅读的字 - 绿色
        fontWeight = 'bold';
      } else if (index === currentIndex + 1 || index === currentIndex + 2) {
        color = '#aaaaaa'; // 接下来两个字 - 淡灰色
      } else if (readIndices.has(index)) {
        color = '#ffffff'; // 已读字符 - 白色（表示已读）
      } else {
        color = 'rgba(160, 160, 160, 0.25)'; // 未读字符 - 非常淡的灰色（可见但不明显）
      }

      span.style.color = color;
      span.style.fontWeight = fontWeight;

      // 确保没有背景
      span.style.backgroundColor = 'transparent';
      span.style.background = 'none';
    });
  }

  // 查找"下一页"按钮并点击它
  function clickNextPageButton() {
    // 尝试多种可能的选择器来找到下一页按钮
    const nextPageSelectors = [
      'button.next-page', 
      'button[aria-label="下一页"]',
      'button:contains("下一页")',
      'a.next-page',
      'a[aria-label="下一页"]',
      'a:contains("下一页")',
      '.next-page',
      '#next-page',
      'button:contains("Next")',
      'a:contains("Next")',
      '[aria-label="Next"]',
      'button.next',
      'a.next'
    ];

    let nextPageButton = null;

    // 尝试通过选择器找到按钮
    for (const selector of nextPageSelectors) {
      try {
        const elements = document.querySelectorAll(selector);
        for (const el of elements) {
          if (el.offsetParent !== null) { // 检查元素是否可见
            nextPageButton = el;
            break;
          }
        }
        if (nextPageButton) break;
      } catch (e) {
        // 某些选择器可能不被支持，忽略错误
      }
    }

    // 如果没找到，尝试按文本内容查找
    if (!nextPageButton) {
      const buttons = document.querySelectorAll('button, a, div[role="button"]');
      for (const button of buttons) {
        const text = button.textContent.trim().toLowerCase();
        if ((text.includes('下一页') || text.includes('next') || text === '>' || text === '→') && 
            button.offsetParent !== null) {
          nextPageButton = button;
          break;
        }
      }
    }

    if (nextPageButton) {
      // 保存当前状态
      const wasActive = active;

      // 暂停阅读
      stopReading(false);

      // 保存状态到本地存储
      localStorage.setItem('rhythmReading', 'true');

      // 模拟点击
      nextPageButton.click();

      // 设置重新启动定时器
      setTimeout(() => {
        if (localStorage.getItem('rhythmReading') === 'true') {
          startReading();
        }
      }, 1500);

      return true;
    }

    return false;
  }

  // 移动到下一个字符
  function moveToNextChar() {
    // 将当前字符标记为已读
    readIndices.add(currentIndex);

    // 移动到下一个非空白字符
    do {
      currentIndex++;

      // 检查是否到达末尾
      if (currentIndex >= charElements.length) {
        console.log("已到达页尾，尝试翻页");

        // 尝试点击下一页按钮
        if (!clickNextPageButton()) {
          // 如果没有找到下一页按钮，完全停止阅读
          stopReading(true);
        }
        return;
      }
    } while (
      currentIndex < charElements.length && 
      charElements[currentIndex] && 
      charElements[currentIndex].dataset.whitespace === 'true'
    );

    // 更新颜色
    updateTextColors();

    // 滚动到当前字符
    scrollToCurrentChar();
  }

  // 滚动到当前字符
  function scrollToCurrentChar() {
    const span = charElements[currentIndex];
    if (!span) return;

    const rect = span.getBoundingClientRect();
    const contentContainer = document.querySelector('.page-content');

    // 确定滚动容器
    const isContainerScrollable = contentContainer && 
      contentContainer.scrollHeight > contentContainer.clientHeight &&
      ['auto', 'scroll', 'overlay'].includes(window.getComputedStyle(contentContainer).overflowY);

    if (isContainerScrollable) {
      // 容器滚动
      const containerRect = contentContainer.getBoundingClientRect();
      const relativeTop = rect.top - containerRect.top + contentContainer.scrollTop;

      const visibleTop = contentContainer.scrollTop;
      const visibleBottom = visibleTop + contentContainer.clientHeight;

      if (relativeTop < visibleTop + lineHeight || relativeTop > visibleBottom - lineHeight) {
        contentContainer.scrollBy({
          top: lineHeight * 2 * (relativeTop > visibleBottom - lineHeight ? 1 : -1),
          behavior: 'smooth'
        });
      }
    } else {
      // 窗口滚动
      const viewportTop = window.scrollY;
      const viewportBottom = viewportTop + window.innerHeight;
      const absoluteTop = rect.top + window.scrollY;

      if (absoluteTop < viewportTop + lineHeight || absoluteTop > viewportBottom - lineHeight) {
        window.scrollBy({
          top: lineHeight * 2 * (absoluteTop > viewportBottom - lineHeight ? 1 : -1),
          behavior: 'smooth'
        });
      }
    }
  }

  // 恢复原始文本
  function restoreOriginalText() {
    // 获取所有已创建的字符span
    const charSpans = document.querySelectorAll('.rhythm-char');

    // 按父节点分组
    const parentNodeMap = new Map();

    charSpans.forEach(span => {
      const parent = span.parentNode;
      if (!parent) return;

      if (!parentNodeMap.has(parent)) {
        parentNodeMap.set(parent, []);
      }
      parentNodeMap.get(parent).push(span);
    });

    // 对每个父节点，将连续的字符span合并回文本节点
    parentNodeMap.forEach((spans, parent) => {
      if (spans.length === 0) return;

      // 排序spans确保顺序正确
      spans.sort((a, b) => {
        const indexA = parseInt(a.dataset.rhythmIndex || '0');
        const indexB = parseInt(b.dataset.rhythmIndex || '0');
        return indexA - indexB;
      });

      // 根据连续性分组
      const groups = [];
      let currentGroup = [spans[0]];

      for (let i = 1; i < spans.length; i++) {
        const prevIndex = parseInt(spans[i-1].dataset.rhythmIndex || '0');
        const currentIndex = parseInt(spans[i].dataset.rhythmIndex || '0');

        if (currentIndex === prevIndex + 1 && spans[i].previousSibling === spans[i-1]) {
          currentGroup.push(spans[i]);
        } else {
          groups.push(currentGroup);
          currentGroup = [spans[i]];
        }
      }

      groups.push(currentGroup);

      // 对每个连续组替换为文本节点
      groups.forEach(group => {
        if (group.length === 0) return;

        try {
          const text = group.map(span => span.textContent).join('');
          const textNode = document.createTextNode(text);

          // 替换第一个span，然后移除其余的
          if (group[0].parentNode === parent) {
            parent.replaceChild(textNode, group[0]);
            for (let i = 1; i < group.length; i++) {
              if (group[i].parentNode === parent) {
                parent.removeChild(group[i]);
              }
            }
          }
        } catch (e) {
          console.error("恢复文本时出错:", e);
        }
      });
    });

    // 清除字符元素数组
    charElements = [];
  }

  // 为新页面加载设置监听器
  function setupPageLoadListener() {
    console.log("设置页面加载监听器");

    // 观察DOM变化
    const observer = new MutationObserver((mutations) => {
      if (document.body.dataset.rhythmReading === "true") {
        for (const mutation of mutations) {
          if (mutation.type === 'childList' && mutation.addedNodes.length > 0) {
            console.log("检测到页面变化，准备重启节奏阅读");

            // 延迟一下，等待页面完全加载
            setTimeout(() => {
              console.log("重新启动节奏阅读");
              document.body.dataset.rhythmReading = "false";
              startReading();
            }, 800);

            // 只执行一次
            observer.disconnect();
            break;
          }
        }
      }
    });

    // 观察整个body的变化
    observer.observe(document.body, { 
      childList: true,
      subtree: true
    });

    // 额外的安全措施：如果2秒后观察器没有触发，也重启阅读
    setTimeout(() => {
      if (document.body.dataset.rhythmReading === "true") {
        console.log("超时后重新启动节奏阅读");
        document.body.dataset.rhythmReading = "false";
        startReading();
      }
    }, 2000);
  }

  // 停止节奏阅读
  function stopReading(stopCompletely = true) {
    if (intervalId) {
      clearInterval(intervalId);
      intervalId = null;
    }

    active = false;

    if (stopCompletely) {
      localStorage.removeItem('rhythmReading');
    }

    // 恢复原始文本
    restoreOriginalText();

    // 如果完全停止，移除速度滑块
    if (stopCompletely) {
      const slider = document.getElementById('rhythm-speed-slider');
      if (slider) slider.remove();
    }
  }

  // 启动节奏阅读
  function startReading() {
    console.log("启动节奏阅读模式");

    // 先停止已有的
    stopReading();

    // 设置为活动状态
    active = true;
    localStorage.setItem('rhythmReading', 'true');

    // 初始化文本
    if (!initializeTextNodes()) {
      console.error("无法初始化文本节点");
      return;
    }

    // 重置阅读位置
    currentIndex = 0;
    readIndices.clear();

    // 添加速度滑块 - 放在页面上方
    addSpeedSlider();

    // 更新颜色
    updateTextColors();

    // 滚动到开始位置
    scrollToCurrentChar();

    // 设置阅读间隔
    intervalId = setInterval(moveToNextChar, speed);
  }

  // 检查是否应该自动启动（比如从上一页翻过来）
  if (localStorage.getItem('rhythmReading') === 'true') {
    console.log("检测到之前的阅读状态，自动恢复");
    startReading();
  } else {
    // 首次启动
    startReading();
  }

  // 首次设置页面加载监听器
  setupPageLoadListener();

  // 对外暴露的方法
  return {
    start: startReading,
    stop: function() { stopReading(); }
  };
}

// 创建节奏阅读实例并保存到全局
window.rhythmReader = setupRhythmReading();

