        range.selectNodeContents(node);
        const rects = range.getClientRects();
        for (let j = 0; j < rects.length; j++) {
          const rect = rects[j];
          if (y >= rect.top && y <= rect.bottom && 
              x >= rect.left && x <= rect.right) {
            return node;
