    document.querySelectorAll('.line-highlight').forEach(el => el.remove());

    const mouseTarget = document.elementFromPoint(e.clientX, e.clientY);

    if (mouseTarget && 
       (mouseTarget.classList.contains('pdf-paragraph') || 
        mouseTarget.tagName === 'P' || 
        mouseTarget.tagName === 'SPAN')) {

      const targetParagraph = mouseTarget.closest('.pdf-paragraph');
      if (!targetParagraph) return;

      // 获取段落文本节点
      const textNode = getTextNodeAtPosition(targetParagraph, e.clientX, e.clientY);
      if (!textNode) return;

      // 获取精确的行位置和尺寸
      const range = document.createRange();
      range.selectNodeContents(textNode);
      const rects = range.getClientRects();

      // 找到鼠标所在的具体行
      let targetRect = null;
      for (let i = 0; i < rects.length; i++) {
        const rect = rects[i];
        if (e.clientY >= rect.top && e.clientY <= rect.bottom) {
          targetRect = rect;
          break;
        }
      }

      if (targetRect) {
        // 创建高亮元素
        const lineHighlight = document.createElement('div');
        lineHighlight.className = 'line-highlight mouse-highlight';

        // 计算相对于段落的位置
        const paragraphRect = targetParagraph.getBoundingClientRect();
        const top = targetRect.top - paragraphRect.top;
        const left = targetRect.left - paragraphRect.left;

        // 设置高亮样式
        lineHighlight.style.position = 'absolute';
        lineHighlight.style.left = `${left}px`;
        lineHighlight.style.top = `${top}px`;
        lineHighlight.style.width = `${targetRect.width}px`;
        lineHighlight.style.height = `${targetRect.height}px`;
        lineHighlight.style.pointerEvents = 'none';
        lineHighlight.style.backgroundColor = 'rgba(255, 165, 0, 0.3)';
        lineHighlight.style.borderRadius = '4px';
        lineHighlight.style.boxShadow = '0 0 8px rgba(255, 165, 0, 0.5)';
        lineHighlight.style.zIndex = '5';
        lineHighlight.style.transition = 'all 0.1s ease';

        // 确保段落有相对定位
          targetParagraph.style.position = 'relative';
        targetParagraph.appendChild(lineHighlight);
      }
    }
  };

  // 清理所有高亮
  const clearAllHighlights = () => {
    document.querySelectorAll('.line-highlight').forEach(el => el.remove());
  };

  // 添加自动阅读器样式
  const addAutoReaderStyles = () => {
    const style = document.createElement('style');
    style.textContent = `
      @keyframes pulse {
        0% { transform: translate(-50%, -50%) scale(0.8); opacity: 0.8; }
        50% { transform: translate(-50%, -50%) scale(1.2); opacity: 1; }
        100% { transform: translate(-50%, -50%) scale(0.8); opacity: 0.8; }
      }
      .speed-control {
        position: fixed;
        top: 20px;
        right: 20px;
        background: rgba(0,0,0,0.7);
        padding: 10px;
        border-radius: 8px;
        color: white;
        z-index: 10000;
      }
      .speed-control button {
        margin: 0 5px;
        padding: 5px 10px;
        background: #4CAF50;
        border: none;
        border-radius: 4px;
        color: white;
        cursor: pointer;
      }
    `;
    document.head.appendChild(style);
  };

  // 在组件挂载时添加样式
  useEffect(() => {
    addAutoReaderStyles();
  }, []);

  // 初始化鼠标跟随模式
  const initializeMouseFollow = () => {
    // 新增延迟补偿
    setTimeout(() => {
      addMouseFollowListeners();

      // 强制触发重排
      const contentArea = document.querySelector('.page-content');
      if (contentArea) {
        void contentArea.offsetHeight;

        // 模拟鼠标移动事件
        const fakeEvent = new MouseEvent('mousemove', {
          clientX: window.innerWidth / 2,
          clientY: window.innerHeight / 2,
          bubbles: true
        });
        contentArea.dispatchEvent(fakeEvent);
      }
    }, 50);
  };

  // 调整阅读速度
  const adjustSpeed = (delta) => {
    window.autoReader.speed = Math.max(100, window.autoReader.speed + delta);
    setAutoReaderState(prev => ({ ...prev, speed: window.autoReader.speed }));

    if (window.autoReader.intervalId) {
      stopAutoReader();
      startAutoReader();
    }
  };

  // 修改翻页函数
  const changePage = async (delta) => {
    const newPage = currentPage + delta;

    if (newPage < 1 || newPage > totalPages) return;

    const wasAutoReading = readingMode === 'auto-reader' && window.autoReader.intervalId;
    const wasRhythmReading = readingMode === 'rhythm' && window.rhythmReaderGlobal.active;

    // 如果正在自动阅读或节奏阅读，先停止
    if (wasAutoReading) {
      clearInterval(window.autoReader.intervalId);
      window.autoReader.intervalId = null;
    }

    if (wasRhythmReading && window.rhythmReader) {
      window.rhythmReader.stop();
    }

    // 更新页码
    setCurrentPage(newPage);

    try {
      // 加载新页面
      const page = await pdfDocument.getPage(newPage);
      const textContent = await page.getTextContent();
      const textItems = textContent.items;
      let extractedText = textItems.map(item => item.str).join(' ');
      extractedText = extractedText.replace(/\s+/g, ' ').trim();

      // 按当前阅读模式处理内容
      let processedContent = '';
      switch(readingMode) {
        case 'follow-mouse':
          processedContent = formatTextForMouseFollow(extractedText);
          break;
        case 'static':
          processedContent = applyStaticHighlight(extractedText);
          break;
        case 'auto-reader':
          processedContent = formatTextToHtml(extractedText);
          break;
        case 'rhythm':
          processedContent = formatTextToHtml(extractedText);
          break;
        default:
          processedContent = formatTextToHtml(extractedText);
      }

      // 更新页面内容
      const contentArea = document.querySelector('.page-content');
      if (contentArea) {
        contentArea.textContent = '';
        contentArea.innerHTML = processedContent;

        // 滚动到页面顶部
        window.scrollTo(0, 0);
        if (contentArea.scrollTo) {
          contentArea.scrollTo(0, 0);
        }

        // 重新启动对应的阅读模式
        if (wasAutoReading) {
          setTimeout(initializeAutoReader, 50);
        } else if (wasRhythmReading && window.rhythmReader) {
          setTimeout(() => {
            window.rhythmReader.start();
          }, 50);
        }
      }
    } catch (error) {
      console.error('翻页失败:', error);
    }
  };

  // 修改后的监听器设置函数
  const addMouseFollowListeners = () => {
    if (readingMode !== 'follow-mouse') return;

    const contentArea = document.querySelector('.page-content');
    if (!contentArea) return;

    // 清理旧的监听器
    contentArea.removeEventListener('mousemove', handleMouseMove);
    contentArea.removeEventListener('mouseleave', clearAllHighlights);

    // 添加新的监听器
    contentArea.addEventListener('mousemove', handleMouseMove);
    contentArea.addEventListener('mouseleave', clearAllHighlights);

    // 确保所有段落都有正确的样式
    document.querySelectorAll('.pdf-paragraph').forEach(p => {
      p.style.position = 'relative';
      p.style.lineHeight = '1.5';
    });
  };

  // 格式化文本为HTML
  const formatTextToHtml = (text) => {
    // 分段
    const paragraphs = text.split(/(?<=。|！|？|\.|\!|\?)\s+/).filter(p => p.trim());

    // 创建HTML内容
    let htmlContent = '<div class="pdf-content">';

    paragraphs.forEach(paragraph => {
      htmlContent += `<p class="pdf-paragraph">${paragraph}</p>`;
    });

    htmlContent += '</div>';
    return htmlContent;
  };

  // 应用静态高亮
  const applyStaticHighlight = (text) => {
    const paragraphs = text.split(/(?<=。|！|？|\.|\!|\?)\s+/).filter(p => p.trim());

    let htmlContent = '<div class="pdf-content">';

    paragraphs.forEach(paragraph => {
      // 新增强制清理逻辑
      const cleanParagraph = paragraph
        .replace(/<span class="text-line">/g, '')
        .replace(/<\/span>/g, '');

      const highlightedText = processTextWithHighlight(cleanParagraph);
      htmlContent += `<p class="pdf-paragraph">${highlightedText}</p>`;
    });

    htmlContent += '</div>';
    return htmlContent;
  };

  // 格式化文本用于鼠标跟随高亮模式
  const formatTextForMouseFollow = (text) => {
    const paragraphs = text.split(/(?<=。|！|？|\.|\!|\?)\s+/).filter(p => p.trim());

    let htmlContent = '<div class="pdf-content mouse-follow-content">';

    paragraphs.forEach(paragraph => {
      // 将段落分割成行
      const lines = paragraph.split('\n').filter(line => line.trim());
      htmlContent += `<p class="pdf-paragraph" data-follow="true" style="position: relative; line-height: 1.5;">`;

      lines.forEach((line, i) => {
        // 每行用span包裹
        htmlContent += `<span class="text-line" style="display: block;">${line}</span>`;
      });

      htmlContent += `</p>`;
    });

    htmlContent += '</div>';
    return htmlContent;
  };

  // 强制初始化字符位置（这是关键步骤！）
  function forceInitReader() {
    console.log("强制初始化阅读器...");

    // 1. 如果autoReader或charPositions不存在，创建它们
    if (typeof window.autoReader === 'undefined') {
      window.autoReader = {
        charPositions: [],
        intervalId: null,
        currentIndex: 0,
        speed: 30
      };
    }

    // 2. 如果没有字符位置数据，尝试触发它的创建
    if (!window.autoReader.charPositions || window.autoReader.charPositions.length === 0) {
      console.log("未找到字符位置数据，尝试强制创建...");

      // 直接使用scanTextPositionsSimple函数
      scanTextPositionsSimple();
    }

    return window.autoReader.charPositions && window.autoReader.charPositions.length > 0;
  }

  // 简单的文本位置扫描函数
  function scanTextPositionsSimple() {
    console.log("使用简单文本扫描...");

    window.autoReader.charPositions = [];
    const contentElement = document.querySelector('.page-content');
    if (!contentElement) {
      console.error("找不到内容容器");
      return;
    }

    // 递归处理文本节点
    function processTextNodes(element) {
      if (element.nodeType === Node.TEXT_NODE) {
        const text = element.textContent;
        if (!text || text.trim() === '') return;

        // 获取元素的位置信息
        const range = document.createRange();

        // 为每个字符创建一个独立的范围
        for (let i = 0; i < text.length; i++) {
          try {
            range.setStart(element, i);
            range.setEnd(element, i + 1);

            const rect = range.getBoundingClientRect();
            if (rect.width > 0 && rect.height > 0) {
              window.autoReader.charPositions.push({
                char: text[i],
                x: rect.left + rect.width/2,
                y: rect.top + rect.height/2,
                element: element.parentElement
              });
            }
          } catch (e) {
            console.warn("无法处理字符", e);
          }
        }
      } else if (element.nodeType === Node.ELEMENT_NODE) {
        // 递归处理子元素
        for (const child of element.childNodes) {
          processTextNodes(child);
        }
      }
    }

    processTextNodes(contentElement);
    console.log("扫描完成，找到字符数量:", window.autoReader.charPositions.length);
  }

  return (
    <div className="App">
      <div className="sidebar">
        <div className="logo">
          <h2>ADHD阅读助手</h2>
        </div>
        <label className="upload-btn">
          选择文件
          <input
            type="file"
            accept=".pdf,.txt"
            onChange={handleFileUpload}
            style={{ display: 'none' }}
          />
        </label>
      </div>
      <div className="main-content">
        <div className="header">
          <h1>ADHD阅读助手</h1>
          <div className="controls">
            <div className="mode-selector">
              <select value={readingMode} onChange={handleReadingModeChange}>
                <option value="normal">普通模式</option>
                <option value="static">静态高亮模式</option>
                <option value="follow-mouse">鼠标跟随高亮</option>
                <option value="auto-reader">自动阅读器</option>
                <option value="rhythm">节奏阅读模式</option>
              </select>
            </div>
            <div className="mode-selector">
              <select value={posMode} onChange={(e) => setPosMode(e.target.value)}>
                <option value="none">无词性标注</option>
                <option value="noun">名词标注</option>
                <option value="all">全词性标注</option>
              </select>
            </div>
            {readingMode === 'auto-reader' && (
              <div className="speed-control">
                <button onClick={() => adjustSpeed(-100)}>-</button>
                <span>{autoReaderState.speed}ms</span>
                <button onClick={() => adjustSpeed(100)}>+</button>
              </div>
            )}
          </div>
        </div>
        <div className="page-content">
          {content ? (
            <div dangerouslySetInnerHTML={{ __html: content }} />
          ) : (
            <div className="empty-state">
              <p>请上传PDF或TXT文件开始阅读</p>
            </div>
          )}
        </div>
        {content && (
          <div className="nav-buttons">
            <button className="page-btn prev" onClick={() => changePage(-1)} disabled={currentPage === 1}>
              上一页
            </button>
            <span className="page-info">
              第 {currentPage} 页 / 共 {totalPages} 页
            </span>
            <button className="page-btn next" onClick={() => changePage(1)} disabled={currentPage === totalPages}>
              下一页
            </button>
          </div>
        )}
      </div>
    </div>
  );
};

export default App;
