// 高亮颜色变量
let currentHighlightColor = '#ff0000'; // ADHD默认模式的红色

// 模式切换
export function toggleMode(mode) {
  const content = document.querySelector('.page-content');
  if (mode === 'adhd') {
    content.classList.add('adhd-default-mode');
    currentHighlightColor = '#ff0000'; // ADHD模式下使用红色
  } else {
    content.classList.remove('adhd-default-mode');
  }
}

// 词性高亮
export function highlightByPOS(pos) {
  const text = document.querySelector('.page-content');
  const words = text.textContent.split(/\s+/);
  
  // 这里应该调用词性标注API
  // 示例实现
  words.forEach(word => {
    const span = document.createElement('span');
    span.textContent = word + ' ';
    span.className = `pos-highlight ${pos}`;
    text.appendChild(span);
  });
}

// 自定义高亮
export function createHighlight(color) {
  const selection = window.getSelection();
  if (selection.toString().length > 0) {
    const span = document.createElement('span');
    span.className = 'highlight';
    span.style.backgroundColor = color;
    selection.getRangeAt(0).surroundContents(span);
    saveHighlights();
  }
}

// 保存高亮
export function saveHighlights() {
  const highlights = Array.from(document.querySelectorAll('.highlight')).map(h => ({
    text: h.textContent,
    color: h.style.backgroundColor,
    position: getElementPosition(h)
  }));
  localStorage.setItem('highlights', JSON.stringify(highlights));
}

// 加载高亮
export function loadHighlights() {
